    vec3 col = 01.5 + 0.5*cos(iTime*1.+uv.xyx+vec3(2.5,1,0.3));

    //combining the circle and cycling colour
    col=col/pct;

    //this line adds tunnel effect 
    col *= 1.8 + 0.2*cos(140.0*col);

    // Output to screen
    fragColor = vec4(col,1.0);
}

