float opUnion( float d1, float d2 ) {  return min(d1,d2); }

vec2 opUnion2( vec2 d1, vec2 d2) { return d1.x < d2.x ? d1 : d2; }

float opSubtraction( float d1, float d2 ) { return max(-d1,d2); }

float opIntersection( float d1, float d2 ) { return max(d1,d2); }

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float opSmoothSubtraction( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h);
}

float opSmoothIntersection( float d1, float d2, float k ) {
    float h = clamp( 0.5 - 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) + k*h*(1.0-h);
}

vec3 opRepP( in vec3 p, in vec3 c )
{
    vec3 q = mod(p,c)-0.5*c;
    return q;
}

float sinnoise ( float p ) {

    //int n = 0;
    float s = 0.;

    for(int n = 0; n < 3; n++) {
        float nf = float(n);
        s += ( cos(pow(2., nf)*p) * sin(pow(nf, 2.)*p/10.) + sin(pow(nf, 2.)*p/10.) ) / pow(2., nf);
    }

    return s;
}

float lnoise ( vec2 p ) {

    float s = 0.;
    float f = 0.1, a = 0.1, ps = 0.5, l = 2.;

    for(int n = 0; n < 4; n++) {
        float height = texture(iChannel0, p.xy*f).r*a;
        s += height;
        f *= l;
        a *= ps;
    }

    return s;

}



