#define PI 3.14159265358979323846
#define scale 4.

float rnd (vec2 p)
{
 return fract(sin(dot(p ,vec2(12.9898, 75.233)))*43563.234);   
}

mat2 rot(float a )
{
 return mat2(cos(a), -sin(a), sin(a), cos(a) );    
}

vec2 tilerot (vec2 p)

{
    //From the book of shaders https://thebookofshaders.com/10/
    vec2 ipos = floor(p*scale);   
     //-0.5 to get -0.5 to 0.5 for rotation
    vec2 fpos = fract(p*scale)-0.5; 
    float index = rnd(ipos);

    if(index >= 0.75){ p = fpos*rot(PI/2.);}
    else if (index >= 0.50){ p = fpos*rot(-PI/2.);}
    else if (index >= 0.25){ p = fpos*rot(PI);}
    else p = fpos;//<---need this!

 //+0.5 to reverse the earlier process so you for 0.- 1 again
 return p+0.5;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //resolution correction 
    uv.x*=iResolution.x/iResolution.y;

   /// uv+=iTime/16.;  //to move the pattern
    uv = tilerot(uv);

    //textures
    vec3 tx0 = vec3(texture(iChannel0, uv));
    vec3 tx1 = vec3(texture(iChannel1, uv));
    vec3 tx2 = vec3(texture(iChannel2, uv));

    //moving light aroud the gold borders
    vec3 gold = vec3(202., 165., 55.)/255.-abs(sin(uv.x*4.+iTime))/2.;
    //initial color will be the greyish background
    vec3 col = vec3(0.)+tx1.x/5.;

    float circ = length(vec2(uv.x+0.1, uv.y-0.5));

    //the shape of the red circle
    float redC = 1.0-step(0.5,circ );

    //coloring the red circle
    col = mix(col, vec3(1.0,0.,0.)*tx0.x, redC);
	//sq += step(0.5, abs(sin(dot(uv, vec2(0., 0.5))*28.)));

    //the shape of the gold borders
    float sq = step(0.98, uv.x)+step(uv.x, 0.02)
        		+step(0.98, uv.y)+step(uv.y, 0.02);

    //coloring the gold borders
    col = mix(col, gold+tx2.x/3., min(sq, 1.));

    //the gold ring around the red circles
    float ring =  smoothstep(0.5, 0.5+0.009, circ) -  
        smoothstep(0.55, 0.55+0.009, circ);

    //makes the bend in the metal like the straight gold  borders have
    float ring2 =  smoothstep(0.5, 0.5+0.009, circ) -  
        smoothstep(0.525, 0.525+0.009, circ);

    //adding the two rings together with color
    col = mix(col, gold, ring+ring2);

    // Output to screen
    fragColor = vec4(col,1.0);
}

