    float 	dr 	= 1.0;
    float 	r 	= 0.0;

    for (int i = 0; i < 15 ; i++) 
    {
        r = length(z);
        if (r>2.0) break;

        // convert to polar coordinates
        float theta = acos(z.z/r);
        float phi 	= atan(z.y,z.x);
        dr 			=  pow( r, Power - 1.)*Power*dr + 1.0;

        // scale and rotate the point
        float zr 	= pow( r,Power);
        theta 		= theta*Power;
        phi 		= phi*Power;

        // convert back to cartesian coordinates
        z = zr*vec3(sin(theta)*cos(phi), sin(phi)*sin(theta), cos(theta));
        z+=pos;
    }
    return 0.5*log(r)*r/dr;
}

//SDF
float map(vec3 p)
{
    return DE(p);
}

vec3 normal(vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(vec3(map(p + eps.xyy) - map(p-eps.xyy),
                  map(p + eps.yxy) - map(p-eps.yxy),
                  map(p + eps.yyx) - map(p-eps.yyx)
                 )
            );
}

float light(vec3 n, vec3 l)
{
    //return dot(n,normalize(l))*.5+.5;
    return max(dot(n,normalize(l)),0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    Power += mod(iTime*0.1 ,20.0);

    vec2 position = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    position -= 0.5;
    position /= vec2(iResolution.y/iResolution.x,1.);

    vec3 ro = vec3(0., 0., -4.);
    vec3 p = ro;
    vec3 rd = normalize(vec3(position, 1.5));

	ro.xz = rotate(ro.xz, iTime * rotationSpeed);
	p.xz = rotate(p.xz, iTime * rotationSpeed);
	rd.xz = rotate(rd.xz, iTime * rotationSpeed);

    bool hit = false;


	vec3 fogColor = vec3(1.0,.1, 1.0);

    for(float i = 0.0; i < 100.0; ++i)
    {
        float df = map(p);

        if(df < 0.001)
        {
            hit = true;
            break;
        }
        p += df * rd * 0.95;
    }
    float t = length(ro - p);

    vec3 finalColor = !hit ? vec3(0.1) : normal(p);

    if(hit)
    {
        vec3 n = normal(p);

	float fresnel = pow(1.-dot(n, -rd), 0.5);

        vec3 l = vec3(1.0, 0.0, -0.1);
	    l.xz = rotate(l.xz, iTime * rotationSpeed);

        finalColor = mix(vec3(0.0, 0.0, 0.0), vec3(0.0, 0.5, 0.5), vec3(light(n,l)));
	finalColor = mix(finalColor, vec3(0.,0.5,0.1),fresnel);
    }
    else
    {
	finalColor = vec3(.0,0.1,0.2);
     	fogColor = vec3(0.2);
    }

    finalColor = mix(finalColor, fogColor, 1.-exp(-0.01*t*t));

    fragColor = vec4(finalColor, 1.0);
}

// Created by Chaz Wilson

float restrict0to1 (float sig) {
    return max(-1.0, min(1.0, sig));
}


float rgba_blend (float src, float alpha){
	alpha = restrict0to1(alpha);
    float blend = -src+1.0;
    return (blend * alpha) + (src * (-alpha + 1.0));
}

vec4 filterMod(float[3] compAlphas, vec4 video1){ 
	vec4 pixel;
    for (int i = 0; i < 4; i++) {
        pixel[i] = rgba_blend(video1[i], compAlphas[i]);
    }
	return pixel;
}

