void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float asp = iResolution.x/iResolution.y;

	uv-=0.5;
    uv.x*=asp;
    //uv.y+=0.2;
    //uv.y-=sqrt(abs(uv.x))*(sin(iTime/2.)/2.+0.5);
    /// Time varying pixel color
    uv.x+=sin(iTime)*0.5;
    float z = sqrt(sqrt(0.2*0.2*0.2*0.2-uv.x*uv.x*uv.x*uv.x-uv.y*uv.y*uv.y*uv.y))/0.2;
    //uv.y+=sin(iTime)*0.5+0.5;
    uv.x-=sin(iTime)*0.5;
    float c = (sqrt(pow(0.3*0.3,sin(iTime)/2.+1.)-pow(uv.x*uv.x,sin(iTime)/2.+1.)-pow(uv.y*uv.y,sin(iTime)/2.+1.)))/(0.3-sin(iTime)*0.1);
   uv*=2.5;
    uv.x/=asp;
    float x =-uv.y;
    float y =uv.x;

    x+=(sin(iTime)*0.5);
    y+=(cos(iTime)*0.5);

    x*=2.;y*=2.;
     y*=asp;
    vec2 dis = vec2(0.4,0.4);
    dis+=sin(iTime)/2.+0.1;

     float b =((1.-sign(-x-.9+abs(y*2.)))/3.*(sign(.9-x)+1.)/3.)*(sign(x+.65)+1.)/2. - ((1.-sign(-x-.39+abs(y*2.)))/3.*(sign(.9-x)+1.)/3.) + ((1.-sign(-x-.39+abs(y*2.)))/3.*(sign(.6-x)+1.)/3.)*(sign(x-.35)+1.)/2.;
	z = max(b,z);
    z=max(c,z);
    // Output to screen
    fragColor += z;
}

float fibonacci(in vec2 uv)
{
    float s = 0.0;

    for (int n = 0; n < 2000; n++)
    {
        float numb = float(n);

        float angle = numb * radians(137.51+iTime/50.);
        float r = 0.02 * sqrt(numb*1.);
        vec2 circlePos = r * vec2(cos(angle), sin(angle));

        s += (1.0 - r) * smoothstep(-1.5/iResolution.y, 0.0, 0.01 - length(circlePos - uv));
    }

    return s;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord  - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3( fibonacci(uv) );

    fragColor = vec4(col, 1.0);

}

// A modificated version of https://www.shadertoy.com/view/ltSXDR

mat2 rot (float a) {
 	return mat2(cos(a), sin(a), -sin(a), cos(a));   
}

float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float sphere (vec3 p, float r) {
 	return length(p) - r;   
}

vec2 add(in vec2 a, in vec2 b) {
    return a.x < b.x ? a : b;
}

vec2 scene (vec3 p) {

    float plane = p.y + 1.0;
    float body = sphere(p, 1.0);

    vec3 hp = (p);
    hp.xy = rot(-0.3 + sin(iTime*2.0)*0.2) * hp.xy;
    hp = hp-vec3(-0.25*0.0, 0.95, 0.0);
    float head = sphere(hp, 0.5);

    head = min(head, sdCapsule(hp+vec3(-0.1,0.0,0.0), vec3(0.0, 0.7, 0.0), vec3(0.0), 0.025));
    head = min(head, sdCapsule(hp+vec3(-0.1,0.0,0.1), vec3(0.0, 0.9, 0.0), vec3(0.0), 0.0125));

    float eye = sphere(hp-vec3(-0.375, 0.265, 0.0), 0.1125);
    eye = min(eye, sphere(hp-vec3(-0.375, 0.2, -0.2), 0.06));



    if (plane < body) {
     	if (plane < head) return vec2(plane, 1.0);
        if (head < eye)
        	return vec2(head, 3.0);
        else return vec2(4.0);
    } else {
     	if (body < head) return vec2(body, 2.0);
        if (head < eye)
        	return vec2(head, 3.0);
        else return vec2(eye, 4.0);
    }
}

vec3 normal (vec3 p) {
    vec2 eps = vec2(0.001, 0.0);
    return normalize(vec3(
    	scene(p+eps.xyy).x - scene(p-eps.xyy).x,
        scene(p+eps.yxy).x - scene(p-eps.yxy).x,
        scene(p+eps.yyx).x - scene(p-eps.yyx).x
    ));
}

vec4 raymarch (vec3 ro, vec3 rd) {
    float h = 0.0;
    for (int i = 0; i < 175; ++i) {
        vec3 p = ro+h*rd;
        vec2 test = scene(p);
        h += test.x;
        if (h < 0.01) break;
        if (h > 64.0) break;
        if (test.x < 0.001)
            return vec4(p, test.y);
    }
    return vec4(-1.0);
}

float shadow (vec3 ro, vec3 rd) {
 	float smax = 1.0;
    float smin = 100.0;
    float h = 0.0;
    float shadow = 1.0;
    for (int i = 0; i < 32; ++i) {
        vec3 p = ro+h*rd;
        float test = scene(p).x;
        h += test;
        if (test < 0.0001)
            return 0.0;
        else
            shadow = min(2.5*test/h, shadow);
    }

    return shadow;

}

float ppp (float x) {
 	if (abs(x) > 0.85) return 0.0;
    return 1.0;
}

mat3 camera () {
    float a =  iMouse.x*0.05 + 2.0;
    vec3 right =	vec3(1.0, 0.0, 0.0);
 	vec3 up =		normalize(vec3(0.0, 1.0, 1.8 + (iMouse.y/iResolution.y*2.0-1.0)*1.5)); 

    mat2 ra = rot(a);
    right.xz *= ra;
    up.xz *= ra;

    return mat3(right, up, cross(right, up));
}

const float orgl = 0.5;

vec4 bb8Body (vec3 raym) {
