struct DPoint
{
    vec3 mPos;
    vec3 mCol;
};

float DistLineToPoint(vec3 ro, vec3 rd, vec3 pos)
{
    vec3 ap = pos - ro;
    return length(cross(ap, rd)) / length(rd);
}

float DrawPoint(vec3 ro, vec3 rd, vec3 pos)
{
    float dist = DistLineToPoint(ro, rd, pos);
    return smoothstep(0.1, 0.09, dist);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / min(iResolution.x, iResolution.y);
    uv.x -= (iResolution.x - iResolution.y) / 
        (2.0 * min(iResolution.x, iResolution.y)); 
    uv -= 0.5;

    vec3 camera = vec3(0, 3, 5);
    camera = vec3(
        cos(iTime) * camera.x + sin(iTime) * camera.z, 
        camera.y,
        -sin(iTime) * camera.x + cos(iTime) * camera.z);
    vec3 view   = vec3(0, 0, 0);
    vec3 up     = vec3(0, 1, 0);

    // Setting up View matrix (don't make view matrix itself, We just need v,s,u element).
    vec3 forward = normalize(view - camera);
    vec3 side    = cross(forward, up);
    vec3 camUp   = cross(side, forward);

    float zoom = 2.0f;

    // Setting up virtual screen, and lay direction of virtual screen pixel.
    vec3 scrOrg = camera + zoom * forward;
    vec3 pxlPos = scrOrg + (side * uv.x) + (camUp * uv.y);
    vec3 rayDir = normalize(pxlPos - camera);

    vec3 result = vec3(0);

    DPoint point;
    point.mPos = vec3(1, 0, 0); point.mCol = vec3(1, 1, 0);
    {
        float v = DrawPoint(camera, rayDir, point.mPos);
        result += point.mCol * v;
    }
    point.mPos = vec3(0, 0, 1); point.mCol = vec3(0, .5, 1);
    {
        float v = DrawPoint(camera, rayDir, point.mPos);
        result += point.mCol * v;
    }
    point.mPos = vec3(0, 0, -1); point.mCol = vec3(0, 1, 0);
    {
        float v = DrawPoint(camera, rayDir, point.mPos);
        result += point.mCol * v;
    }
    point.mPos = vec3(-1, 0, 0); point.mCol = vec3(1, .5, 0);
    {
        float v = DrawPoint(camera, rayDir, point.mPos);
        result += point.mCol * v;
    }

    fragColor = vec4(result, 1.0);
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// Edge detection code from:https://www.shadertoy.com/view/MsSGD1
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define hash(h) fract(sin(h) * 43758.5453123)
#define EDGE_WIDTH 0.045
#define PI 3.14159265359

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0))-0.08;
}

float dBox2d(vec2 p, vec2 b) {
    return max(abs(p.x) - b.x, abs(p.y) - b.y);
}

vec4 combine(vec4 val1, vec4 val2 ){
    if ( val1.w < val2.w ) {
        return val1;
    }
    return val2;
}

float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

float sdTorus(vec3 p, vec2 t) {
    vec2 q = vec2(length(p.xz)-t.x,p.y);
    return length(q)-t.y;
}

vec3 sonicFaceTex(vec3 p) {
    vec2 uv = p.xy;
    vec3 col = vec3(0.0,0.0,1.0);
    float eyeL = length(uv+vec2(-0.17,0.0))-0.1;
    float eyeL2 = length(uv+vec2(-0.14,0.03))-0.09;
    float eyeL3 = length(uv+vec2(-0.11,0.05))-0.08;
    float eyeL4 = length(uv+vec2(-0.08,0.06))-0.07;
    float eyeL5 = length(uv+vec2(-0.05,0.07))-0.06;
    float eyeL6 = length(uv+vec2(-0.02,0.08))-0.04;
    float eyeL7 = length(uv+vec2(0.00,0.08))-0.03;
    float mouth = length(uv+vec2(-0.2,0.17))-0.025;
    float mouth2 = length(uv+vec2(-0.17,0.18))-0.035;
    float mouth3 = length(uv+vec2(-0.14,0.19))-0.04;
    float mouth4 = length(uv+vec2(-0.11,0.2))-0.045;
    float mouth5 = length(uv+vec2(-0.08,0.2))-0.05;
    float mouth6 = length(uv+vec2(-0.05,0.2))-0.05;
    float mouth7 = length(uv+vec2(-0.02,0.2))-0.05;
    float nose = length(uv+vec2(0.00,0.16))-0.02;

    uv.y*=0.6;
    float eyeball = dot(uv+vec2(-0.17,-0.01),uv+vec2(-0.17,-0.01))*20.0;
    uv = p.xy;
    float eyeball2 = dot(uv+vec2(-0.17,-0.03),uv+vec2(-0.17,-0.03))*50.0;
    uv.y*=0.6;
    float eyeball3 = dot(uv+vec2(-0.17,-0.01),uv+vec2(-0.17,-0.01))*10.0;

    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL2) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL3) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL4) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL5) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL6) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeL7) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth2) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth3) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth4) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth5) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth6) );
    col = mix( col, vec3(1.0,0.8,0.2), 1.0-smoothstep(0.01,0.015,mouth7) );
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.01,0.015,nose) );
    col = mix( col, vec3(0.2,1.0,0.2), 1.0-smoothstep(0.01,0.015,eyeball3) );
    col = mix( col, vec3(0.0), 1.0-smoothstep(0.01,0.015,eyeball) );
    col = mix( col, vec3(1.0), 1.0-smoothstep(0.01,0.015,eyeball2) );


    col = (p.z>0.01)? col:vec3(0.0,0.0,1.0);

    return col;
}

vec4 sdSonic(vec3 p) {
    vec3 uv = p;
    float sonicD = sdRoundCone((p+vec3(0.0,0.15,0.0))*matRotateX(radians(120.0+sin(iTime*6.0)*20.0)),0.15,0.03,0.45);
    float sonicD2 = sdRoundCone((p+vec3(0.0,0.0,0.0))*matRotateX(radians(90.0+sin(iTime*6.0)*20.0)),0.15,0.03,0.5);
    float sonicD3 = sdRoundCone((p+vec3(0.0,-0.15,0.0))*matRotateX(radians(70.0+sin(iTime*6.0)*20.0)),0.15,0.03,0.3);
    float sonicF = length(p+vec3(0.0,0.0,-0.15))-0.35;
    p.x = abs(p.x);
    float ears = sdRoundCone((p+vec3(-0.2,-0.17,-0.2))*matRotateZ(radians(20.0)),0.1,0.02,0.2);

    float sonicD4 = sdRoundCone((p+vec3(-0.2,-0.1,0.0))*matRotateX(radians(70.0+sin(iTime*5.9993)*20.0))*matRotateZ(radians(30.0)),0.15,0.03,0.5);
    float sonicD5 = sdRoundCone((p+vec3(-0.2,0.1,0.0))*matRotateX(radians(120.0+sin(iTime*5.9993)*20.0))*matRotateZ(radians(30.0)),0.15,0.03,0.5);

    uv.x = abs(uv.x);
    vec4 sonic = vec4(sonicFaceTex(uv),min(min(min(min(min(min(sonicD,sonicD2),sonicD3),sonicF),ears),sonicD4),sonicD5));
	return sonic;
}

vec3 floorTex(vec2 uv) {

    vec3 colA = vec3(0.9,0.6,0.0);
    vec3 colB = vec3(0.8,0.4,0.0);
    vec3 col = vec3(mix(colA,colB,mod((floor(uv.x*5.0) + floor(uv.y*5.0))*0.5, 2.0)));

    float w = 0.7;
    col = (uv.x>=-w && uv.x< w)? vec3(0.6,0.9,0.6):col;

    return col;
}

vec4 map(vec3 p){    
    vec3 pref = p;
    vec2 uv = p.xy;

    vec3 spPos = vec3(0.0);

    float sdist = 2.1;
    float srotVal = radians((iTime*60.0));
	float sy = spPos.y+cos(srotVal)*(sdist);
    float sz = spPos.z+sin(srotVal)*(sdist);

	float sdeg = (atan(sy,sz)*180.0/PI);
    vec3 sonicPos = (p+vec3(0.0,sy,sz))*matRotateX(radians(45.0+sdeg));
    vec4 sonic = sdSonic(sonicPos);
    vec4 sp = vec4(floorTex(uv),length((p+spPos))-1.5);

    float numCoin = 10.0;
    float deg = 360.0/numCoin;
    vec4 coin = vec4(1.0,1.0,0.0,1.0);

    float coinD = 8.0;
    for(float i = 0.0; i<numCoin; i+=1.0) {
        float rotVal = radians(i*deg);
        mat3 m = matRotateX(rotVal);
        mat3 mrot = matRotateZ(rotVal*iTime*0.3);
        float cdist = 1.9;
        float y = spPos.y+cos(rotVal)*(cdist);
        float z = spPos.z+sin(rotVal)*(cdist);
        vec3 coinPos = (p+vec3(spPos.x,z,y))*m*mrot;
        coinD = min(coinD,sdTorus(coinPos,vec2(0.15,0.03)));
        coin = vec4(1.0,1.0,0.0,coinD);
    }

    return combine(combine(sp,coin),sonic);
}

vec3 normalMap(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        map(p + vec3(  d, 0.0, 0.0)).w - map(p + vec3( -d, 0.0, 0.0)).w,
        map(p + vec3(0.0,   d, 0.0)).w - map(p + vec3(0.0,  -d, 0.0)).w,
        map(p + vec3(0.0, 0.0,   d)).w - map(p + vec3(0.0, 0.0,  -d)).w
    ));
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = map(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

// from simon green and others
float ambientOcclusion(vec3 p, vec3 n)
{
    const int steps = 4;
    const float delta = 0.15;

    float a = 0.0;
    float weight = 4.;
    for(int i=1; i<=steps; i++) {
        float d = (float(i) / float(steps)) * delta; 
        a += weight*(d - map(p + n*d).w);
        weight *= 0.5;
    }
    return clamp(1.0 - a, 0.0, 1.0);
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
    vec3 cw = normalize(ta-ro);
    vec3 cp = vec3(sin(cr), cos(cr),0.0);
    vec3 cu = normalize( cross(cw,cp) );
    vec3 cv =          ( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec2 uv = p;

    float time = iTime*2.0;

    vec3 ro = vec3( 2.5*cos(0.1*time + 6.0), 3.5, 2.5*sin(0.1*time + 6.0) );
    vec3 ta = vec3( 0.5, -0.4, -0.5 );
    mat3 ca = setCamera( ro, ta, 0.0 );
    vec3 rd = ca * normalize( vec3(p.xy,2.0) );

    float t, dist;
    float lastDistEval = 1e10;
    float edge = 0.0;
    t = 0.0;
    vec3 distPos = ro+rd;
    vec4 distCl = vec4(0.0);
    for(int i = 0; i < 64; i++){
        distCl = map(distPos);
        dist = distCl.w;
        t += dist;
        distPos = ro+rd*t;

        if (lastDistEval < EDGE_WIDTH && dist > lastDistEval + 0.001) {
            edge = 1.0;
        }
        if (dist < lastDistEval) lastDistEval = dist;
        if(dist < 0.01 || dist > 30.0) break;
    }

    vec3 color;
    float shadow = 1.0;
    if(dist < 1.0){
        // lighting
        vec3 lightDir = vec3(0.0, 1.0, 0.0);
        vec3 light = normalize(lightDir + vec3(0.5, 0.0, 0.9));
        vec3 normal = normalMap(distPos);

        // difuse color
        float diffuse = clamp(dot(light, normal), 0.5, 1.0);
        float lambert = max(.0, dot( normal, light));

        // ambient occlusion
        float ao = ambientOcclusion(distPos,normal);

        // shadow
        shadow = shadowMap(distPos + normal * 0.001, light);

        // result
        color += vec3(lambert);
        color = ao*diffuse*(distCl.xyz+(.1-length(p.xy)/3.))*vec3(1.0, 1.0, 1.0);

    }else{
        color =.94*max(mix(vec3(0.5,1.1,1.2)+(.1-length(p.xy)/3.),vec3(1),.1),0.);
    }

    // rendering result
    float brightness = 1.;
    vec3 dst = (color * max(0.8, shadow))*brightness;

    // add edge detection result
    dst = mix(dst,vec3(0.0,0.0,0.3),edge);


    fragColor = vec4(dst, 1.0);
}

