// and www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


// c is the sin/cos of the angle. r is the radius
float sdPie( in vec2 p, in vec2 c, in float r )
{
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c*clamp(dot(p,c),0.0,r) );
    return max(l,m*sign(c.y*p.x-c.x*p.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animation
    float t =            3.14*(0.5+0.5*cos(iTime*0.52));
    vec2  w = vec2(0.50,0.25)*(0.5+0.5*cos(iTime*vec2(1.1,1.3)+vec2(0.0,2.0)));

    // distance
    float d = sdPie(p,vec2(sin(t),cos(t)), 0.5);

    // coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdPie(m,vec2(sin(t),cos(t)), 0.5);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col, 1.0);
}

#define ss(a,b,c) smoothstep(a,b,c)
#define SIN(a) (.5 + .5*sin(a))
#define COS(a) (.5 + .5*cos(a))
#define PI 3.1415

struct Circle
{
    vec2 c;
    float r;
};

// complex number multiplication
vec2 zm(vec2 a, vec2 b)
{
    return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

// returns 1 for values inside a and b value.
float band(float a, float b, float x)
{
    return step(a,x) * step(x,b);
}

// like band, but will make gradient from 0 to 1 and 0 again inside [a,b]
float sband(float a, float b, float x)
{
    float n = (x-a)/(b-a);
    return (1.- 2.*abs(n-.5))*band(a,b,x);
}
float opSmoothUnion( float d1, float d2, float k )
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );

    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float fCircle(vec2 uv, Circle c)
{
   return length(uv - c.c) - c.r;
}

float fBox(vec2 uv, vec2 b)
{
	vec2 d = max(abs(uv) - b,0.);
    return length(d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    vec2 muv = (2.*iMouse.xy - iResolution.xy)/iResolution.y;

    // mushroom head
    Circle c0 = Circle(vec2(.0,.3), .4);
    Circle c1 = Circle(vec2( .55,.1), .4);
    Circle c2 = Circle(vec2(-.55,.1), .4);
    Circle c3 = Circle(vec2(0,.06), .4);

    float an = .6*SIN(iTime);//angle
    vec2 cuv = zm(uv, vec2(cos(an),sin(an))) + vec2(.0,-.2);
    float head = fCircle(cuv, c0);
    head = opSmoothUnion(head, fCircle(cuv, c1), .3);
    head = opSmoothUnion(head, fCircle(cuv, c2), .3);
    head = opSmoothUnion(head, fCircle(cuv, c3), .4);

    // sdf to mask
    float headMask = 1.- ss(0.,.01,head);

    // mushroom head pattern
    Circle cm = Circle(vec2(.1,.1), .09);
    vec2 uvM = cuv * (1. - SIN(head*15. + 4.*iTime)*3.)
        		+ .031*vec2( 1.9*sin(cuv.y*10. + iTime),
                        	 .5*cos(cuv.x*59.) )
        		+ vec2(1. - cuv.x*(cuv.x - 5.)*(cuv.y)*.5);
    float mask = fCircle( mod(uvM*.9, .4 ), cm);
    float hh = 1.- ss(0.,.051, mask);


    // mushroom body
    float dx = (.5 - ss(.6,-.6,uv.y)*.3*sin(uv.y*2.5 + .5*sin(iTime) ) ); 
    vec2 buv = uv*vec2(1.5,1.5) + vec2(dx, 0.) + vec2(-.6,.6);
    float body = fBox(buv, vec2(.15,.5))-.3*ss(.6,-.6,uv.y);
    float bodyMask = ss(0.04,.02, body);

    // eyes
    vec2 eluv = vec2(abs(buv.x),buv.y);
    eluv *= 2.*vec2(2.,1.);
    eluv += vec2(-.3,-1.2);

    float blink = max(sband(.2, .4, mod(iTime, 5.)), sband(1., 1.2, mod(iTime, 3.)));
    float eyes = fCircle(eluv * vec2(1., blink*20. + 1.), Circle(vec2(0.), .01));
    float pupils = fCircle(eluv*vec2(1.,2.)*2. + vec2(.1 + .1*sin(iTime),.1 + 0.2*sin(3.*iTime)), Circle(vec2(0.), .01 + blink*.3));
    float eyesMask = ss(.25, .2, eyes);


    // mouth
    vec2 mouv = vec2(abs(buv.x),buv.y + .04*cos(uv.x*1.));
	float mouth = fBox(mouv, vec2(.3,.01 + .015*cos(uv.x*14.)));
    float mouthMask = ss(.01,.0, mouth);

    // beck
    vec2 beuv = buv + vec2(.6, .03);
    float bgrad = ss(-0.02,-.09, uv.x*.1);
    float beck = fBox(beuv, vec2(.3, .001 + .001*bgrad ))- bgrad*bgrad*.3;
    float beckMask = ss(.01,.0, beck);

    // add beck
    float tipCircle = fCircle(buv, Circle(vec2(-1.,-.05), .04));
    float bg = ss(.2,.14, tipCircle);
    vec3 tip = mix(vec3(.9,.9,.1), vec3(1.,0.,0.), SIN(tipCircle*200.+ 10.*iTime));

    // smoke
    float noise = texture(iChannel0, 5.*fragCoord.xy/iResolution.xy).r;
    vec2 smuv = uv-vec2(.1,.3);
    smuv *= vec2(1. + (uv.y+.3)*SIN(uv.y*5. + iTime*2.), .3*abs(uv.y));
    float smokeCircle = fCircle(smuv, Circle(vec2(-.9,.2), .04));
    float smokeMask = ss(.2,.1, smokeCircle);
    smokeCircle = fCircle(smuv, Circle(vec2(-.7,.1), .04));
    smokeMask = mix(smokeMask, ss(.2,.1, smokeCircle),.5);
    smuv = uv*vec2(1. + (uv.y+.4)*SIN(uv.y*15. + iTime*2.), .3*abs(uv.y));
    smokeCircle = fCircle(smuv, Circle(vec2(-.6,.4), .2));
    smokeMask = mix(smokeMask, ss(.4,.0, smokeCircle),.5);


    float cloud0 = ss(0., 0.1, uv.y - .50 - .07*sin(uv.x * 05. + iTime*0.5) - .07*sin(uv.x*2.2) );
    float cloud1 = ss(0., 0.1, uv.y - .60 - .10*sin(uv.x * 07. + iTime*0.8) - .10*sin(uv.x*4.2) );
    float cloud2 = ss(0., 0.1, uv.y - .70 - .08*sin(uv.x * 09. + iTime*0.5) - .15*sin(uv.x*5.2) );
    float cloud3 = ss(0., 0.1, uv.y - .75 - .10*sin(uv.x * 11. + iTime*0.8) - .20*sin(uv.x*6.2) );

    vec4 clouds;

    clouds = mix(vec4(0.), vec4(.3), cloud0);
    clouds.a += cloud0;
    clouds = mix(clouds, vec4(.5), cloud1);
    clouds.a += cloud1;
    clouds = mix(clouds, vec4(.7), cloud2);
    clouds.a += cloud2;
    clouds = mix(clouds, vec4(.9), cloud3);
    clouds.a += cloud3;


    clouds.a = clamp(0., 1., clouds.a);

    vec2 skyuv = uv + .2*vec2(0.,-cos(uv.x) - 1.);
    vec3 skyGrad = 
        .67*vec3(1., 0., 0.)*SIN(skyuv.y*2.20 + .3) +
        .75*vec3(0., 1., 0.)*SIN(skyuv.y*2.41 + .6) +
        .81*vec3(0., 0., 1.)*SIN(skyuv.y*1.92 + .9) ;

    vec3 sky = mix(skyGrad, clouds.xyz, clouds.a);

    // Composing
    vec3 col;

    col = mix(sky, mix(vec3(.7,0.,0.), vec3(.9,.9,0.8),hh), headMask );

    // add body

    col = mix(col, vec3(.8,.8,.0), bodyMask);


    // add eyes
    col = mix(col,
			mix(vec3(0.),mix(vec3(1.), vec3(1.,0.,0.),eyes*eyes*5.), ss(0.,0.3, pupils)),
            eyesMask);

    // add mouth 
    col = mix(col,vec3(0.),mouthMask);

    col = mix(col,mix(vec3(1.), tip, bg), beckMask);

    col += mix(vec3(0.), vec3(1.), smokeMask);

    // Output to screen
    fragColor = vec4(col,1.0);

    //fragColor = vec4(ss(.05,.0,body));
    //fragColor = vec4(smokeCircle);
}

