void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// use square aspect ratio
	vec2 uv = fragCoord;
	uv = fragCoord / iResolution.y;
	uv -= vec2(0.5*iResolution.x / iResolution.y, 0.5);
uv.x*=1.5;
    uv = InitUV(uv);

	// get time 
	float timePercent = (iTime+1.)*zoomSpeed;
	int iterations = int(floor(timePercent));
	timePercent -= float(iterations);


	// update zoom, apply pow to make rate constant
	float zoom = pow(e, -glyphSizeLog*timePercent);
	zoom *= zoomScale;

    // cache focus positions
    for(int i = 0; i  < powTableCount + 2; ++i)
      focusList[i] = CalculateFocusPos(iterations+i-2);

	// get offset
	vec2 offset = vec2(0);
	for (int i = 0; i < powTableCount; ++i)
		offset += ((vec2(GetFocusPos(i)) + vec2(glyphMargin)) * gsfi) * powTable[i];

	// apply zoom & offset
    vec2 uvFractal = uv * zoom + offset;

	// check pixel recursion depth
	vec3 pixelFractalColor = GetPixelFractal(uvFractal, iterations, timePercent);
    pixelFractalColor = FinishPixel(pixelFractalColor, uv);

	// apply final color
	fragColor = vec4(pixelFractalColor, 1.0);
}

#define LAYERS 3.
#define PI_4 0.78539815
#define PI_2 1.5707963

#define SIZE 30.

#define rand1(p) fract(sin(p* 78.233)* 43758.5453) 
#define SF 1./min(iResolution.x,iResolution.y)

#define sinp(v) (sin(v)*.5+.5)

mat2 rot(float a){
	float ca = cos(a);
    float sa = sin(a);
    return mat2(ca,-sa,sa,ca);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    float m = 0.;
    for(float i=0.; i<LAYERS; i++){

        float size = SIZE + sin(iTime)*5.;
        float sv = texelFetch(iChannel0, ivec2(10., 0.), 0).x-.5;               
        vec2 guv = uv*size * (1.+i*(sv)*.2) + vec2(sin(iTime*.3), cos(iTime*.2))*10.;
        vec2 gid = floor(guv);
        guv = fract(guv) - .5;

        float gidHash = floor((rand1(gid.x + gid.y*100.))*2.);    
        guv *= rot(PI_2 * (gidHash));

        float sf = SF*size*2.;
        float l = length(guv - .5);    
        m += smoothstep(sf,.0, abs(l-.5));

        l = length(guv + .5);    
        m += smoothstep(sf,.0, abs(l-.5));
    }

    m/=LAYERS;

    fragColor = vec4(1.-m);
}

#define S(a,b,t)  smoothstep(a,b,t)

float N(float t){
	return fract(sin(t*3456.)*6547.);//make a random value 0 to 1;
}

vec4 N14(float t){
	return fract(sin(t*vec4(123., 1024., 3456., 9564))*vec4(6547.,345., 8799., 1564.));//make a random value 0 to 1;
}//make a radom vec4 value

struct ray{//it's like creating your own value type.
	vec3 o, d;
};
    ray GetRay(vec2 uv, vec3 camPos, vec3 lookat, float zoom){
        ray a;
        a.o = camPos;

        vec3 f = normalize(lookat - camPos);
        vec3 r = cross(vec3(0.,1,0.), f);
        vec3 u = cross(f, r);//zoom is the distance of camera to screen,.
        vec3 c = a.o +  f* zoom;    //center of screen point, f is normalized
        vec3 i = c + uv.x * r + uv.y * u;
        a.d = normalize(i - a.o);//distance of ray from focus to screen.
        return a;

    }
vec3 ClosestPoint(ray r, vec3 p){//the point closest to the ray
	//dot  the projection of vector into another one;
	return r.o+ max(0., dot(p - r.o, r.d))*r.d;//max :Prevent negative results
}
float DistRay(ray r, vec3 p){
	return length(p - ClosestPoint(r, p));
}
float Bokeh(ray r, vec3 p, float size, float blur){

    float d = DistRay(r , p);
    size *= length(p - r.o);
    float c = S(size , size*(1. - blur) , d);//size(1 - blur)  normalize blur to (0, 1)
	c *= mix(.6 , 1., S(size*.8, size, d));//mix: mix two values as alpha;
    return c;
}
vec3 StreetLights(ray r, float t, float roadWith){

    float side = step(r.d.x, 0.);//x<0 return 0,x>0,return 1;
    r.d.x = abs(r.d.x);
    const float s = 1./ 10.;//s has to be a constant, represent a quantity
    float m = 0.;
    for(float i = 0.;i< 1.;i += s){
        float ti = fract(t + i + side*s*.5 );//let two side have different velocities;
        vec3 p = vec3(roadWith , 3.5 , 100.- ti*100.);
        m += Bokeh(r, p, .05, .1)*ti*ti*ti;//x^n can make curve flatter;
    }


    return  vec3(1., .7, .3) *m;
}

vec3 EnvLights(ray r, float t, float roadWith){

    float side = step(r.d.x, 0.);//x<0 return 0,x>0,return 1;
    r.d.x = abs(r.d.x);
    const float s = 1./ 4.5;//s has to be a constant, represent a quantity

    vec3 c = vec3(0.);

    float m = 0.;
    for(float i = 0.;i< 1.;i += s){
        float ti = fract(t + i + side*s*.5 );//let two side have different velocities;
        vec4 n = N14(i + side*100.);// make different

        float fade = ti*ti*ti;

        float occlusion = sin(ti*6.28*10.*n.x)*.5+.5;// make the shop light blink

        fade = occlusion;

        float x = mix(2.5, 10., n.x);
        float y = mix(.1, 1.5, n.y);
        vec3 p = vec3(roadWith + x ,y , 70.- ti*70.);//light start point  is 50

        vec3 col = n.wzy;

        c += Bokeh(r, p, .05, .1)*fade*col*.5;//x^n can make curve flatter;
    }


    return  c;
}


vec3 HeadLights(ray r, float t, float roadWith){

    t *= 2.;//make car faster than street lamp
    float w1 = .25;//light width
    float w2 = w1*1.2;
    const float s = 1./ 10.;
    float m = 0.;
    for(float i = 0.;i< 1.;i += s){
        float n =N(i);

        if(n > .345) continue;//jump to top,cut % light

        float ti = fract(t + i);
        float z =  100.- ti*100.;
        //fract(t + i), if i=.9,fract's value will rise to 1 in short time
        float fade = ti*ti*ti*ti;
        float focus = S(.8, 1., ti);
        float size = mix(.05, .03, focus);
        roadWith *= -.3;

        m += Bokeh(r, vec3(roadWith -1.-w1, .15 ,z ), size, .1)*fade;
        m += Bokeh(r, vec3(roadWith -1.+w1, .15 ,z ), size, .1)*fade;

        m += Bokeh(r, vec3(roadWith -1.-w2, .15 ,z ), size, .1)*fade;//extra light effect
        m += Bokeh(r, vec3(roadWith -1.+w2, .15 ,z ), size, .1)*fade;

        float ref = 0.;
        ref += Bokeh(r, vec3(roadWith -1.-w2, -.15 ,z ), size*3., 1.)*fade;
        ref += Bokeh(r, vec3(roadWith -1.+w2, -.15 ,z ), size*3., 1.)*fade;

        m += ref*focus;

    }


    return  vec3(.9, .9, 1.) *m;
}

vec3 TailLights(ray r, float t, float roadWith){

	t *= .25;
    float w1 = .25;//light width
    float w2 = w1*1.2;
    const float s = 1./ 10.;
    float m = 0.;
    for(float i = 0.;i< 1.;i += s){
        float n =N(i);

        if(n > .5) continue;//jump to top,cut % light

        float lane = step(.25,n);//n = 0~ .5

        float ti = fract(t + i);
        float z =  100.- ti*100.;
        //fract(t + i), if i=.9,fract's value will rise to 1 in short time
        float fade = ti*ti*ti*ti;
        float focus = S(.8, 1., ti);
        float size = mix(.05, .03, focus);

        float laneShift = S(.99, .95, ti);
        float x = 1.5 - lane*laneShift;//control rightside car'lan
        roadWith *= .3;

        float blink = step(0. ,sin(t*500.))*7.*lane*step(.96,ti);//return 0 | 7 lane control witch car blink
        //step(.96,ti), car at far point ti = 0, instead close poing ti = 1,
        m += Bokeh(r, vec3(roadWith + x - w1, .15 ,z ), size, .1)*fade;
        m += Bokeh(r, vec3(roadWith + x + w1, .15 ,z ), size, .1)*fade;

        m += Bokeh(r, vec3(roadWith + x - w2, .15 ,z ), size, .1)*fade;//extra light effect
        m += Bokeh(r, vec3(roadWith + x + w2, .15 ,z ), size, .1)*fade*(1. + blink);//1|8 right taillight

        float ref = 0.;
        ref += Bokeh(r, vec3(roadWith + x - w2, -.15 ,z ), size*3., 1.)*fade;
        ref += Bokeh(r, vec3(roadWith + x + w2, -.15 ,z ), size*3., 1.)*fade*(1. + blink*.1);

        m += ref*focus;

    }


    return  vec3(1., .1, .03) *m;
}

vec2 Rain(vec2 uv, float t){


    t *= 40.;

    vec2 a = vec2(3.,1.);

    vec2 st = uv*a;
    st.y += t*.22;

    vec2 id = floor(st);//in same rect,make same id,seems like 11,21,31,41

    float n = fract(sin(id.x*76.34)*768.34);
    st.y += n;  //make different rhythm,offset y eath box
    id = floor(st); //initialize  id
    st = fract(st)-.5;//movepoint to center

    t += fract(sin(id.x*76.34 + id.y*1453.7)*768.34)*6.283;//make per rect different time 
    //same id t plus value is same. multiply 2π,the reason : t is use to sin function

    float y = - sin( t + sin(t + sin(t)*.5 )) *.43;//water drop animation
    vec2 p1 = vec2(0. , y);
    vec2 o1 = (st - p1)/a;
    float d = length(o1);

    float ml = S(.07, .0, d);

    vec2 o2 = (fract(uv*a.x*vec2(1.,2.)) - .5)/vec2(1.,2.);
    d = length(o2);//*vec2 change the y coordnate to create more drop

    float m2 = S(.3*(.5-st.y), .0, d)*S(-.1, .1, st.y - p1.y);
    //if st.y>p1.y，return 1,else return 0, so can hide the point under of p1.
    //5-st.y  scale by y

    //if(st.x > .46|| st.y > .49) ml = 1.;//debug line

	return vec2(ml*o1*30.+ m2*o2*10.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    float screenScale = iResolution.x/iResolution.y;
    uv.x *= screenScale ;

  	float g = S(0.,.5,.3*length(uv*vec2(1.,1.5)));
    g *= g;
    vec2 GM = g*uv*.2;//convex lens


    vec2 m = iMouse.xy/iResolution.xy;
    float t = iTime*.07+ m.x*.3;//if pause,can control time by moving mouse;

    float rt = t*230.;//run speed
    float cameAnima = cos(rt)*.025;
    float runRhythm = clamp(0.,3.,sin(rt + sin(rt+sin(rt)*.5)))*.03;
    //the peak is to the left sine,move up quikly and down slowly,
    //clamp is just let he stay at ground a moment

    float runSideId =  step(0.,sin(rt*.5));

    vec3 camPos = vec3(.5 + cameAnima * runSideId ,.2 + cameAnima, 0.);
    vec3 lookat = vec3(.5 , .2+runRhythm , 1.);


    //uv.x += sin(uv.y*40.)*.1;
    vec2 rainDistort = Rain(uv*5. , t)*.5;
    rainDistort += Rain(uv*7. , t)*.3;

    uv.x += sin(uv.y*70.)*.003;
    uv.y += sin(uv.x*70.)*.003;

    ray r = GetRay(uv - rainDistort*.5 - GM, camPos, lookat, 2.);// - refract other  side



    float roadWith = 4.2+ (.5 + sin(t/2.)*.5);// road width

    vec3 col = StreetLights(r, t, roadWith);
    col += HeadLights(r, t, roadWith);
    col += TailLights(r, t, roadWith);
    col += EnvLights(r, t, roadWith);



    col += (r.d.y + .55)*vec3(.2,.1,.5)*.3;//sky color
    //col = vec3(0.);
    col += (S(.0,.5,r.d.y)*1.3)*vec3(.9,.2, .1)*.5;//sky color 2



    //col = vec3(g);
    //col = vec3(rainDistort, 0.);

    // Output to screen
    fragColor = vec4(col, 1.);
}

