// Analytic intersection of a Goursat surface (degree 4 equation). I think I
// still have a bug...

// List of ray-surface intersectors at https://www.shadertoy.com/playlist/l3dXRf
// and http://iquilezles.org/www/articles/intersectors/intersectors.htm


// (x4 + y4 + z4) - (r2^2)·(x2 + y2 + z2) + r1^4 = 0;
float iGoursat( in vec3 ro, in vec3 rd, in float ka, float kb )
{
    float po = 1.0;

    vec3 rd2 = rd*rd; vec3 rd3 = rd2*rd;
    vec3 ro2 = ro*ro; vec3 ro3 = ro2*ro;

    // raw quartic
    float k4 = dot(rd2,rd2);
    float k3 = dot(ro ,rd3);
    float k2 = dot(ro2,rd2) - kb/6.0;
    float k1 = dot(ro3,rd ) - kb*dot(rd,ro)/2.0;
    float k0 = dot(ro2,ro2) + ka - kb*dot(ro,ro);

    // make leading coefficient 1
    k3 /= k4;
    k2 /= k4;
    k1 /= k4;
    k0 /= k4;

    // reduced cubic
    float c2 = k2 - k3*(k3);
    float c1 = k1 + k3*(2.0*k3*k3-3.0*k2);
    float c0 = k0 + k3*(k3*(c2+k2)*3.0-4.0*k1);

#if 1
    // prevent |c1| from being too close to zero
    // reduced cubic
    if( abs(c1) < 0.1*abs(c2) )
    {
        po = -1.0;
        float tmp=k1; k1=k3; k3=tmp;
        k0 = 1.0/k0;
        k1 = k1*k0;
        k2 = k2*k0;
        k3 = k3*k0;

        c2 = k2 - k3*(k3);
        c1 = k1 + k3*(2.0*k3*k3-3.0*k2);
        c0 = k0 + k3*(k3*(c2+k2)*3.0-4.0*k1);
    }
#endif


    c0 /= 3.0;

    float Q = c2*c2 + c0;
    float R = c2*c2*c2 - 3.0*c0*c2 + c1*c1;
    float h = R*R - Q*Q*Q;

    // 2 intersections
    if( h>0.0 )
    {
        h = sqrt(h);

        float s = sign(R+h)*pow(abs(R+h),1.0/3.0); // cube root
        float u = sign(R-h)*pow(abs(R-h),1.0/3.0); // cube root

        float x = s+u+4.0*c2;
        float y = s-u;
        float ks = x*x + y*y*3.0;
        float k = sqrt(ks);

		float t = -0.5*po*abs(y)*sqrt(6.0/(k+x)) - 2.0*c1*(k+x)/(ks+x*k) - k3;
        return (po<0.0)?1.0/t:t;
    }

	// 4 intersections
    float sQ = sqrt(Q);
    float w = sQ*cos(acos(-R/(sQ*Q))/3.0);
  //float w = sQ*cos(atan(sqrt(-h),-R)/3.0);

    float d2 = -w - c2; if( d2<0.0 ) return -1.0;
    float d1 = sqrt(d2);
    float h1 = sqrt(w - 2.0*c2 + c1/d1);
    float h2 = sqrt(w - 2.0*c2 - c1/d1);

    float t1 = -d1 - h1 - k3; t1 = (po<0.0)?1.0/t1:t1;
    float t2 = -d1 + h1 - k3; t2 = (po<0.0)?1.0/t2:t2;
    float t3 =  d1 - h2 - k3; t3 = (po<0.0)?1.0/t3:t3;
    float t4 =  d1 + h2 - k3; t4 = (po<0.0)?1.0/t4:t4;

    float t = 1e20;
    if( t1>0.0 ) t=t1;
    if( t2>0.0 ) t=min(t,t2);
    if( t3>0.0 ) t=min(t,t3);
    if( t4>0.0 ) t=min(t,t4);
    return t;
}

vec3 nGoursat( in vec3 pos, float ka, float kb )
{
    return normalize( 4.0*pos*pos*pos - 2.0*pos*kb );
}

#define AA 2

#define ZERO min(iFrame,0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
	float an = 0.3*iTime;
	vec3 ro = vec3( 3.2*cos(an), 1.5, 3.2*sin(an) );
    vec3 ta = vec3( 0.0, -0.05, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    // object animation
    float def = smoothstep(-1.0,-0.4,-cos(0.3*iTime));
    float ra = 0.3 + def*0.6*sin(1.311*iTime+0.0);
    float rb = abs(ra) + 0.6 + def*0.25*sin(0.73*iTime+3.0);


    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
	    vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

        // raytrace
        float t = iGoursat( ro, rd, ra, rb );

        // shading/lighting	
	    vec3 col = vec3(0.08)*(1.0-0.3*length(p));

	    if( t>0.0 )
	    {
            vec3 pos = ro + t*rd;
		    vec3 nor = nGoursat( pos, ra, rb );
            vec3 lig = normalize(vec3(0.7,0.6,0.3));
            vec3 hal = normalize(-rd+lig);
            float inn = dot(nor,normalize(pos));
		    float dif = clamp( dot(nor,lig), 0.0, 1.0 );
		    float amb = (0.5+0.5*nor.y)*(0.6+0.4*inn)*(0.6+0.4*pow(0.5+0.5*inn,2.0));

            const float fr = 20.5;
            vec3 th = abs(nor);
            //col = vec3(0.4) + 0.4*smoothstep(-0.01,0.01,cos(pos.x*fr*0.5)*cos(pos.y*fr*0.5)*cos(pos.z*fr*0.5)); 
            col = vec3(0.6) + 0.4*smoothstep(-0.01,0.01,cos(pos.x*fr*0.5)*cos(pos.y*fr*0.5)*cos(pos.z*fr*0.5));
            float wi = smoothstep(-1.0,-0.98-0.015*th.x,cos(pos.x*fr))
                      *smoothstep(-1.0,-0.98-0.015*th.y,cos(pos.y*fr))
                      *smoothstep(-1.0,-0.98-0.015*th.z,cos(pos.z*fr));
            col *= wi;
#if 0
		    col *= vec3(0.3,0.5,0.7)*amb + vec3(1.1,0.8,0.6)*dif;
            col += wi*0.5*pow(clamp(dot(hal,nor),0.0,1.0),32.0)*dif;
#else
		    col *= amb;
            float fre = clamp(1.0+dot(rd,nor),0.0,1.0);
            col += col*fre;
#endif            
	    }

        col = sqrt( col );

	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	// dither to remove banding in the background
    tot += fract(sin(fragCoord.x*vec3(13,1,11)+fragCoord.y*vec3(1,7,5))*158.391832)/255.0;


    fragColor = vec4( tot, 1.0 );
}

const float PI = 3.14159265359;
const float TWO_PI = 6.28318530718;

// params
#define MARCHING_STEPS 100
#define MATERIAL_COLOR_A vec3(0.2, 0.3, 0.9)
#define MATERIAL_COLOR_B vec3(0.1, 0.2, 0.1)
#define MATERIAL_COLOR_C vec3(0.3, 0.9, 0.1)

#define SHADOW_STEPS 16
#define AO_STEPS 8

// noise
// Volume raycasting by XT95
// https://www.shadertoy.com/view/lss3zr
float hash(in float x) {
  return fract(sin(x) * 43237.5324);
}
vec3 hash3(in float x) {
  return vec3(
      hash(x +   .0),
      hash(x +  53.0),
      hash(x + 117.0)
    );
}
float noise(in vec3 x) {
  vec3 f = fract(x);
  vec3 i = floor(x);
  f = f*f*(3.0-2.0*f);
  float n = i.x*1.0 + i.y*57.0 + i.z*113.0;
  return mix(mix(mix(hash(n +   0.0), hash(n +   1.0), f.x),
                 mix(hash(n +  57.0), hash(n +  58.0), f.x), f.y),
             mix(mix(hash(n + 113.0), hash(n + 114.0), f.x),
                 mix(hash(n + 170.0), hash(n + 171.0), f.x), f.y), f.z);
}
mat3 m = mat3( 0.64,  0.80,  0.60,
              -0.80,  0.36, -0.48,
              -0.60, -0.48,  0.64 );
float fbm(in vec3 x) {
  float f = 0.0;
  f += 0.5000*noise(x); x=m*x*2.02;
  f += 0.2500*noise(x); x=m*x*2.03;
  f += 0.1250*noise(x); x=m*x*2.01;
  f += 0.0625*noise(x);
  return f;
}
float usin(in float x) {
  return 0.5 + 0.5*sin(x);
}
vec2 rotate(in vec2 p, in float r) {
  float c=cos(r), s=sin(r);
  return mat2(c, -s, s, c) * p;
}


float box(in vec3 p, in vec3 b) {
  vec3 d = abs(p) - b;
  return length(max(d, 0.)) + min(max(d.x, max(d.y, d.z)), 0.);
}
float plane(in vec3 p, in vec3 n, in float h) {
  return dot(p, n) - h;
}

float stone(in vec3 p, in float r, in float seed) {
  float b = box(p, vec3(r));
  for(int i=0;i<8;i++) {
    float fi = float(i+1);
    vec3 n = normalize(-1.0+2.0*hash3(fi*seed));
    b = max(b, plane(p, n, 0.5*r+(0.1*hash(fi+seed))));
  }
  return b;
}

vec2 opU(in vec2 d1, in vec2 d2) {
  return d1.x<d2.x ? d1 : d2;
}
vec3 objTx(vec3 p) {
	p.y -= 1.0;
	p.xy = rotate(p.xy, iTime*0.20);
	p.yz = rotate(p.yz, iTime*0.14);
	p.zx = rotate(p.zx, iTime*0.17);
    return p;
}

vec2 map(in vec3 p) {
  float ground = plane(p+vec3(0.,0.2*fbm(p*7.0), 0.), vec3(0.,1.,0.), -1e-4);
  vec3 q = p;
  q = objTx(q);
  float t = iTime*0.1;
  float ft = fract(t);
  float it = floor(t);

  float s1 = stone(q, 0.5, it);
  float s2 = stone(q, 0.5, it+1.0);
  float obj = mix(s1, s2, ft);
  return opU(vec2(ground, 0.), vec2(obj, 1.0));
}

vec2 march(in vec3 ro, in vec3 rd) {
  float mn=0.0, mx=1000.0;
  float thr = 1e-4;

  float d=0.0, m=-1.0;
  for(int i=0;i<MARCHING_STEPS;i++) {
    vec3 pos = ro + rd*d;
    vec2 tmp = map(pos);
    if(tmp.x<thr || mx<tmp.x) break;
    d += tmp.x*0.5;
    m = tmp.y;
  }
  if(mx<d) m = -1.0;
  return vec2(d, m);
}

vec3 calcNormal(in vec3 p) {
  vec2 e = vec2(1.0, -1.0) * 1e-4;
  return normalize(
      e.xyy * map(p + e.xyy).x +
      e.yxy * map(p + e.yxy).x +
      e.yyx * map(p + e.yyx).x +
      e.xxx * map(p + e.xxx).x
    );
}

float maxHei = 5.8;
float calcSoftShadow(in vec3 ro, in vec3 rd, in float tmn, in float tmx) {
  // bouding volume
  float tp = (maxHei - ro.y)/rd.y;
  if(tp>0.0) tmx = min(tmx, tp);

  float res = 1.0;
  float t = tmn;
  for(int i=0;i<SHADOW_STEPS;i++) {
    float h = map(ro + rd*t).x;
    res = min(res, 8.0*h/t);
    t += clamp(h, 0.02, 0.1);
    if(res<0.005 || tmx<res) break;
  }
  return clamp(res, 0., 1.);
}
float calcAO(in vec3 pos, in vec3 nor) {
  float occ = 0.0;
  float sca = 1.0;
  for(int i=0;i<AO_STEPS;i++) {
    float hr = 0.01 + 0.12*float(i)/4.0;
    vec3 aopos = nor*hr + pos;
    float dd = map(aopos).x;
    occ += -(dd-hr)*sca;
    sca *= 0.95;
  }
  return clamp(1.0-3.0*occ, 0., 1.) * (0.5+0.5*nor.y);
}


vec3 render(in vec3 ro, in vec3 rd){
  // ray march and get position/normal/reflect vector.
  vec2 res = march(ro, rd);
  float d=res.x, m=res.y;
  vec3 pos = ro + rd * d;
  vec3 nor = calcNormal(pos);
  vec3 ref = reflect(rd, nor);

  // material
  vec3 color = vec3(0.0);
  if(m==0.0) {
    color = mix(vec3(0.3, 0.13, 0.02), vec3(0.3, 0.8, 0.1),
    pow(fbm(pos*0.5), 2.0));
  }
  else if(m==1.0) {
    vec3 mtlpos = objTx(pos);
    color = (
      MATERIAL_COLOR_A*fbm(mtlpos*vec3(0., 0., 1.)*42.0) +
      MATERIAL_COLOR_B*fbm(mtlpos*vec3(1.)*15.0) +
      MATERIAL_COLOR_C*fbm(mtlpos*vec3(1.)* 1.0)
    ) ;
  }

  // lighting
  /*
	reference :
	"Raymarching - Primitives" by iq 
	https://www.shadertoy.com/view/Xds3zN
  */
  float occ = calcAO(pos, nor);
  vec3 lp = vec3(cos(iTime), 1.+5.0*usin(iTime*0.3), sin(iTime));
  vec3 ld = normalize(lp - pos);
  float lr = 1.0;
  vec3 hal = normalize(ld - rd);
  float amb = sqrt(clamp(0.5+0.5*nor.y, 0., 1.));
  float dif = clamp(dot(nor, ld), 0., 1.);
  float bac = clamp(dot(nor, normalize(vec3(-ld.x, 0., -ld.y))), 0., 1.) * clamp(1.0-pos.y, 0., 1.);
  float dom = smoothstep(-0.2, 0.2, ref.y);
  float fre = pow(clamp(1.0+dot(nor, rd), 0., 1.), 2.);
  // calc SoftShadow
  dif *= calcSoftShadow(pos, ld, 0.02, 2.5);
  dom *= calcSoftShadow(pos, ref, 0.02, 2.5);
  // calc specular
  float spe = pow(clamp(dot(nor, hal), 0., 1.), 16.0) * dif * (0.04+0.96*pow(clamp(1.0+dot(hal, rd), 0., 1.), 5.0));
  // calc 'light n?' from lighting params
  vec3 lin = vec3(0.);
  lin += 5.80 * dif*vec3(1.0);
  lin += 2.85 * amb*vec3(1.0) * occ;
  lin += 0.55 * bac*vec3(1.0) * occ;
  lin += 0.85 * dom*vec3(1.0) * occ;
  lin += 0.25 * fre*vec3(1.0) * occ;
  // add distance attenuation
  float att = 1.0/pow(length(ld)/lr + 1.0, 2.0);
  lin *= att;
  color *= lin;
  if(m==1.0) color += 7.0 * spe*vec3(1.0, 1.0, 1.0);

  color *= exp(-0.001*d*d*d);
  return color;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 uv = fragCoord.xy/iResolution.xy;
  vec2 p = (fragCoord.xy*2.-iResolution.xy)/min(iResolution.x, iResolution.y);
  vec3 color = vec3(0.);
  float t = iTime * 0.3;

  vec3 ro = vec3(cos(t), 1.+usin(t*0.3), sin(t));
  ro.xz *= 1.0;
  vec3 tar = vec3(0., 1., 0.);
  vec3 cz = normalize(tar - ro);
  vec3 cx = normalize(cross(cz, vec3(0., 1., 0.)));
  vec3 cy = normalize(cross(cx, cz));
  vec3 rd = mat3(cx, cy, cz) * normalize(vec3(p, 1.0));

  color = render(ro, rd);

  fragColor = vec4(color, 1.);
}

float sdSphere(vec3 p, float r){
    //vec3 q = fract(p)*2.0-1.0;
    return length(p)-r;
}

float sdCube(vec3 p, vec3 b){
	vec3 d = abs(p) - b;
	return length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0); // remove this line for an only partially signed sdf 
}

float sdUnion(float dist1, float dist2){
	return min(dist1,dist2);   
}

float sdIntersect(float dist1, float dist2){
	return max(dist1,dist2);  
}

float sdNegate(float dist1, float dist2){
 	return max(-dist1, dist2);   
}

float marchRay(vec3 o, vec3 r){
 	float t = 0.0;
    for(int i = 0; i < 512 && t >= 0.0; i++){
		vec3 p = o+r*t;
        float cube = sdCube(fract(p)*2.0-1.0, vec3(0.9,0.9,0.9));
        float sphere = sdSphere(fract(p)*2.0-1.0, 1.25);

        float d = sdNegate(sphere, cube)-0.1;
        t += d*0.25;
    }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;

    uv.x *= iResolution.x/iResolution.y;

    vec3 r = normalize(vec3(uv, 1.0));
    float theta = iTime/10.0;
    r.xz *= mat2(cos(theta), sin(theta), -sin(theta), cos(theta));
    vec3 o = vec3(0.5, 0.5, -iTime);

    float t = marchRay(o, r);

    float fog = 1.0/(1.0+t*t*0.1);
    vec3 fc = vec3(fog);

    fragColor = vec4(fc, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float str = tan(uv.x*10.0 + sin(uv.x*5.0 + iTime) + sin(iTime)) ;
    if(str > 2.0)
        fragColor = vec4(1.0,0.0,0.0,0.0);
    else if(str > 0.8)
        fragColor = vec4(1,0.5,0.0,0.0);
    else if(str > 0.25)
        fragColor = vec4(1.0,1.0,0.0,0.0);
    else if(str > -0.25)
        fragColor = vec4(0.0,1.0,0.0,0.0);
    else if(str > -0.8)
        fragColor = vec4(0.0,0.8,1.0,0.0);
    else if(str > -2.0)
        fragColor = vec4(0.0,0.0,1.0,0.0);
    else 
        fragColor = vec4(0.5,0.0,1.0,0.0);



}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.01;
const float VOLUME_STEP = 0.03;
const float VOLUME_STEP_LIGHT = 0.1;
const float VOLUME_DENSITY = 0.04;
const float LIGHT_INTESITY = 0.03;


// 3D hash function
float hash(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// 3D precedural noise
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    // interpolate between hashes of adjacent grid points
    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

// 3D noise layered in several octaves
float layeredNoise(in vec3 x) {
    x += vec3(10.0, 5.0, 6.0);
    return 0.6*noise(x*5.0) + 0.4*noise(x*10.0) + 0.2*noise(x*16.0) - 0.2;
}

// singed distance function of the y=0 plane
float planeSDF(vec3 p) {
    return p.y;
}

float sphereSDF(vec3 p, vec3 center, float r) {
    return length(center - p) - r;
}

float boxSDF(vec3 p, vec3 size) {
    vec3 d = abs(p) - (size / 2.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

// singed distance function to enire scene
float sceneSDF(vec3 samplePoint) {
   	//float sphere = sphereSDF(samplePoint, vec3(0.0, 0.0, 0.0), 1.0);
    float box = boxSDF(samplePoint, vec3(1.5, 1.0, 1.5));
    //return sphere;
    return box;
}

float sampleVolume(vec3 p, float densityMultiplier) {
    return layeredNoise(p)*densityMultiplier;
}

// integrate density along ray
float marchDensity(vec3 startPoint, vec3 marchingDirection, float start, float end) {
    float depth = start;
    float density = 0.0;
    float d;
    vec3 samplePoint;

    while (depth < end) {
        samplePoint = startPoint + depth * marchingDirection;
    	d = sceneSDF(samplePoint);
        // outside volume
        if (d > EPSILON) {
            depth += d;
        }
        // inside volume
        else {
            // sum desity values
         	density += sampleVolume(samplePoint, VOLUME_DENSITY)*min(-d, VOLUME_STEP_LIGHT);
            depth += VOLUME_STEP_LIGHT;
        }
        // opaque: stop early
        if (density >= 1.0) return 1.0;
    }
    return density;
}

float volumeAbsorption(float lightIntensity, float acumulatedDensityToPoint) {
    return max(0.0, lightIntensity - acumulatedDensityToPoint);
}

// integrate illumination along ray
float marchVolumetricLighting(vec3 eye, vec3 marchingDirection, vec3 lightPos, float start, float end) {
    float depth = start;
    float accumulatedDensity = 0.0;
    float brightness = 0.0;
    float d;
    vec3 samplePoint;

    while (depth < end) {
        samplePoint = eye + depth * marchingDirection;
    	d = sceneSDF(samplePoint);
        // outside volume
        if (d > EPSILON) {
            depth += d;
        }
        // inside volume
        else {
            float densityAtPosition = sampleVolume(samplePoint, VOLUME_DENSITY);
            // integrated density between eye and point
            accumulatedDensity += densityAtPosition*min(-d, VOLUME_STEP);
            // get integrated density between point and light
            vec3 lightRay = vec3(lightPos - samplePoint);
            float acumulatedDensityToLight = marchDensity(samplePoint, normalize(lightRay), start, end);
            // point brightness is dimmed by volume between eye and point
            float pointBrightness = volumeAbsorption(LIGHT_INTESITY, acumulatedDensityToLight);
            // sum up brightness values along ray
            brightness += volumeAbsorption(pointBrightness, accumulatedDensity);
            depth += VOLUME_STEP;
        }
        if (brightness >= 1.0) return 1.0;
    }
    return brightness;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

// estimate normal by aproximatind the first deriviative of the sceneSDF
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

// generate camera transform
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
   	//vec3 eye = vec3(0.0, -5.0, -6.0);
    vec3 eye = vec3(6.0*cos(iTime*0.1), -3.0, 6.0*sin(iTime*0.1));
    mat3 viewToWorld = viewMatrix(eye, vec3(0.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0));
    vec3 worldDir = viewToWorld * viewDir;
    vec3 lightPos = vec3(1.2*cos(iTime), 1.0, 1.2*sin(iTime));

    float brightness = marchVolumetricLighting(eye, worldDir, lightPos, MIN_DIST, MAX_DIST);


    vec3 color = vec3(1.0, 1.0, 1.0)*brightness;

    fragColor = vec4(color, 1.0);
}

