//thanks IQ https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdRoundedCylinder( vec3 p, float ra, float rb, float h )
{
    vec2 d = vec2( length(p.xz)-2.0*ra+rb, abs(p.y) - h );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0)) - rb;
}
float sdSphere (vec3 p, float r) {
    return length(p)-r;
}
SDF portalSDF (vec3 p, int PI) {///// portal sdf
    vec2 pa = p.xy;
    p.x = pa.y;
    p.y = pa.x;
    float sd = sdSphere(p, 0.4);//sdRoundedCylinder(p, 0.2, 0.05, 0.0);
    Material pp = portal;
    pp.pi = PI;
    return SDF(pp, sd);
}
SDF lightSDF (vec3 p, vec3 pp) {
    return SDF(showLight, length(pp-p)-0.1);
}
SDF ground (vec3 p, float h) {
    return SDF(soft, p.y-h);
}
SDF ground (vec3 p, float h, float thickness) {
    float freq = 3.;
    float displace = sin(p.x*freq)*sin(p.x*freq)*cos(p.z*freq)*cos(p.z*freq)*0.8;
    return SDF(soft, abs(p.y-h)-thickness*displace);
}
SDF sphere (vec3 p, float r) {
    return SDF(object, length(p)-r);
}
//thanks to IQ for the box sdf function
SDF box (vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
	return SDF(def, length(max(d,0.0))
         + min(max(d.x,max(d.y,d.z)),0.0));
}
SDF room (vec3 p, vec3 b) {
    return SDF(soft, -box(p, b).d);
}
SDF sdfADD (SDF a, SDF b) {
    if(a.d<b.d) {
        return a;
    } else {
        return b;
    }
}
SDF sdf (vec3 p) {
    vec2 rp = rotate2D(p.xz, 0.0);
    SDF s = sdfADD(
        sphere(p, 0.2), 
        sdfADD(
            ground(p, -1.5, 0.3),
            room(vec3(rp.x, p.y, rp.y), vec3(3.))
        )
    );
    //show the lights (dev)
    /*
    for(int i = 0; i<numLights; i++) {
        s = sdfADD(s, lightSDF(p, lights[i].position));
    }
*/
    for(int i = 0; i<numPortals; i++) {//portal start
        Portal por = portals[i];
        //don't draw un-filled ones
        if(por.filled==0) {
            continue;
        }
        //rotate
        vec3 rp = p-por.start;
        rp.zy = rotate2D(rp.zy, por.rot.y);
        rp.xz = rotate2D(rp.xz, por.rot.x);
        s = sdfADD(s, portalSDF(rp, i));
    }
    return s;
}
Ray trace (vec3 o, vec3 r, int reray) {
    vec3 p = vec3(0.0, 0.0, 0.0);
    SDF s = SDF(def, 10000.);
    float t = 0.;
    int steps = 0;
    for(int i = 0; i<128; i++) {
        p = o+r*t;
        s = sdf(p);
        t+=s.d*0.4;
        //curve of light around portals
        if(s.mat.reflection<0.&&s.d>0.&&reray==0) {
            vec3 ppos = portals[s.mat.pi].start;
            vec3 bend = normalize(p-ppos);
            //interpolate
            float x = length(p-ppos);

            float bend_amount = sin(pow(0.5, x*10.-1.651)) * (1.-max(0.0,dot(p-o, p-ppos))) * 0.01;
            r = normalize(mix(r, bend, bend_amount));
        }

        if(s.d<HIT_THRESHOLD){
            steps = i;
            break;
        }
        if(t>10.0) {
            steps = i;
            t = 1000.;
        	break;
        }
    }
    return Ray(p, steps, t, s);
}
const float E = 0.0001;
vec3 estimateNormal (vec3 p) {
    return normalize(vec3(
        sdf(vec3(p.x+E, p.y, p.z)).d-sdf(vec3(p.x-E, p.y, p.z)).d,
        sdf(vec3(p.x, p.y+E, p.z)).d-sdf(vec3(p.x, p.y-E, p.z)).d,
        sdf(vec3(p.x, p.y, p.z+E)).d-sdf(vec3(p.x, p.y, p.z-E)).d
    ));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    vec2 mouse = iMouse.xy/iResolution.xy;
    mouse-=0.5;
    uv.x-=iResolution.x/iResolution.y/2.;
    uv.y-=0.5;
    uv*=2.0;
    float perspective = 0.8;
    float time = iTime*0.5;
    vec3 cam = vec3(sin(time), 0., cos(time));
    cam.xz*=0.8;
    vec3 ray = normalize(vec3(uv.x*perspective, uv.y*perspective, 1.0));
    //cam.zy = rotate2D(cam.zy, mouse.y*5.0);
    ray.zy = rotate2D(ray.zy, mouse.y*5.0);

    //cam.xz = rotate2D(cam.xz, -mouse.x*10.0);
    ray.xz = rotate2D(ray.xz, -mouse.x*10.0);
    vec3 col = vec3(0.);

    float ambient = 0.3;
    Material m;
    Material pm;
    for(int times = 0; times<MAX_RERAY; times++) {//max reflections or teleportations
        Ray t = trace(cam, ray, times);
        m = t.sdf.mat;//the material
        vec3 norm = estimateNormal(t.end);
        vec3 tcol = m.color*ambient;
        vec3 refl = reflect(normalize(ray), norm);
        //calculate light intensity at the point for each light
        for(int i = 0; i<numLights; i++) {
            Light l = lights[i];
            vec3 toLight = normalize(l.position-t.end);
            float diffuse = max(0.0, dot(toLight, norm)*m.diffuse);
            float specular = pow(max(0.0, dot(refl, toLight)), 16.0)*m.specular;

            //float levels = 5.;//levels of shadows
            float lightAmount = (diffuse+specular);//intensity of light. Default is (diffuse+specular)
            tcol += (1.-ambient)*m.color*l.intensity*l.color*lightAmount;// /float(numLights)
        }
        tcol -= float(t.steps)*float(t.steps)*0.00004;//ao
        float fog = 1.0 / (1.0 + t.d*t.d*0.05);
        tcol*=fog;
        if(m.reflection>0.0&&times<MAX_RERAY-1) {
            ray = refl;
            //ray = normalize(ray + 0.5*ray*texture(iChannel0, t.end).xyz);//add distortion
            //ray = normalize(ray + 0.5*ray*rand3(t.end*4.));//add distortion
            cam = t.end + ray*HIT_THRESHOLD;
            col += tcol*(1.-m.reflection);
            /*
            for(int reach = 0; reach<16; reach++) {
                SDF s = sdf(cam);
                if(s.d<=HIT_THRESHOLD*20.) {
                    cam += ray*0.08;//(HIT_THRESHOLD-s.d)*40.;
                } else {
                    break;
                }
            }
			*/
        } else if (m.reflection<0.) {//signifies a portal
            Portal por = portals[m.pi];//in portal
            Portal OUT = portals[por.endPortal];//out portal
            col += tcol*0.0 + 0.4*por.color*pow(1.7-dot(normalize(cam-t.end), norm), 8.);//add portal glow
            vec3 off = t.end-por.start;//offset from the original portal
            cam = OUT.start+off;//specify the new starting position after portal
            float sdd = t.sdf.d;
            ray = normalize(mix(ray, -norm, 0.0));
            for(int reach = 0; reach<20; reach++) {//leave the portal region sdf
                SDF s = sdf(cam);
                if(s.d<=HIT_THRESHOLD) {
                    cam += ray*0.2;
                } else {
                    break;
                }
            }
        } else {
            //tcol *= texture(iChannel1, getTexPos(t.end, norm)*0.1).r;//texture everything
            if(pm.reflection>0.0) {
            	col = col*(1.-pm.reflection) + tcol*(pm.reflection);
            } else {
                col += tcol;
            }
            break;
        }
        pm = m;
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

float maxDist = 75.;
vec3 camRot = vec3(0, 0, 0);
vec3 lightDir = vec3(.8,1.,-.1);
vec3 lightColor = vec3(.65,.55,.5)*1.5;

float noise(vec2 p)
{
    return fract(sin(p.x*16.+p.y*6463.)*3732.);
}

float smoothNoise(vec2 p)
{
    vec2 id = floor(p*10.);
    vec2 ld = fract(p*10.);

    ld = ld*ld*(3.-2.*ld);

    float bl = noise(id);
    float br = noise(id+vec2(1., 0.));
    float b = mix(bl, br, ld.x);

    float tl = noise(id+vec2(0., 1.));
    float tr = noise(id+vec2(1., 1.));
    float t = mix(tl, tr, ld.x);

    float bt = mix(b, t, ld.y);

    return bt;
}

float getWaterHeight(vec3 p)
{
    return  4.+ iTime/20.+smoothNoise(p.xz/3.+iTime/7.)/200.;
}

vec2 map(vec3 p)
{
    float waterHeight = getWaterHeight(p);
    float a = p.y*0.3;
    p+=vec3(sin(a), 0, cos(a))*4.;
    float mat = 0.;
    float terrainHeight=25.;
    float base = smoothNoise(p.xz/640.)*terrainHeight;
    base += 0.5*pow(smoothNoise(p.xz/320.), 3.)*terrainHeight;
    base += 0.125*pow(smoothNoise(p.xz/80.), 3.)*terrainHeight;
    base += 0.02*pow(smoothNoise(p.xz/20.), 2.)*terrainHeight;
    //p-=vec3(sin(a), 0, cos(a))*7.;
    base += 0.005*pow(smoothNoise(p.xz/7.), 2.)*terrainHeight;
    if(p.y <= waterHeight)
    {
        mat = 1.;
        return vec2(waterHeight-p.y, mat);
    }
    return vec2(base-p.y, mat);
}


vec2 ray(vec3 ro, vec3 rd, float minD, float maxD, float stepSize) // x channel - distance to hit, y channel - material
{
    float t = minD;
    float lastVal; 

    while(t < maxD)
    {
        vec2 mapSample = map(ro+rd*t);
        if(mapSample.x > 0.)
            return vec2(t-(abs(mapSample.x)/(abs(mapSample.x)+abs(lastVal)))*stepSize, mapSample.y);
        t+=stepSize;
        stepSize*=1.01;
        lastVal = mapSample.x;
    }

    return vec2(maxD, 0);
}

vec3 normal(vec3 p)
{
	float delta = 0.01;
    vec3 x = vec3 (delta, 0.00, 0.00);
	vec3 y = vec3 (0.00, delta, 0.00);
	vec3 z = vec3 (0.00, 0.00, delta);

    float val = map(p).x;
    vec3 grad = vec3(val - map(p - x).x, val - map(p - y).x, val - map(p - z).x);
    return -normalize(grad);
}


vec3 lighting(vec3 n, vec3 ambientLight)
{
    return  max(pow(dot(normalize(lightDir), n), 5.), 0.0) * lightColor * 1.25 +ambientLight;
}

vec3 doFog(vec3 rgb, float dist, float maxD, vec3 rd, vec3 p)
{
    lightDir = normalize(lightDir);
    vec3 fogColor = vec3(0.12, 0.11, 0.13);
    fogColor += 2.*vec3(0.14, 0.12, 0.14)*pow(1.-abs(rd.y), 6.);
    fogColor += .6*lightColor*pow(clamp(dot(rd, lightDir), 0., 1.), 3.);
    if(dist == maxD)
    {
    	fogColor += .3*lightColor*smoothNoise(vec2(p.x, p.z)*10./rd.y/7000.)*smoothNoise(vec2(p.x, p.z)*10./rd.y/3000.)*pow(abs(rd.y), .5);
        fogColor += .3*lightColor*pow(clamp(dot(rd, lightDir), 0., 1.), 50.); 
    }
    return mix(rgb, fogColor, pow(dist/maxD, 1.));
}

vec3 terrainColor(vec3 p)
{
    vec3 n = normal(p);
    float xAlpha = pow(abs(n.x), 2.);
    float yAlpha = pow(abs(n.y), 7.);
    float zAlpha = pow(abs(n.z), 2.);
    vec3 terrainColor = vec3(0);
    terrainColor += texture(iChannel0, vec2(p.z, p.y)/7.).xyz*xAlpha;
    terrainColor += .5*texture(iChannel1, vec2(p.x, p.z)/10.).xyz*yAlpha*vec3(.5,.63,.5);
    terrainColor += .5*texture(iChannel1, vec2(p.x, p.z)/3.).xyz*yAlpha*vec3(.5,.63,.5);
    terrainColor += texture(iChannel0, vec2(p.x, p.y)/7.).xyz*zAlpha;
    return terrainColor*lighting(n, vec3(0.01));
}

vec3 waterColor(vec3 p, vec3 rd)
{
    vec3 reflectedRd = reflect(rd, normal(p));
    vec2 rayResult = ray(p, reflectedRd, 1., maxDist, 0.5);
    vec3 reflection = doFog(terrainColor(p+reflectedRd*rayResult.x), rayResult.x, maxDist, reflectedRd, p);
    return mix(vec3(.22, .2, .18), reflection, clamp(0.5-pow(rd.y, 2.), 0., 1.));
}

float screenRain(vec2 p, vec3 rd)
{
    p.y/=50.;

    return smoothNoise(p+vec2(1., mod(iTime, iResolution.y)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord -  iResolution.xy*.5)/iResolution.y;

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    float lastXInput = 0.;
    float lastYInput = 0.;
    float deltaXInput = iMouse.x-lastXInput;
    float deltaYInput = iMouse.y-lastYInput;
    camRot+=vec3(deltaYInput/100., deltaXInput/100., 0);
    vec3 camP = vec3(0, 6.+getWaterHeight(vec3(1)), iTime/4.);
    vec3 camDir = normalize(vec3(sin(camRot.y), sin(camRot.x), cos(camRot.y)));
    vec3 camRight = cross(camDir, vec3(0, 1., 0));
    vec3 rd = normalize(camDir + camRight*1.*uv.x + vec3(0,1.,0)*1.*uv.y);
    vec2 rayResult = ray(camP, rd, .3, maxDist, 0.1);
    vec3 p = camP+rd*rayResult.x;
	vec3 col = vec3(0);
    if(rayResult.y == 0.)
        col = doFog(terrainColor(p), rayResult.x, maxDist, rd, p);
    if(rayResult.y == 1.)
        col = doFog(waterColor(p, rd), rayResult.x, maxDist, rd, p);

    col += .02*lightColor*pow(screenRain(uv*30.-camRot.y*15., rd), 25.);
    col += .07*lightColor*pow(screenRain(uv*20.-camRot.y*10., rd), 25.);
    col += .15*lightColor*pow(screenRain(uv*10.-camRot.y*10., rd), 25.);
    col += .15*lightColor*pow(screenRain(uv*5.-camRot.y*10., rd), 25.);
    // Output to screen
    fragColor = vec4(col,1.0);
    lastXInput = iMouse.x;
    lastYInput = iMouse.y;
}

