const int NUM_STEPS = 12;
const float PI	 	= 3.141592;
const float EPSILON	= 1e-5;
#define EPSILON_NRM (0.15 / iResolution.x)

// sea
const int ITER_GEOMETRY = 10;
const int ITER_FRAGMENT = 6;
const float SEA_HEIGHT = 0.2;
const float SEA_CHOPPY = 2.0;
const float SEA_SPEED = 0.6;
const float SEA_FREQ = 0.56;
const vec3 SEA_BASE = vec3(0.1,0.19,0.22) * 0.5;
const vec3 SEA_WATER_COLOR = vec3(0.1,0.2,0.4) * 0.75;
#define SEA_TIME (1.0 + iTime * SEA_SPEED)
const mat2 octave_m = mat2(1.0,1.0,-1.2,1.6);

// math
mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}
float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

// lighting
float diffuse(vec3 n,vec3 l,float p) {
    return pow(dot(n,l) * 0.4 + 0.6,p);
}
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (PI * 8.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}

// sky
vec3 getSkyColor(vec3 e) {
    e.y = max(e.y,0.0);
    return vec3(pow(1.0-e.y,2.0)*0.5+0.2, (1.0-e.y)*0.6, (0.6+(1.0-e.y)*0.4)*0.7)*(pow(e.y,10.9)+0.05)*13.2;
}

// sea
float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_GEOMETRY; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	//d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        //choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

vec3 getSeaColor(vec3 p, vec3 n, vec3 l, vec3 eye, vec3 dist) {  
    float fresnel = clamp(1.0 - dot(n,-eye), 0.0, 1.0);
    fresnel = pow(fresnel,4.0) * 0.75;

    vec3 reflected = getSkyColor(reflect(eye,n));    
    vec3 refracted = SEA_BASE + diffuse(n,l,2000.0) * SEA_WATER_COLOR * 0.12; 

    vec3 color = mix(refracted,reflected,fresnel);
    //vec3 color = vec3(fresnel);
    //vec3 color = refracted;

    float atten = max(1.0 - dot(dist,dist) * 0.0001, 0.0);
    color += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 0.28 * atten;

    color += vec3(specular(n,l,eye,60.0))*2.0;

    return color;
}

// tracing
vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}

float heightMapTracing(vec3 ori, vec3 dir, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = map(ori + dir * tx);
    if(hx > 0.0) return tx;   
    float hm = map(ori + dir * tm);    
    float tmid = 0.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ori + dir * tmid;                   
    	float hmid = map(p);
		if(hmid < 0.0) {
        	tx = tmid;
            hx = hmid;
        } else {
            tm = tmid;
            hm = hmid;
        }
    }
    return tmid;
}

// main
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;    
    float time = iTime * 0.3 + iMouse.x*0.01;

    // ray
    vec3 ang = vec3(sin(0.0)*0.1,sin(0.0)*0.2+0.3,0);    
    vec3 ori = vec3(0.0,3.5,5.0);
    vec3 dir = normalize(vec3(uv.xy,-2.0)); //dir.z += length(uv) * 0.15;
    dir = normalize(dir);// * fromEuler(ang);

    // tracing
    vec3 p;
    heightMapTracing(ori,dir,p);
    vec3 dist = p - ori;
    vec3 n = getNormal(p, dot(dist,dist) * EPSILON_NRM);
    vec3 light = normalize(vec3(0.0,1.0,0.8)); 

    // color
    vec3 color = mix(
        getSkyColor(dir),
        getSeaColor(p,n,light,dir,dist),
    	pow(smoothstep(0.0,-0.05,dir.y),0.3));

    // post
	fragColor = vec4(pow(color,vec3(0.75)), 1.0)/pow(length(dist),0.15);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float PI = 3.14159265;

    // teh number of centers
    const float numcenters = 3.0;

    // ...
    const bool fancypantsmode = true;

    const float incr = PI*2.0/numcenters;
    const float maxx = PI*2.0-0.01;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy);

    float x = fragCoord.x;
    float y = fragCoord.y;

    float cx = iResolution.x/2.0;
    float cy = iResolution.y/2.0;

    float w = iResolution.x;
    float h = iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.0, 0.0, 0.0);
	float a = 0.0;
	float b = 0.0;
    for (float d=0.0; d<=maxx; d+=incr) {
        float _r = distance(iMouse.xy, vec2(cx, cy))/iResolution.x;
        float _t = atan(iMouse.y-cy, iMouse.x-cx);
    	float r = iResolution.x*20.0*25.0*sin(_t-PI/3.0)*sin(_t-PI/3.0)/distance(
            fragCoord,
            vec2(cx+cos(d-_t)*w*_r, cy-sin(d-_t)*w*_r)
       	)+iTime*26.0;
        b += r;
        r = floor(r/255.0*2.0)*255.0/2.0;
        a += r;
    }
    // c = floor(c/255*2)*255/2;
    b *= 2.0/numcenters;
    a = mod(a, 255.0);
    b = mod(b, 255.0);
    a *= 2.0;

    if (fancypantsmode) {
    	// black/blue/cyan/white gradient pattern
    	col = vec3(min(a, b)/255.0, a/255.0, max(a, b)/255.0);
    }
    else {
    	// classic black/white crisp checkerboard pattern
    	col = vec3(a/255.0, a/255.0, a/255.0);
    }

    // Output to screen
    fragColor = vec4(col,1.0);


}

//noise & fbm from : https://www.shadertoy.com/view/Xds3Rj

float hash(float x)
{
	return fract(21654.6512 * sin(385.51 * x));
}

float hash(vec2 p)
{
	return fract(21654.65155 * sin(35.51 * p.x + 45.51 * p.y));
}

float lhash(float x, float y)
{
	float h = 0.0;

	for(int i = 0;i < 5;i++)
	{
		h += (fract(21654.65155 * float(i) * sin(35.51 * x + 45.51 * float(i) * y * (5.0 / float(i))))* 2.0 - 1.0) / 10.0;
	}
	return h / 5.0 + 0.02;
	return (fract(21654.65155 * sin(35.51 * x + 45.51 * y))* 2.0 - 1.0) / 20.0;
}

float noise(vec2 p)
{
	vec2 fl = floor(p);
	vec2 fr = fract(p);

	fr.x = smoothstep(0.0,1.0,fr.x);
	fr.y = smoothstep(0.0,1.0,fr.y);

	float a = mix(hash(fl + vec2(0.0,0.0)), hash(fl + vec2(1.0,0.0)),fr.x);
	float b = mix(hash(fl + vec2(0.0,1.0)), hash(fl + vec2(1.0,1.0)),fr.x);

	return mix(a,b,fr.y);
}

//Fractal Brownian Motion 
float fbm(vec2 p)
{
	float v = 0.0, f = 1.0, a = 0.5;
	for(int i = 0;i < 5; i++)
	{
		v += noise(p * f) * a;
		f *= 2.0;
		a *= 0.5;
	}
	return v;
}

