//My earlier variation: https://www.shadertoy.com/view/WtsSzn
//The Original: https://www.shadertoy.com/view/llc3Ws

#define vNum 2.
//number of variations

mat2 rot(float a)
{
 return mat2(cos(a), -sin(a), sin(a), cos(a));   

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    //these lines let me divide the screen into for coord systems
    //from 0-1 so I can have the 4 variations
    float id = floor(uv*vNum).x+floor(uv*vNum).y*vNum;
    uv = fract(uv*vNum);

    //these give me the vignetting,its applied to each of the 4 coord
    //systems now. You can delete this and the "*vig" line at the
    //end of the code to see it without, not a big deal
    vec2 qt = uv;
    qt *=  1.0 - qt.yx;
    float vig = qt.x*qt.y*15.;
    vig = pow(vig, 0.15);

    //saving uv by using a new variable for some reason...
   	vec2 st = uv;

    //all the basic screen scaling stuff at the beginning of most shaders
    st=st*2.-1.;
    st.x *= iResolution.x/iResolution.y;



    //init a color variable
    vec3 color;


	//I do this again? why? I already did it above. This is an example
    //of a random accident that ends up working out.
    st = st*2.0-1.; //it just scales the space back more"*2." and shifts things "-1" riht and up I believe



    //this cool df thing comes from "kig" on shadertoy: https://www.shadertoy.com/view/lll3DB
    //I improvised on it, not sure what it all does yet though.
    float df = pow(abs(st.y+.9)*uv.x*uv.x, 2.9)+pow(abs(st.x+1.)*uv.y*uv.y, 2.);
  	st *= 1.0+.5*df;
    //tryed adding even more to the df
    //df /= pow(abs(st.y-1.39)*uv.x*uv.x*uv.x*uv.x, .9)+pow(abs(st.x-5.)*uv.y*uv.y, 0.4);

    //moves pattern left in time, shows as a rotation because of the 
    //warping by the df code but it's just a shift left
    st.x += iTime*1.0;
    //st.y += iTime*1.0;

    //some more scaling
    st/=1.328;
    st/=10.;

    //gold color
    vec3 gold = vec3(213., 123.,15.)/255.;


    //a loop for the crazy fractal stuff
    for(int i= 0;i<7;i++)
    {

        //I mult the screen by the absolute value of sin(1.48)? what lol
        //sin(1.48) is just a number, I could have just use the number with 
        //either a positive or negative value. lol what? This is the only
        //i'll try to come back and fix this one.
        st*=abs(sin(1.48 ));    
        //then rotate the space 
        st = st*rot(1.4);
        //and then I repeat the two above randomly based on what ends
        //up looking good.
        st = st*rot(1.4);
        st*=abs(sin(2.48 ));
        st = st*rot(1.4);
        st/=abs(sin(10.48 ));

        //and then I scale the space once more after getting the absolute
        //values of st.
        st = abs(st)*2.0-1.0;

		//This allows the fractal to move and actually is very important
        //for some reason.
		st=fract(st+iTime/100.)-0.5;

        //here I add (subtract) some shapes that obviously end up being negative, to the color
        //teh shapes are really just types of circles and they have I believe
        //opacity values based on the index of the for loop.

        		//the base color, id gives variation, it goes form (0-3)
    	color -= (cos(vec3(0.200+id/3.,0.994,0.9464) 
                      //another circle, with id for variation
                  -  (  1./pow(1.0-smoothstep(0.5, 0.62+id/9. , length(st)), 1.)*2. 
                  //subtracting another circle I guess to create rings, and more id for 
                      //omre variation, and float(i) changes things based on the for loop index
                      - (smoothstep(0.3, .48+id/2. , length(st))) + float(i)*40.)));

        //then I mix gold in based on the value of a basic length(st) when it's beyond 0.55
        color = mix(color, gold, smoothstep(0.55,0.6, length(st)));


        //most likely this is only really important because I didn't increase
        //the scaling earlier. You could probably delete this and
        //add st*=1.5 somewhere else and get a similar result.
        //however the +id/9 adds somethign to it.. maybe it just shift based on 
        //the variation number (0 - 3)
        st*=1.5+id/9.;

    }

	fragColor = vec4(color*vig/1.,1.0);
}

float mapSphere(vec3 p, vec3 ray, float radius)
{
	return length(p - ray) - radius;
}

float smin( float a, float b, float k )
{
    float res = exp2( -k*a ) + exp2( -k*b );
    return -log2( res )/k;
}

float mapAll(vec3 p, float time)
{
    const float disp = 7.;
    const float speed = 0.5;
    float displacement = sin(sin(time * speed) * p.x * disp)
        * cos(sin(time * speed) * p.y * disp)
        * sin(sin(time * speed) * p.z * disp) * 0.25;

    float sphere_0 = mapSphere(p, vec3(-2.*sin(time * speed),0.,0.), 1.0) + displacement;
	float sphere_1 = mapSphere(p, vec3(1.*sin(time * speed),0.,0.), 1.0) + displacement;


    return smin(sphere_0, sphere_1, 32.);
}

vec3 calculateNormal(in vec3 p, float time)
{
    const vec3 small_step = vec3(0.001, 0.0, 0.0);

    float gradient_x = mapAll(p + small_step.xyy, time) - mapAll(p - small_step.xyy, time);
    float gradient_y = mapAll(p + small_step.yxy, time) - mapAll(p - small_step.yxy, time);
    float gradient_z = mapAll(p + small_step.yyx, time) - mapAll(p - small_step.yyx, time);

    vec3 normal = vec3(gradient_x, gradient_y, gradient_z);

    return normalize(normal);
}

vec3 trace(vec3 ro, vec3 rd, float time)
{
    int maxSteps = 10;

    float t = 0.0;
    float totalDist = 0.0;

    int i = 0;
    for (; i < maxSteps; i++)
    {
        vec3 position = ro + rd * t;
    	float dist = mapAll(position, time);
        totalDist += dist;

        if (totalDist > 4.0)
        {
            return vec3(0.0, 0.0, 0.0);
        }

        if (dist < 0.01)
        {
            vec3 normal = calculateNormal(position, time);

            vec3 lightPosition = vec3(2.0, -5.0, 3.0);

            vec3 directionToLight = normalize(position - lightPosition);

            float diffuseIntensity = max(0.0, dot(normal, directionToLight));
			vec3 light = vec3(0.0, abs(sin(iTime)), abs(cos(iTime))) * diffuseIntensity;


            return light;
            //return texture(iChannel0, position.xy, 0.0).xyz;
        }
        t += dist;        
    }

    return vec3(0.0, 0.0, 0.0);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec3 origin = vec3(.5, 0.0, -2);
    // Time varying pixel color
    vec3 col = trace(origin, vec3(uv.xy, 1), iTime);

    fragColor = vec4(col.xyz, 1.0);
}

// Fork of "Oak foise" by deerfeeder. https://shadertoy.com/view/MtKcD3
// 2019-04-01 13:33:55


const int noiseSwirlSteps = 8;
const float noiseSwirlValue = 0.5;
const float noiseSwirlStepValue = noiseSwirlValue / float(noiseSwirlSteps);

const float noiseScale = 2.;
const float noiseTimeScale = 0.15;


float simplex(vec3 v);
float getNoise(vec3 v);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 0.5-fragCoord.xy / iResolution.xy;
  	uv = abs(uv);
	float noise = getNoise(vec3(uv * noiseScale, iTime*noiseTimeScale));
    noise = noise * noise * noise * noise * 0.5;  //more contrast
    fragColor = vec4(noise, noise, noise, 1.0);
}



float fbm3(vec3 v) {
    float result = simplex(v);
    result += simplex(v * 2.) / 2.;
    result += simplex(v * 4.) / 4.;
    result /= (1. + 1./2. + 1./4.);
    return result;
}

float fbm5(vec3 v) {
    float result = simplex(v);
    result += simplex(v * 2.) / 2.;
    result += simplex(v * 4.) / 4.;
    result += simplex(v * 8.) / 8.;
    result += simplex(v * 16.) / 16.;
    result /= (1. + 1./2. + 1./4. + 1./8. + 1./16.);
    return result;
}

float getNoise(vec3 v) {
    //  make it curl
    for (int i=0; i<noiseSwirlSteps; i++) {
    	v.xy += vec2(fbm3(v), fbm3(vec3(v.xy, v.z + 1000.))) * noiseSwirlStepValue;
    }
    //  normalize
    return fbm5(v) / 2. + 0.5;
}

