float sphere(vec3 pos, vec3 sp, float radius) 
{
    return length(sp-pos)-radius;
}

float blend(float a, float b, float alpha) {
	float ramp = clamp(0.5+0.5*(b-a)/alpha,0.,1.0);
    return mix(b,a,ramp)-alpha*ramp*(1.-ramp);
}

float distanceFunction(vec3 pos) {
    float td = 100.0;
    for(int i=10;i<30;i+=2) {
        float ip = float(i)/50.0;
        vec3 newP = vec3(pos.y,pos.y,pos.z);
	    td = blend(td,
         sphere(newP,vec3(sin(ip)*.5-cos(iTime*ip+sin(ip)),cos(ip)*.5+sin(iTime*ip+cos(ip)),4.0),0.2),0.3);
    }    
    for(int i=11;i<30;i+=2) {
        float ip = float(i)/50.0;
        vec3 newP = vec3(pos.x,pos.x,pos.z);
	    td = blend(td,
         sphere(newP,vec3(sin(ip)*.5-cos(iTime*ip+sin(ip)),cos(ip)*.5+sin(iTime*ip+cos(ip)),4.0),0.2),0.3);
    }    
    for(int i=1;i<30;i++) {
        float ip = float(i)/50.0;
	    td = blend(td,
         sphere(pos,vec3(sin(ip)*.5-cos(iTime*ip+sin(ip)),cos(ip)*.5+sin(iTime*ip+cos(ip)),4.0),0.2),0.3);
    }    
    return td;
}

struct traceResult {
	float total_dist;
    float dist;
    vec3 position;
};


float trace(vec3 ro, vec3 rd) {
	float d = 0.0;
    float td = 0.0;
    vec3 rp;
    for(int x=0;x<20;x++) {
		rp = ro + rd * d;
        td = distanceFunction(rp);
        if(td < 0.001) {
            break;
        } else if(td > 10.0) {
            return 0.;
        }
        d += td;
    }

    traceResult tr = traceResult(d, td, rp);
    vec3 light = vec3(sin(-iTime*.5)*10.,cos(iTime*.6)*10.,-4.);
    vec3 l = normalize(light - tr.position);
    vec2 e = vec2(0.01,0);
    vec3 n = normalize(tr.dist - vec3(distanceFunction(tr.position-e.xyy),
                            distanceFunction(tr.position-e.yxy),
                            distanceFunction(tr.position-e.yyx)));
    return clamp(dot(n,l),0.,1.);

}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5 * iResolution.xy)/iResolution.y;

    vec3 ro = vec3(0,0,0);
    vec3 rd = normalize(vec3(uv.x,uv.y,1.0));

    float color = trace(ro, rd);

    vec3 col = vec3(color);

    fragColor = vec4(col,1.0);
}

