    vec3 avgcol = vec3(0);
    for(int i = ZERO; i < AA; i++) {
        for(int j = 0; j < AA; j++) {
            vec2 coord = fragCoord + vec2(i, j) / AAf;
            avgcol += shot1(coord, iTime);
        }
    }
   	fragColor = vec4(avgcol / (AAf * AAf), 1.0);
}

#define float3 vec3
#define float2 vec2
#define float4 vec4


#define pal(c) vec3(c/4&1,c/2&1,c&1)*( c<8 ? .66 : 1.)


struct NC
{
    int i0;
    int i1;
    int w;
};


NC near(float3 col)
{
    const float3 W=float3(0.299,0.587,0.114);
    NC res;
    float rv=100.0;
    float luma0=dot(col,W);
    for(int i=0;i<15;i++)
    {
        for(int j=i+1;j<16;j++)
        {
            for(int f=0;f<4;f++)
            {
                float3 icol=mix(pal(i),pal(j),float(f)*.25);
                float3 dist=(col-icol);
                dist*=W;

                float d=length(dist);
                if(d<rv)
                {
                    res=NC(i,j,f);
                    rv=d;
                }
            }
        }
    }
    return res;
}


vec3 col(int x, int y)
{
	float time=iTime;
	vec2 uv = vec2(float(x), float(y))/160.0;
    vec2 uv0=uv;
	float i0=1.1;
	float i1=0.9;
	float i2=0.5;
	float i3=0.6;
	float i4=0.0;
    float r=0.0;
    float g=0.0;
    float b=0.0;
    float w=0.0;
	for(int s=0;s<4;s++)
	{
		vec2 d;
		d=vec2(cos(uv.y*i0-i4+time/i1),-sin(uv.x*i0-i4+time/i1))/i3;
        d+=vec2(-d.y,d.x)*0.7;
		uv.xy+=d;

		i0=(i0-0.2)*1.1;
		i1=(i1-2.0)*1.05;
		i2=(i2-1.51)*1.06;
		i3*=0.8237;
		i4+=0.05;
	    r+=(sin(uv.x-time)*0.75+0.5)/i2;
	    b+=(sin(uv.y+time)*0.75+0.5)/i2;
	    g+=(sin((uv.x+uv.y+sin(time*0.5))*0.5)*0.75+0.5)/i2;
        w+=1.0/i2;
	}
    r/=w;
    g/=w;
    b/=w;
	return vec3(r,g,b);
}

float4 border(int y)
{
    return float4(pal(int(float(y)+iTime*90.+sin(iTime))/12%8),1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord)/iResolution.xy;

    int ix;
    int iy;

    int sc=int(iResolution.y/192.);

    ix=int(fragCoord.x)/sc;
    iy=int(fragCoord.y)/sc;

    int bx=int(iResolution.x)/sc-256;
    int by=int(iResolution.y)/sc-192;

    if((ix<=bx/2)||(ix>=256+bx/2))
    {
        fragColor=border(iy);
        return;
    }
    else
        ix-=bx/2;

    if((iy<=by/2)||(iy>=192+by/2))
    {
        fragColor=border(iy);
        return;
    }
    else
        iy-=by/2;

    int x0=(ix/8)*8;
    int y0=(iy/8)*8;

    int fx=ix-x0;
    int fy=iy-y0;

    NC nc=near(col(x0+4, y0+4));
    if(iMouse.z>0.)
    {        
	    float3 c=col(ix, iy);
	    fragColor = vec4(c,1.0);
    }
    else
    {
        fragColor = vec4(pal(nc.i0),1.0);
        switch(nc.w)
        {
            case 1:if((fx%2+fy%2)==2)fragColor = vec4(pal(nc.i1),1.0);break;
            case 2:if((fx+fy)%2==0)fragColor = vec4(pal(nc.i1),1.0);break;
            case 3:if((fx%2+fy%2)<2)fragColor = vec4(pal(nc.i1),1.0);break;
        }
    }
}

void mainImage( out vec4 O, in vec2 U )
{
    float s,v, t = iTime/14.;
    vec2 R = iResolution.xy, u,
         M = iMouse.xy/R;
    if ( M == vec2(0) ) M.y = 2.*min(fract(t),fract(-t));  // auto-demo

    s = exp2(floor(7.*M.y)), v;                            // scaling
    u = U = floor(U/s);
    R /= s;

    U -= vec2( mod(float(iFrame)/16.,R.x), R.y/2. );       // locate points
    U.x = mod(U.x+25., 50.)-25.;

    v  = smoothstep(1.5, 0., length( U ) ) / (1.5*1.5);    // antialiased dot
    v += float( floor( U ) == vec2( 0, -exp2(7.)/s)  );    // aliased dot

    O = vec4( pow( v, 1./2.2 ) );                          // to sRGB
    O.r += float (mod(floor(u.x),50.)==0.);                // bars
}

