#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0.0);

#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        vec2 p = (-iResolution.xy + 2.0*(fragCoord.xy+vec2(float(m),float(n))/float(AA)))/iResolution.y;
        float w = float(AA*m+n);
        float time = iTime + 0.5*(1.0/24.0)*w/float(AA*AA);
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
        float time = iTime;
#endif

        float magnification = 16000. ;
        float x_center = -0.706370390374415 ;
        float y_center = 0.48035040536878 ;


        magnification = pow(magnification, 1./8.) ;
        float zoom = 1./magnification+(1.-1./magnification)*(0.5+0.5*cos(.07*time));
        zoom = pow( zoom,8.0);

        vec2 c = p ; // fragCoord.xy / iResolution.xy;
        c=c*zoom;
        c.y+= y_center;
        c.x+=1.+x_center;

        const float B = 256.0;
        float l = 0.0;
	    vec2 z  = vec2(0.0);

        int k = 0 ;
        for( int i=0; i<1200; i++ )
        {
            if (i%4 == 1 && k < 55) {
                // z = z*z + c		
                z = vec2( z.x*z.x - z.y*z.y, 2.0*abs(z.x*z.y) ) + c; 
                k += 1;
                }
            else {
                // z = z*z + c		
                z = vec2( z.x*z.x - z.y*z.y, 2.0*z.x*z.y ) + c;
                }

    		if( dot(z,z)>(B*B) ) break;

    		l += 1.0;
        }

    	// ------------------------------------------------------
        // smooth interation count
    	//float sl = l - log(log(length(z))/log(B))/log(2.0);

        // equivalent optimized smooth interation count
    	float sl = l - log2(log2(dot(z,z))) + 4.0; 
    	// ------------------------------------------------------

        float al = smoothstep( -0.1, 0.0, sin(0.5*6.2831*iTime ) );
        l = sl ; // mix( l, sl, al );

        col += 0.5 + 0.5*cos( 3.0 + l*0.15 + vec3(0.0,0.6,1.0));
#if AA>1
    }
    col /= float(AA*AA);
#endif

    fragColor = vec4( col, 1.0 );
}

mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float sphereSDF (vec3 p, float r) {
  return length(p) - r;
}

float displacement(vec3 p, float n) {
  return sin(p.x * n) * cos(p.y * n + n) - sin(p.z + p.y * n);
}

float map (vec3 p) {
  float time = iTime * .7;

  vec3 p1 = p;
  p1.xz *= rotate(time * .3);
  p1.yz *= rotate(time * .2);

  float s1 = sphereSDF(p1, 1.3);
  s1 += sin((p1.x + p1.y * p1.z) * (3.14 * 10.) - time * 2.) * .015 - sin((p1.x - p1.y * p1.z) * (3.14 * 12.) - time) * .01;

  p.z -= time * .6 + sin((p.x * p.z) + time);
  p.xy *= rotate(time * .2);
  vec3 c = vec3(1.4, 1.4, 2.);
  p = mod(p, c) - .5 * c;

  float s2 = sphereSDF(p, .75);
  s2 += sin((p.x + p.y * p.z) * 20. + time * 2.) * .03 + cos(length(p.x - p.y * p.z) * 65. - time) * .01;

  return max(s1 * .6 + displacement(p1, 8. * (sin(time) * .7 + .4)) * .1, -s2 * .3);
}

float trace (vec3 ro, vec3 rd) {
  float e = .0001;
  float d = e * 2.;
  float t = 0.;
  for (int i = 0; i < 60; i++) {
    if (d < e || t > 50.) continue;
    d = map(ro + rd * t);
    t += d;
  }
  return t;
}

vec3 getNormal (vec3 p) {
  float d = map(p);
  vec2 e = vec2(.01, 0.);

  return normalize(d - vec3(
    map(p - e.xyy),
    map(p - e.yxy),
    map(p - e.yyx)));
}

float getLight (vec3 p, vec3 light, vec3 rd) {
  vec3 nor = getNormal(p);
  float lOcclusion = .8;
  float ambient = clamp(.5 + .5 * nor.y, 0., 1.) * .3;
  float diffuse = clamp(dot(nor, light), 0., 1.) * .8;
  vec3 half_way = normalize(-rd + light);
  float specular = pow(clamp(dot(half_way, nor), 0.0, 1.0), 16.) * diffuse * .6;

  return (ambient + diffuse + specular) * lOcclusion;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float time = iTime * .6;

  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;
  vec3 color;

  // camera
  vec3 ro = vec3(0., 0., 5.);
  vec3 rd = normalize(vec3(uv, -3.));

  float t = trace(ro, rd);
  vec3 p = ro + rd * t;

  // texture
  color += sin(length(p + cos(atan(p.x, p.z * p.z) * 5.)) * 50.) * (t * .02);
  color *= cos(dot(p.xy, uv) * 5. - iTime) * .5;

  // lights
  vec3 light1 = normalize(vec3(3., 2., 1.));
  vec3 light2 = normalize(vec3(3., 5., -2.));
  light1.xz *= rotate(time);
  light2.xz *= rotate(-time * .2);
  color += getLight(p, light1, rd) * vec3(.5, .6, .2);
  color += getLight(p, light2, rd) * vec3(.2, .3, .8);
  color += vec3(.6 + p.y * .3, .3 + p.x * .1, .4 + p.z * .5) * .3;

  float fog = 1. / (1. + t * t * .1);
  color *= fog * 3.;

  if (t > 50.) color += .5 / length(uv);

  fragColor = vec4(color, 1.);
}

//https://www.shadertoy.com/view/4ds3zn
