	vec4 rayorig = vec4(20.0,0.0,-0.0,1);

    vec2 rot = vec2(0.3,0.1)*(iMouse.xy / iResolution.xy - 0.5) * 2.0 * PI;
    if (iMouse.x <= 0.0 && iMouse.y <= 0.0)
    	rot = vec2(0,0);
