// Circle Fitting 1 by Martijn Steinrucken aka BigWings - 2019
// countfrolic@gmail.com  Twitter: @The_ArtOfCode
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//
// This is an old doodle I had lying around. It is my attempt to come up with some sort of spline by
// fitting arcs together. You can move the mouse to see the thing in action.
// I figure that a reliable way to connect two arbitrary points which two arbitrary tangents by using
// circle arcs would be very useful for many applications.
// Perhaps the community can build on this, so I figured I'd make it public.

/*

Steps for fitting arcs into a spline

1) Pick first arc the way you want
2) draw line through arc center and end point. 
   New arc center has to be on this line to keep spline derivative smooth
3) Pick end point for next arc, could be anywhere except for the line tangent
   to the current arc end point
4) Mark center between current arc end point and new point
5) Draw line perpendicular through this center.
   New arc center has to be on this line in order for it to go through both points.
6) Find intersection between lines of steps 2 and 5
7) Draw arc at the new center point with radius of the distance between center and new point
8) Go to step 2 to add the next arc.

There are always two splines going through all the points (the arcs, and the anti arcs)
You can choose which one you like best, probably the shortest one
You can't indivdually control spline derivatives at each point. Since we are building this
thing out of cirlces, you might get unintended loops.
You can change the way the spline flows by changing the type of arc you use to connect the
first two points.
*/


#define S(a,b,t) smoothstep(a,b,t)

#define SHOW_HELPERS .7
#define THICKNESS .01
float circle(vec2 uv, vec2 p, float r, float w) {
	p -= uv;
    float d = smoothstep(w, w*.5, abs(r-length(p)));
    return d;
}

float arc(vec2 uv, vec2 p, vec2 a, vec2 b, float r, float w) {
	vec2 c = (a+b)/2.;

    return .1;
}

float point(vec2 uv, vec2 p, float r) {
	return S(r, r*.8, length(uv-p));
}

vec2 R(vec2 a) {
   return vec2(-a.y,a.x);
}

float lineseg(vec2 p, vec2 a, vec2 b,  float w) {

    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0., 1.);	
	float d = length(pa - ba * h);

    return S(w, w*.8, d);
}

float line(vec2 p, vec2 a, vec2 b,  float w) {

    vec2 pa = p - a, ba = b - a;
	float h = dot(pa,ba) / dot(ba,ba);	
	float d = length(pa - ba * h);

    return S(w, w*.8, d);
}



vec2 LineIntersect(vec2 a, vec2 b, vec2 c, vec2 d) {
    float e = (b.x-a.x)*(d.y-c.y)-(b.y-a.y)*(d.x-c.x);
    float r = ((a.y-c.y)*(d.x-c.x)-(a.x-c.x)*(d.y-c.y)) / e;

    vec2 p = a+r*(b-a);

    return p;
}

float Side(vec2 a, vec2 b, vec2 p){
     return sign( ((b.x - a.x)*(p.y - a.y) - (b.y - a.y)*(p.x - a.x)) );
}
vec3 getCircle(vec2 p1, vec2 p2, vec2 p3) {
	float a = p1.x*(p2.y-p3.y)-p1.y*(p2.x-p3.x)+p2.x*p3.y-p3.x*p2.y;
    float p1d = (p1.x*p1.x+p1.y*p1.y);
    float p2d = (p2.x*p2.x+p2.y*p2.y);
    float p3d = (p3.x*p3.x+p3.y*p3.y);

    float b = p1d*(p3.y-p2.y) + p2d*(p1.y-p3.y) + p3d*(p2.y-p1.y);
    float c = p1d*(p2.x-p3.x) + p2d*(p3.x-p1.x) + p3d*(p1.x-p2.x);
    float d = p1d*(p3.x*p2.y-p2.x*p3.y) + p2d*(p1.x*p3.y-p3.x*p1.y)+p3d*(p2.x*p1.y-p1.x*p2.y);

    vec2 p = vec2(-b/(2.*a), -c/(2.*a));
    float r = length(p-p1);
    return vec3(p, r);
}

vec4 fitArc(vec2 uv, vec2 lastCenter, vec2 start, vec2 end,  float side) {
	vec2 mid = (start+end)/2.;
    vec2 perpendicular = (end-start).yx*vec2(-1,1);
    vec2 arcCenter = LineIntersect(lastCenter, start, mid, mid+perpendicular);
    float radius = length(start-arcCenter);

    float dist = length(uv-arcCenter)-radius;
    dist = abs(dist);	// signed or unsigned distance

    float mask = S(.02, 0.01, dist);

    perpendicular = (end-arcCenter).yx*vec2(-1,1);
    vec2 maskPos = arcCenter+(mid-arcCenter)*2.;

    float r = length(end-maskPos);
    float m = S(r, r-.001, length(maskPos-uv)); 

    side = Side(end, start, arcCenter)*side;
    if(side>0.) m = 1.-m;

    mask *= m;

    mask += lineseg(uv, start, end, .005);
    mask += point(uv, arcCenter, .01);
    return vec4(arcCenter, radius, mask);
}

vec3 circle3(vec2 uv, vec2 A, vec2 B, vec2 C, float dist) {
     vec2 d = (A+B)/2.;
    vec2 ab = B-A;

    vec2 e = (B+C)/2.;
    vec2 bc = C-B;


    vec2 f = d + vec2(-ab.y, ab.x)*dist;
    vec2 g = e + vec2(-bc.y, bc.x);

    vec2 p = LineIntersect(f, B, e, g);

    vec2 pe = p-e;
    vec2 df = d-f;

    vec2 fb = B-f;
    vec2 maskPos = LineIntersect(B, B+vec2(-fb.y, fb.x), g, p);
    float r = length(B-maskPos);
    float mask = S(r, r-.001, length(maskPos-uv)); 

    float side = Side(A, B, maskPos);
    if(side<0.) mask = 1.-mask;
   // if(fract(iTime)<.5) mask = 1.-mask;
    vec3 col = vec3(0);
    col += circle(uv, p, length(p-B), THICKNESS)*max(SHOW_HELPERS*.2, mask);

    maskPos = LineIntersect(B, B+vec2(-fb.y, fb.x), d, f);

    r = length(B-maskPos);
    mask = S(r, r-.001, length(maskPos-uv)); 
    if(dist<0.) mask = 1.-mask;
   // if(fract(iTime)<.5) mask = 1.-mask;
    col += circle(uv, f, length(f-B), THICKNESS)*max(SHOW_HELPERS*.2, mask);


    vec3 helpers = vec3(0.);
    helpers += point(uv, p, .015)*vec3(0,1,1);
    helpers += lineseg(uv, A, B, .002)*.25;
    helpers += lineseg(uv, B, C, .002)*.25;
    helpers += lineseg(uv, d, f, .002)*.125;
    helpers += line(uv, e, g, .002)*.125;
    helpers += line(uv, f, B, .002)*vec3(1,1,0)*.1;
    helpers += point(uv, d, .01)*vec3(0,1,0);
    helpers += point(uv, e, .01);
    helpers += point(uv, f, .01)*vec3(1,.5,0);



    //col += line(uv, f, B, .002)*vec3(1,1,0)*.5;

    vec2 n = R(B-f);
    helpers += point(uv, B+n, .03)*vec3(1,0,1);
    helpers += point(uv, B-n, .03)*vec3(1,0,1);

    helpers += lineseg(uv, B+n, B-n, .002)*vec3(1,1,0)*.5;
     col += SHOW_HELPERS*helpers;

    return col;
}



