float ITER = 100.;
float PI = acos(-1.);

#define time iTime


mat2 rot (float a)
{return mat2 (cos(a),sin(a),-sin(a),cos(a));}

// iq modeling function
float capsule (vec3 p, vec3 a, vec3 b, float r)
{
    vec3 pa = p-a; vec3 ba = b-a;
    float h = clamp(dot(pa,ba)/dot(ba,ba), 0.,1.);
    return length(pa - ba*h) - r+smoothstep(0.1,0.6,abs(h-.5))*0.08;
}

float cutesule (vec3 p, vec3 a, vec3 b, float r1, float r2)
{
    vec3 pa = p-a; vec3 ba = b-a;
    float h = clamp(dot(pa,ba)/dot(ba,ba), 0.,1.);
    return length(pa - ba*h) - mix(r1,r2, h);
}

float body (vec3 p)
{
    return capsule (p, vec3(.65,0.,0.), vec3(-.65,0.,0.),0.5);
}

float neck (vec3 p)
{
    p.x += sin(p.y*2.)*0.2; 
    return cutesule (p-vec3(.9,.3,0.), vec3(0.), vec3(0.5,0.7,0.), 0.3, 0.45); 
}

float face (vec3 p)
{
    return cutesule(p-vec3(1.4,1.,0.), vec3(0.), vec3(.6,-0.3,0.), 0.4,0.25);
}

float legs (vec3 p)
{
    p.z = abs(p.z)-0.5;
    vec3 pp = p;

    p.x += sin (p.y*2.)*0.1;
    float l1 = capsule (p-vec3(.7,-1.3,0.), vec3(0.), vec3(0.,1.,-.2), 0.3);

    p = pp;
    p.x -= sin (p.y*2.)*0.2;
    float l2 = capsule (p-vec3(-.7,-1.4,0.), vec3(0.), vec3(0.,1.2,-.2), 0.28);
    return min(l2,l1);
}

float mane (vec3 p)
{
    p.z = abs(p.z)-0.15;
    p -= vec3(.4,.5,0.);
    p.xy *= rot(-PI/4.);
    p.x += sin(p.y*2.)*0.3; 
    float s1 = length(p-vec3(0.,1.,0.))-0.3;
    float s2 = length(p-vec3(0.,.6,0.))-0.25;
    float s3 = length(p-vec3(0.,.3,0.))-0.2;
    float s4 = length(p-vec3(0.,.05,0.))-0.1;
    return min(min(s4,s2),min(s1, s3));
}

float tail (vec3 p)
{
    p += vec3(1.6,.8,0.);
    p.xy *= rot(-PI/4.);
    p.x += sin(p.y*2.)*0.3; 
    float s1 = length(p-vec3(0.,1.,0.))-0.4;
    float s2 = length(p-vec3(0.,.4,0.))-0.35;
    float s3 = length(p-vec3(0.,.1,0.))-0.3;
    float s4 = length(p-vec3(0.,-0.2,0.))-0.2;

    return min(min(s4,s2),min(s1, s3));
}

float horn (vec3 p)
{
    p.xy -= vec2(1.2);
    p.xy *= rot(-PI/4.);
    return cutesule(p, vec3(0.), vec3(0.,1.,0.), 0.25,0.08);
}

int mat_id;
float SDF (vec3 p)
{
    float per = 5.5;
    p.yz *= rot(mix(PI/10., -PI/10., floor(sin(time*2.)+1.)));
    p.x -= time;
    p.y -= -.3+abs(sin(time*2.));
    p.x = mod(p.x-per*0.5, per)-per*0.5;

    float prim1 = min(min(body(p),legs(p)), min(neck(p),face(p)));
    float prim2 = horn(p);
    float prim3 = min(mane(p),tail(p));

    float d =  min(min(prim1, prim2),prim3);

    if (d == prim1) mat_id =1;
    if (d == prim2) mat_id =2;
    if (d == prim3) mat_id =3;

    return d;
}

vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*t+d));}


vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p) - vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

// iq shader: https://www.shadertoy.com/view/lsccR8
float sdfStar5( in vec2 p )
{
    // repeat domain 5x
    const vec2 k1 = vec2(0.809016994375, -0.587785252292); // pi/5
    const vec2 k2 = vec2(-k1.x,k1.y);
    p.x = abs(p.x);
    p -= 2.0*max(dot(k1,p),0.0)*k1;
    p -= 2.0*max(dot(k2,p),0.0)*k2;

    // draw triangle
    const vec2 k3 = vec2(0.951056516295,  0.309016994375); // pi/10
    return dot( vec2(abs(p.x)-0.3,p.y), k3);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float per = 3.;
    vec2 uu = mod(uv*10.,per)-per*0.5;
    uu *= rot(sin(time*2.)*0.5);

    vec3 ro = vec3(0.001,0.001,-6.); vec3 p = ro;
    vec3 rd = normalize(vec3(uv,1.));
    vec3 col = clamp(vec3(smoothstep(0.1,0.11,sdfStar5(uu)))+vec3(0.8,0.6,0.8),0.,1.);

    float shad = 0.; bool hit = false;

    for(float i=0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/ITER;
            break;
        }
        p+=d*rd*0.7;
    }
    if (hit)
    {
        vec3 n = getnorm(p);
        vec3 l = normalize(vec3(1.,2.,-8.));
        vec3 albedo;
        if (mat_id == 1) albedo = palette(dot(n, -rd),vec3(0.5), vec3(0.5), vec3(0.5), vec3(0.,0.37,0.73));
        if (mat_id == 2) albedo = palette(dot(n, -rd),vec3(0.5), vec3(0.5), vec3(3.), vec3(0.,0.37,0.73));
        if (mat_id == 3) albedo = palette(dot(n, -rd),vec3(0.5), vec3(0.5), vec3(0.9), vec3(0.,0.37,0.73));

        col = albedo + vec3(pow(max(0.,dot(reflect(rd,n), l)), 15.));
        col *= vec3(1.-shad);
    }


    fragColor = vec4(pow(col, vec3(0.4545)),1.);
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;
const float BUMP_FACTOR = 0.5;


// 3D hash function
float hash(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// 3D precedural noise
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    // interpolate between hashes of adjacent grid points
    return mix(mix(mix( hash(p+vec3(0,0,0)), 
                        hash(p+vec3(1,0,0)),f.x),
                   mix( hash(p+vec3(0,1,0)), 
                        hash(p+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash(p+vec3(0,0,1)), 
                        hash(p+vec3(1,0,1)),f.x),
                   mix( hash(p+vec3(0,1,1)), 
                        hash(p+vec3(1,1,1)),f.x),f.y),f.z);
}

// 3D noise layered in several octaves
float layeredNoise(in vec3 x) {
    return 0.7*noise(x) + 0.2*noise(x*4.0) + 0.07*noise(x*8.0) + 0.02*noise(x*16.0) + 0.01*noise(x*32.0);
}

// singed distance function of the y=0 plane
float planeSDF(vec3 p) {
    return p.y;
}

// singed distanc function of plane with noise bump map
float bumpPlaneSDF(vec3 p) {
    // get distance to plane as usual
    float d = planeSDF(p);

    vec3 normal;
    float bump = 0.0;

  	// only consider bumps if close to plane
    if(d < BUMP_FACTOR*1.5)
	{	
		normal = vec3(0.0, 1.0, 0.0);
		bump = layeredNoise(p)*BUMP_FACTOR;
	}
    return d - bump;
}

// singed distance function to enire scene
float sceneSDF(vec3 samplePoint) {
    return bumpPlaneSDF(samplePoint);
}

// returns the distanse to the scene along this ray
// by raymarching using the sceneSDF
float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    //raymarching loop
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        // get distance to scene
        float dist = sceneSDF(eye + depth * marchingDirection);
        // exit if close enough
        if (dist < EPSILON) {
			return depth;
        }
        // step closer
        depth += dist;
        // exit if too far
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

// estimate normal by aproximatind the first deriviative of the sceneSDF
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

// generate camera transform
mat3 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat3(s, u, -f);
}

// lambert shading coefficient with raymarched shadows
float lambertShading(vec3 p, vec3 lightDir) {
    vec3 n = estimateNormal(p);

    // lambert shading coefficient
    float brightness = max(0.0, dot(-lightDir, n));

    // trace ray from surface point in direction of the light source
    // offset in direction of the normal to avoid self intersection
    float distToLight = shortestDistanceToSurface(p + n * EPSILON * 100.0, -lightDir, MIN_DIST, MAX_DIST);

    // in shadow
    if (distToLight + 10.0 * EPSILON < MAX_DIST) {
        return 0.0;
    }

    return brightness;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
   	vec3 eye = vec3(2.0*sin(iTime*0.3 - 3.0), 3.0, iTime);
    mat3 viewToWorld = viewMatrix(eye, eye + vec3(2.0*sin(iTime*0.3), -3.0, 8.0), vec3(0.2*sin((iTime-1.0)*0.3), 1.0, 0.0));

    vec3 worldDir = viewToWorld * viewDir;

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist * worldDir;

    vec3 lightDir = normalize(vec3(-1.0, -1.0, -3.0));

    vec3 ambientLight = vec3(0.01, 0.01, 0.005);
    vec3 diffuseColor = vec3(0.8, 0.8, 0.8);

    vec3 color = ambientLight;

    color += diffuseColor*lambertShading(p, lightDir);

    fragColor = vec4(color, 1.0);
}

float map(vec3 p)
{
    p = abs(p);

    vec3 d = vec3(0.5, 0.5, 3.8) - p;
    return max(d.x, max(d.y, d.z));
}

vec3 getNormal(vec3 p)
{
    vec2 e = vec2(0.01, 0);

    float d = map(p);
    vec3 n = d - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx));

    return normalize(n);
}

mat3 camera(vec3 ro, vec3 ta)
{
    vec3 up = vec3(0, 1, 0);
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    vec2 mo = (iMouse.xy / iResolution.xy) + 1.0;
    vec3 ro = vec3(sin(iTime), 0, 9.);
    vec3 ta = vec3(0, 0, 1.);

    vec3 p = ro;

    vec3 ray = camera(ro, ta) * normalize(vec3(uv, 1.5));

    float d = 0., t = 0.;
    int march = 0;
    const int max_march = 128;
    for (int i = 0; i < max_march; i++)
    {
        d = map(p);
        march = i;

        if (d < 0.01) break;

        p += ray * d;
        t += d;
    }

    if (d < 0.01)
    {
        vec3 light = normalize(vec3(1, 1, 2));
        vec3 n = getNormal(p);
        float diff = clamp(dot(light, n), 0.1, 1.0);

        col = vec3(diff);
    }

    vec3 fog = vec3(0.5, 0.8, 1.7) * t * 0.01;
    float fog2 = pow((1.0 / float(max_march)) * float(march), 2.);

    fragColor = vec4(col,1.0) * fog2 + fog.rgbr;
}

// A commented Mandelbrot fractal shader
// Dedicated to the public domain via the Unlicense

//
// Complex Math
//

/** A complex number. */
struct complex {
    float real;
    float imag;
};

/** Add two complex numbers together. */
complex complex_add(complex a, complex b) {
    complex sum;
    sum.real = a.real + b.real;
    sum.imag = a.imag + b.imag;
    return sum;
}

/** Multiply two complex numbers together. */
complex complex_multiply(complex a, complex b) {
    complex product;
    product.real = a.real * b.real - a.imag * b.imag;
    product.imag = a.real * b.imag + a.imag * b.real;
    return product;
}

/** Square a complex number. */
complex complex_square(complex a) {
    return complex_multiply(a, a);
}

//
// Mandelbrot Fractal
//

/**
 * The maximum number of Mandelbrot iterations. If we hit this value,
 * then we know the pixel under test lies in the Mandelbrot set.
 */
const int MAX_ITERATIONS = 100;

// HSV to RGB converter by Sam Hocevar (http://sam.zoy.org/)
// https://github.com/hughsk/glsl-hsv2rgb/blob/master/index.glsl
vec3 hsv2rgb(vec3 c) {
  	vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  	vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  	return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	// Get normalized pixel coordinates
    // Center is (0, 0), bottom-left is (-0.5, -0.5), and top-right is (0.5, 0.5)
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;

    // Correct the aspect ratio
    uv.x *= iResolution.x / iResolution.y;

    // Zoom the "camera" in and out of the scene
    uv.xy += uv.xy * 0.5 * (sin(0.25 * 3.14 * iTime) - 0.5);

    // Move the "camera" around the scene
    uv.x -= 1.0 + 0.5 * cos(0.125 * 3.14 * iTime);
    uv.y += 0.5 * sin(0.5 * 3.14 * iTime);

    // Start iterating at zero (which is both a real and a complex number)
    complex z;
    z.real = 0.0;
    z.imag = 0.0;

    // Run a bunch of iterations to test if this pixel is in the Mandelbrot set
    // The iterations variable is outside the loop so we can go back and retrieve it
    int iterations;
    for (iterations = 0; iterations < MAX_ITERATIONS; ++iterations) {
        // Wrap pixel coordinates into a complex term (treat x as real and y as imaginary)
        // This will let us check if the pixel is in the Mandelbrot set
        // If it is not, we will remember how many iterations it took to figure out it's not
        // This number of iterations plotted against the XY plane makes a fractal! Neat!
        complex c;
        c.real = uv.x;
        c.imag = uv.y;

        // Update the Mandelbrot algorithm (from Wikipedia)
        z = complex_add(complex_square(z), c);

        // If the series diverged
        // We use the number 2 as an "escape" value to test for divergence
        if (z.real * z.real + z.imag * z.imag > 2.0 * 2.0) {
            // Stop iterating
            break;
        }
    }

    // The color intensity of this pixel
    // As mentioned above, the fractal is not the set, per se...
    // To get the fractal, we plot the # of iterations we took at each pixel
    // The more iterations a pixel took, the brigher we make it (more intense)
    float intensity = 0.25 + float(iterations) / float(MAX_ITERATIONS);

    // Color this pixel according to its intensity
    fragColor = vec4(1.5 * intensity * hsv2rgb(vec3(intensity + 1.5 * iTime, 0.75, 0.75)), 1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float PI = 3.1415926;
    // 535897932384626433832795028841971693993751058209749
