#define ITERATIONS 300
#define AA 2

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );   

}

vec2 zoom(vec2 uv, vec2 A, vec2 B){
    return A + (B-A)*uv;
}

vec2 zoomOn(vec2 uv, vec2 p, float z){
 	return (uv/z+p);    
}

vec2 complexSquare(vec2 c){
    return vec2(c.x*c.x - c.y*c.y, 2.*c.x * c.y);
}

float mandelbrot(vec2 uv){

    vec2 c = vec2(0.);

    int i=0;

    for(i ; i<ITERATIONS ; i++){
        c = complexSquare(c) + uv;

        if(dot(c,c) > 4.){
            break;
        }

    }
    return float(i)/float(ITERATIONS);
}

vec3 color(vec2 uv){

    float zoom = pow(2.,iTime);

    //uv = zoom(uv,vec2(-0.5),vec2(-0.4));
    //uv = zoomOn(uv, vec2(-0.99,-.3), 10000.);
    uv = zoomOn(uv, vec2(
        0.3602404434376143632361252444495453084826078079585857504883758147401953460592,
        -0.6413130610648031748603750151793020665794949522823052595561775430644485741727),
        zoom);

    // Time varying pixel color
    float mandel = mandelbrot(uv);

    //mandel = pow(mandel,1./(log2(zoom)/2.));

    mandel = pow(mandel,0.2 + log2(zoom)/18.);

   	return palette(mandel, 
                  vec3(0.5, 0.5, 0.5), 
                  vec3(0.5, 0.5, 0.5), 
                  vec3(1.0, 1.0, 1.0), 
                  vec3(0.30, 0.20, 0.20));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y * vec2(1.,-1.);

    // (x,y), (x+.5,y) , (x+.5,y+.5) , (x, y+.5)

    vec3 col = vec3(0.);

    for(int i=0 ; i<AA ; i++){
        for(int j=0 ; j<AA ; j++){
            col += color(uv+vec2(float(2.*float(i)/float(AA)),
                                 float(2.*float(j)/float(AA))
                                )/iResolution.xy);
        }
    }

    col /= float(AA)*float(AA);

    uv += vec2(2.)/iResolution.xy;


    //vec3 col = color(uv);

    fragColor = vec4(col,0.0);
}

const float PI = 3.1415926;
const float EPS = 0.002;

mat2 rotate(float a)
{
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float sphereSlime(vec3 p)
{
    float t = sin(iTime)*0.4;
    p.xy *= rotate(t*PI*1.4);
    return length(p) - abs(cos(p.x+iTime))*.2 - abs(sin(p.y+iTime))*.2;
}

float bgSphereSlimes(vec3 p)
{
    p = mod(p, 0.75)-0.2;
    p.y -= 0.2;
    p.x -= 0.2;
    float t = cos(iTime)*0.3;
    p.xy *= rotate((t+p.x/p.y)*PI*1.4);
    return length(p) - 0.2 - abs(sin(p.x+iTime*1.2))*0.03 - abs(cos(p.y+iTime*1.2))*0.03;
}

float dist(vec3 p)
{
    return min(sphereSlime(p), bgSphereSlimes(p));
}

vec3 normal(vec3 p)
{
    return normalize(vec3(
        dist(p + vec3(EPS, 0.0, 0.0) - vec3(EPS, 0.0, 0.0)),
        dist(p + vec3(0.0, EPS, 0.0) - vec3(0.0, EPS, 0.0)),
        dist(p + vec3(0.0, 0.0, EPS) - vec3(0.0, 0.0, EPS))
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.x;
    vec3 color = vec3(0.0);

    vec3 ro = vec3(uv, 0.0);
    vec3 rd = vec3(0.0, 0.0, 1.0);

    float d = 0.0;
    for(int i=0; i<1; i++){
        float fi = float(i);
        vec3 p = ro + rd*fi;
        d += dist(p);
        if(d<0.01){
            color = normal(p)*d;
            color =  normalize(color + vec3(0.1, 0.2, 0.1));
            break;
        }
    }
    fragColor = vec4(color,1.0);
}

