void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/min(iResolution.x, iResolution.y);

    vec2 st = uv;

    st -= iTime*vec2(-0.1, 0.1);

    st *= 8.0;

    vec2 i_st = floor(st);
    vec2 f_st = fract(st);

    float m_dist = 8.0;
    float m_angle;
    vec2 mb;
    vec2 mr;

    for (int y= -1; y <= 1; y++) {
        for (int x= -1; x <= 1; x++) {
            // Neighbor place in the grid
            vec2 neighbor = vec2(float(x),float(y));

            vec2 point = random2(i_st + neighbor);

            point = 0.5 + 0.5*sin(0.5*iTime + 6.2831*point);

            vec2 diff = neighbor + point - f_st;

            // Distance to the point
            float dist = dot(diff, diff);

            // Keep the closer distance
            if(dist < m_dist) {
                m_dist = dist;
                m_angle = atan(diff.y, diff.x);
                mb = neighbor;
                mr = diff;
            }
        }
    }



    m_dist = 8.0;
    vec2 nb;
    vec2 nr;

    for (int y= -2; y <= 2; y++) {
        for (int x= -2; x <= 2; x++) {
            // Neighbor place in the grid
            vec2 neighbor = mb + vec2(float(x),float(y));

            vec2 point = random2(i_st + neighbor);

            point = 0.5 + 0.5*sin(0.5*iTime + 6.2831*point);

            vec2 diff = neighbor + point - f_st;

            // Distance to the point
            float dist = dot(0.5*(mr+diff), normalize(diff-mr));

            // Keep the closer distance
            if(dist < m_dist) {
                m_dist = dist;
                nb = point;
                nr = 0.5*(diff-mr);
            }

        }
    }


    float n_dist = m_dist/1.0;

    float c_id = random(i_st + mb);

    float th = 0.35*0.5*(sin(iTime + 101.0*c_id)+1.0);

    float height = step(th, n_dist)*(n_dist-th)*(length(nr)-th);
    vec3 point = vec3(uv.xy, height);
    vec3 normal = normalize(vec3(step(th, n_dist)*nr, 1.0));

    vec3 light = vec3(-0.6, 0.6, 2.5);

    vec3 diff = light-point;
    float l_intensity = 4.75/pow(length(diff), 2.0);


    float t1 = l_intensity*clamp(0.0, 1.0, dot(normalize(diff), normal));

    vec3 bg = vec3(0.83, 0.46, 0.38);
    vec3 c1 = vec3(0.22, 0.36, 0.94);

    vec3 col = 1.5*mix(t1*c1, (1.0-t1)*bg, 1.0-t1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

