float sdArc (in vec2 p, in float a0, in float a1, in float r )
{
    float a = mod(atan(p.y, p.x), radians(360.));

    float ap = a - a0;
    if (ap < 0.)
        ap += radians(360.);
    float a1p = a1 - a0;
    if (a1p < 0.)
        a1p += radians(360.);

    // is a outside [a0, a1]?
    // https://math.stackexchange.com/questions/1044905/simple-angle-between-two-angles-of-circle
    if (ap >= a1p) {
        	// clamp angle to [a0, a1] by finding the closer one of the two
        	// https://gamedev.stackexchange.com/questions/4467/comparing-angles-and-working-out-the-difference
            float da0 = abs(abs(a0 - a) - radians(180.)); 
            float da1 = abs(abs(a1 - a) - radians(180.)); 
            if (da0 > da1)
                a = a0;
            else
                a = a1;
    }

    vec2 q = vec2(r * cos(a), r * sin(a));
    return length(q - p); 
}

float sdArc2 (in vec2 p, in float a0, in float a1, in float r )
{
    float a = mod(atan(p.y, p.x), radians(360.));

    float ap = a - a0;
    if (ap < 0.)
        ap += radians(360.);
    float a1p = a1 - a0;
    if (a1p < 0.)
        a1p += radians(360.);

    // is a outside [a0, a1]?
    // https://math.stackexchange.com/questions/1044905/simple-angle-between-two-angles-of-circle
    if (ap >= a1p) {
        // snap to the closest of the two endpoints
        vec2 q0 = vec2(r * cos(a0), r * sin(a0));
        vec2 q1 = vec2(r * cos(a1), r * sin(a1));
        return min(length(p - q0), length(p - q1));
    }

    return abs(length(p) - r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;

	float a0 = mod(1. * iTime, radians(360.));
    float a1 = mod(3. * iTime, radians(360.));

	float d = sdArc2( p, a0, a1, .5 );

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(d));
	col *= 0.8 + 0.2*cos(140.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(d)) );

	fragColor = vec4(col,1.0);
}

#define PI 3.1415927

#define AA 2

#define clamp01(x) clamp(x, 0., 1.)

struct DistMeta {
    float dist;
    int matId;
};

struct Material {
    vec3 diff_color;
    vec3 spec_color;
    float shininess;
};


float sdPlane(vec3 pos, vec3 norm, float d) {
    return dot(pos, norm) - d;
}

float sdSphere(vec3 pos, float radius) {
    return length(pos) - radius;
}

float sdBox(vec3 pos, vec3 bounds) {
    return length(max(abs(pos) - bounds, 0.));
}


float opUnion(float a, float b) {
    return min(a, b);
}

float opIntersect(float a, float b) {
    return max(a, b);
}

float opSubtract(float a, float b) {
    return max(a, -b);
}

DistMeta opUnion(DistMeta a, DistMeta b) {
    if (a.dist < b.dist)
        return a;
    else
        return b;
}


Material getMaterial(int matId) {
    switch (matId) {
    case 0:
        return Material(vec3(.55), vec3(.8), 35.);
    case 1:
        return Material(vec3(1, 0, 0)*.9, vec3(.8), 20.);
    case 2:
        return Material(vec3(0, 1, 0)*.9, vec3(.8), 20.);
    case 3:
        return Material(vec3(0, 0, 1)*.9, vec3(.8), 20.);
    case 4:
        return Material(vec3(.4, 0, 1), vec3(.8), 15.);
    default:
        return Material(vec3(.25), vec3(0), 0.);
    }
}


DistMeta map(vec3 pos) {
    DistMeta res = DistMeta(sdPlane(pos, vec3(0,1,0), -6.), 0);
    res = opUnion(res, DistMeta(sdSphere(pos - vec3(0,0,0), 5.), 1));
    res = opUnion(res, DistMeta(sdSphere(pos - vec3(10,0,-2), 4.), 2));
    res = opUnion(res, DistMeta(sdSphere(pos - vec3(-10,3,-2), 4.), 3));
    res = opUnion(res, DistMeta(sdBox(pos - vec3(-8,-3,3), vec3(2)) - .4, 4));
    return res;
}


DistMeta castRay(vec3 origin, vec3 dir) {
    float minDist = 1.;
    float maxDist = 100.;

    float t = minDist;
    int matId = -1;
    for (int i=0; i<128; i++) {
        float eps = 1e-4*t;
        DistMeta res = map(origin + dir*t);
        matId = res.matId;
        if (res.dist < eps || t > maxDist) break;
        t += res.dist;
    }
    if (t > maxDist)
        matId = -1;
    return DistMeta(t, matId);
}


vec3 calcNormal(vec3 pos) {
    vec2 e = vec2(1.0, -1.0) * 1e-3;
    return normalize(e.xyy * map(pos + e.xyy).dist +
                     e.yxy * map(pos + e.yxy).dist +
                     e.yyx * map(pos + e.yyx).dist +
                     e.xxx * map(pos + e.xxx).dist);
}


vec3 shade(vec3 origin, vec3 dir, float dist, int matId) {
    vec3 pos = origin + dist*dir;
    vec3 normal = calcNormal(pos);
    Material mat = getMaterial(matId);
    float ambient = .12*(1.0 + .6*normal.y);

    vec3 light1 = normalize(vec3(1, 1, .75));
    float diffuse = clamp01(dot(normal, light1));
    float specular = pow(clamp01(dot(reflect(-light1, normal), -dir)), mat.shininess);

    vec3 light2 = normalize(vec3(1, .5, -2));
    diffuse += .8 * clamp01(dot(normal, light2));
    specular += .8 * pow(clamp01(dot(reflect(-light2, normal), -dir)), mat.shininess);

    float total = diffuse + ambient;
    vec3 col = clamp01(total)*mat.diff_color;
    col += clamp01(specular)*mat.spec_color;
    return clamp01(col);
}


vec3 render(vec3 origin, vec3 dir) {
    vec3 col = vec3(.5, .8, 1.)*(1.+1.5*dir.y);
    DistMeta res = castRay(origin, dir);
    if (res.matId >= 0) {
        col = shade(origin, dir, res.dist, res.matId);
    }
    return col;
}


mat3 lookAt(vec3 cameraPos, vec3 center, vec3 up) {
    vec3 forward = normalize(center - cameraPos);
    vec3 right = normalize(cross(forward, up));
    up = normalize(cross(right, forward));
    return mat3(right, up, forward);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy == vec2(0) ?
        vec2(0) : (2.*iMouse.xy - iResolution.xy) / iResolution.xy;

    float r = 30.;
    float theta = 2.*PI*(iTime/10. + mouse.x*.5 + .25);
    float phi = -mouse.y*PI/2.;
    vec3 cameraPos = vec3(r*cos(theta)*cos(phi), r*sin(phi), r*sin(theta)*cos(phi));
    vec3 center = vec3(0, 0, 0);
    mat3 cameraRot = lookAt(cameraPos, center, vec3(0, 1, 0));

    float fov = 45.0;
    float screenDist = 1. / tan(fov/2.*PI/180.);

    vec3 col = vec3(0);
    for (int i=0; i<AA; i++) {
        for (int j=0; j<AA; j++) {
            vec2 pix = fragCoord + vec2(i, j)/float(AA);
            vec2 uv = (2.*pix - iResolution.xy) / iResolution.y;
            vec3 rayDir = cameraRot * normalize(vec3(uv, screenDist));
            col += render(cameraPos, rayDir);
        }
    }
    col /= float(AA*AA);

    // gamma
    col = pow(col, vec3(1./2.2));

    fragColor = vec4(col, 1.0);
}

