{
    vec2 p = (fragCoord * 2. - iResolution.xy)/min(iResolution.x, iResolution.y);

    vec3 camPos = vec3(0,0,-3.);
    float screenZ = 5.;
    vec3 rayDir = normalize(vec3(p, screenZ));
	vec3 col = vec3(0.);
    vec3 lightPos = vec3(0,0.,5.);
    vec3 lightDir = normalize(vec3(cos(iTime),0.,sin(iTime)));
    //vec3 lightDir = vec3(-1,-1,1);

    float depth = 0.0;
    for(int i=0; i<128;i++){
        vec3 rayPos = camPos + rayDir * depth;
        float dist = distanceFunction(rayPos);

        if(dist < 0.000001){
            float diff = clamp(dot(lightDir, getNormal(rayPos)), 0.1, 1.);
            col = vec3(1.,1.,.6) * diff;
            break;
        }
        depth += dist;

    }

    fragColor = vec4(col,1.);
}

//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
