precision highp float;

mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

float box(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return length(max(d, 0.0));
}

float ifsBox(vec3 p) {
    for(int i=0; i<6; i++) {
        p = abs(p) - 1.0;
        p.xz *= rot(0.7);
        p.xy *= rot(0.8);
    }
    return box(p, vec3(0.0, 0.9, 0.2));
}

float map(vec3 p) {
    float c = 8.0;
	p.z = mod(p.z, c) - c * 0.5;
    return ifsBox(p);
}

vec3 hsv(float h, float s, float v) {
	return ((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 cPos = vec3(2.5*sin(0.4*iTime), 0.5*cos(1.3*iTime), -20.0*iTime);
    vec3 cDir = vec3(0.0, 0.0, -1.0);
    vec3 cUp  = vec3(0.0, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);

    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir);

    // Phantom Mode https://www.shadertoy.com/view/MtScWW by aiekick
    float t = 0.0;
    float acc = 0.0;
    for (int i = 0; i < 199; i++){
        vec3 pos = cPos + ray * t;
        float dist = map(pos);

    	dist = max(abs(dist), 0.02);
        float a = exp(-dist*3.0);
        if (mod(pos.z-60.0*iTime, 50.0) < 8.0) {
            a *= 5.0;
        }
    	acc += a;

    	t += dist*0.5;
        if (t > 100.0) break;
    }

    vec3 col = hsv(fract(0.06*iTime), 0.6, acc * 0.01);
    fragColor = vec4(col, 1.0);
}

// Slow, high-quality 2D hash adapted from nimitz's
// WebGL2 hash collection
// (https://www.shadertoy.com/view/Xt3cDn)
vec2 hash22(uvec2 p)
{
    const uint PRIME32_2 = 2246822519U, PRIME32_3 = 3266489917U;
	const uint PRIME32_4 = 668265263U, PRIME32_5 = 374761393U;
    uint h32 = p.y + PRIME32_5 + p.x*PRIME32_3;
    h32 = PRIME32_4*((h32 << 17) | (h32 >> (32 - 17))); //Initial testing suggests this line could be omitted for extra perf
    h32 = PRIME32_2*(h32^(h32 >> 15));
    h32 = PRIME32_3*(h32^(h32 >> 13));
    h32 = h32^(h32 >> 16);
    uvec2 rz = uvec2(h32, h32*48271U);
    return vec2(rz.xy & uvec2(0x7fffffffU)) / float(0x7fffffff);
}

// Specialty Perlin ease-in/out function
vec2 soften(vec2 t)
{
    vec2 t3 = t * t * t;
    vec2 t4 = t3 * t;
    vec2 t5 = t4 * t;
    return 6.0f * t5 - 
           15.0f * t4 +
           10.0f * t3;
}

// Perlin noise function, taking corner + pixel positions as input
float Perlin(vec2 uv)
{
    // Find corner coordinates
    vec4 lwrUpr = vec4(floor(uv), ceil(uv));
    mat4x2 crnrs = mat4x2(lwrUpr.xw, lwrUpr.zw,
                          lwrUpr.xy, lwrUpr.zy);

    // Generate gradients at each corner
    mat4x2 dirs = mat4x2(hash22(uvec2(floatBitsToUint(crnrs[0]))),
                         hash22(uvec2(floatBitsToUint(crnrs[1]))),
                         hash22(uvec2(floatBitsToUint(crnrs[2]))),
                         hash22(uvec2(floatBitsToUint(crnrs[3]))));

    // Shift gradients into [-1...0...1]
    dirs *= 2.0f;
    dirs -= mat4x2(vec2(1.0f), vec2(1.0f), 
                   vec2(1.0f), vec2(1.0f));

    // Normalize
    dirs[0] = normalize(dirs[0]);
    dirs[1] = normalize(dirs[1]);
    dirs[2] = normalize(dirs[2]);
    dirs[3] = normalize(dirs[3]);

    // Find per-cell pixel offset
    vec2 offs = mod(uv, 1.0f);

    // Compute gradient weights for each corner; take each offset relative
    // to corners on the square in-line
    vec4 values = vec4(dot(dirs[0], (offs - vec2(0.0f, 1.0f))),
                       dot(dirs[1], (offs - vec2(1.0f))),
                       dot(dirs[2], (offs - vec2(0.0f))),
                       dot(dirs[3], (offs - vec2(1.0f, 0.0f))));

    // Return smoothly interpolated values
    vec2 softXY = offs;//soften(offs);
    return mix(mix(values.z, 
                   values.w, softXY.x),
               mix(values.x, 
                   values.y, softXY.x),
               softXY.y);
}

// Compute fractal noise for a given pixel position
#define SCALING_FBM
//#define EXTRA_TRIG_FX
float fbm(vec2 uv)
{
    const uint depth = 6u;
    const vec2 dFreq = vec2(1.01f, 1.02f);
    const float dAmpl = 1.2f;
    float srct = iTime + 100.0f;
    float t = min((srct * (0.075 / log(srct)) + sin(srct * 0.01)), 400.0);
    vec2 sfreq = vec2(1.01f + (sin(t) * 2.0)); // Starting noise scaling frequency
    vec2 rfreq = vec2(1.1f); // Starting noise rotational frequency
    float ampl = 0.25f; // Starting noise intensity
    float f = 0.0f;
    for (uint i = 0u; i < depth; i += 1u)
    {
        f += ampl * Perlin(uv);//abs(Perlin(uv));
        float fi = float(i);
        #ifdef EXTRA_TRIG_FX
        	uv += vec2(cos(fi), 
        	           sin(fi)) * (iTime * 0.25f) * rfreq;
        #endif
        //uv += vec2(t);
        #ifdef SCALING_FBM
        	uv += pow(t, dot(sfreq, rfreq) * 0.25);
            sfreq *= dFreq + vec2(0.1f * float(i));
        #endif
        rfreq *= dFreq + vec2(0.1f * float(i));
        ampl *= dAmpl;
    }
    return f;
}

#define NOISE_MIRR
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to [1.0 / [cellSize]])
    const float cellSize = 0.0825f;
    #ifdef NOISE_MIRR
    	float hAspect = (iResolution.x / iResolution.y) * 0.5f;
    	vec2 uv = abs((fragCoord / iResolution.y) - vec2(hAspect, 0.5f)) / cellSize;
    #else
    	vec2 uv = (fragCoord / iResolution.y) / cellSize;
    #endif
    //uv += (vec2(0.1) + texture(iChannel1, uv).rr) * 0.1;
    // Generate per-channel fbm()
    // fbm(p + fbm(p + fbm(p)))
    // FBM recursion function from iq through the Book of Shaders:
    // http://www.iquilezles.org/www/articles/warp/warp.htm
    // https://thebookofshaders.com/13/
    float fbm0 = fbm(uv + vec2(
        			 fbm(uv + vec2(
                    	 fbm(uv)))));
    vec2 uu = uv + vec2(sin(iTime), fbm0);
    float fbm1 = fbm(uu + vec2(
        			 fbm(uu + vec2(
                    	 fbm(uv)))));
    vec2 vv = uv + vec2(cos(iTime) * fbm1, fbm1 + fbm0);
    float fbm2 = fbm(vv + vec2(
        			 fbm(vv + vec2(
                    	 fbm(vec2(fbm0, fbm1))))));
	vec3 rgb = vec3(fbm0, fbm1, fbm2);

    // Output to screen
    fragColor = vec4(rgb, 1.0);
}

float sphere(vec3 ray, vec3 dir, vec3 center, float radius)
{
	vec3 rc = ray-center;
	float c = dot(rc, rc) - (radius*radius);
	float b = dot(dir, rc);
	float d = b*b - c;
	float t = -b - sqrt(abs(d));
	float st = step(0.0, min(t,d));
	return mix(-1.0, t, st);
}


vec3 colorplaneta( vec2 fragCoord )
{
    vec2 uv;
    //Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;

    //Equalize the aspect ratio
    ///uv.y /= iResolution.x/iResolution.y;
    uv=fragCoord;

    //Zoom out
    uv *= 5.0;

    //Add distortion
    for(float i = 1.0; i < 4.0; i+=1.0){ 
    	uv.x += sin(iTime + uv.y * i);
    	uv.y += cos(iTime + uv.x * i);
    }

    //Time varying pixel colour
    vec3 col = 0.5 + 0.5*cos(4.0*iTime + uv.xyx + vec3(0,2,4));

    //Fragment colour
    return col;
}


vec3 background(float t, vec3 rd)
{
	vec3 light = normalize(vec3(sin(t), 0.6, cos(t)));
	float sun = max(0.0, dot(rd, light));
	float sky = max(0.0, dot(rd, vec3(0.0, 1.0, 0.0)));
	float ground = max(0.0, -dot(rd, vec3(0.0, 1.0, 0.0)));


    vec3 tmp3;
    vec2 tmp2;
    tmp2=vec2(rd.x*1.0, rd.y*1.0);
    tmp3=colorplaneta(tmp2);


	return 
		(pow(sun, 256.0)+0.2*pow(sun, 2.0))*vec3(2.0, 1.6, 1.0) +
		pow(ground, 0.5)*tmp3 +
		pow(sky, 1.0)*vec3(0.5, 0.6, 0.7);
}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (-1.0 + 2.0*fragCoord.xy / iResolution.xy) * 
		vec2(iResolution.x/iResolution.y, 1.0);


	vec3 ro = vec3(0.0, 0.0, -3.0);
	vec3 rd = normalize(vec3(uv, 1.0));
	vec3 p = vec3(0.0, 0.5, 0.0);
	float t = sphere(ro, rd, p, 1.25);
	vec3 nml = normalize(p - (ro+rd*t));


	vec3 bgCol = background(iTime, rd);
	rd = reflect(rd, nml);

	vec3 col = background(iTime, rd) * vec3(0.9, 0.8, 1.0);





	fragColor = vec4( mix(bgCol, col, step(0.0, t)), 1.0 );
}

#define scale 4.
#define pi 3.14159265359

float rnd(vec2 p)
{
 return fract(sin(dot(p, vec2(12.9898, 78.233)))*43562.34532);   
}

mat2 rot(float a)
{
 return mat2(cos(a), -sin(a), sin(a), cos(a));   
}


float circle(vec2 p, float r)
{
    float c;
    float ss = 0.008;
    c = length(p);
    c = smoothstep(r-ss, r+ss, c);  
	return c;    
}

float circles(vec2 p, float r)
{

 float c1 = (1.0-circle(p+vec2(0.5, 0.0), r))*(step(-0.5, p.x));   
float c2 = (1.0-circle(p-vec2(0.5, 0.0), r))*(step(p.x, 0.5)); 

    return c1+c2;
}

//rotation coord system that rotates based on an id that is randomized

vec3 rotCoord(vec2 p)

{
    float id = rnd(floor(p*scale));
    vec2 m = floor(p*scale);
    float index = mod(m.x+m.y, 2.0);


    p = fract(p*scale)-0.5;

    if(index>0.0)
    {
        p = p*rot(pi/2.);
    }

    /*

 //if index of row is even index = 0.0, else index = 1.0
    float index = mod(m.y, 2.0);

    //flip all even rows 90 degrees
    p = fract(p*scale)-0.5;

    if(index>0.0)
    {
        p = p*rot(pi/2.);
    }

*/



 return vec3(p, index);   
}

float vig (vec2 st)
{
    st *=  1.0 - st.yx;
    float vig = st.x*st.y*15.;
    vig = pow(vig, 0.2); 
    return vig;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv;
    float v = vig(st);
    st+=iTime/8.;

    uv+=iTime/8.;


    //uv = uv*2.-1.;
    uv.x*=iResolution.x/iResolution.y;

    vec3 stuff = rotCoord(uv);

    uv = stuff.xy;
    float colID = stuff.z;


    float c = 1.0-circles(uv, 0.5);
    float outline= circles(uv, 0.5)-circles(uv, 0.485);

    // Time varying pixel color

	//col = vec3(1.0-circles(uv, 0.5));
    float tx0 = vec3(texture(iChannel0, uv)).x;
    float tx1 = vec3(texture(iChannel1, uv)).x;
    float tx2 = vec3(texture(iChannel2, st)).x;

    vec3 col = vec3(0.4, 0., 0.)/1.5*tx2;

    col = mix(col, vec3(.8, 0., 0.)*tx0, c*step(0.1, colID));
    col = mix(col, vec3(.8, 0., 0.)*tx0, (1.0-c)*step(colID, 0.1));
    // Output to screen
    col = mix(col, vec3(195., 155., 25.)/155.-tx1+(sin(iTime*2.+uv.x*4.+uv.y)+1.)/4., outline);


    fragColor = vec4(col*v,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = fract(uv.y*3.);
    //uv.y -= .5;

    float T = iTime * 2.5;
    //T += uv.y*3.14*3.;
    //T = abs(fract(iTime)-.5);


    vec3 col = vec3(0);


    float t = uv.x;// + iTime * .2;
    float waves = 5.;
    float s = cos(t * 3.14*2. * waves);
    float h = mix(.0, .05, cos(T+3.14)*.5+.5);

    float s1 = s*h+h;
    float s2 = s*h-h;

    // triangle thick part
    float th = mix(.0, .5, sin(uv.x));

    //float R = (1.-step(s1+th, uv.y));
    //float G = (   step(s2-th, uv.y-1.));
    //float B = (   step(.5, uv.y));

    float a = 0.02; // aliasing
    float R = smoothstep(s1+th+a, s1+th, uv.y);
    float G = smoothstep(s2-th-a, s2-th, uv.y-1.);
    //float B1 = step(s*.1+.5 + uv.x, 1.-uv.y);
    //float B2 = step(s1 + .5, uv.y);

    //col = vec3(R);
    col = vec3(R+G);
    //col += step(s, uv.y);


    // Output to screen
    fragColor = vec4(col,1.0);
}

