//Layered voronoi
#define VORONOI_NOISE_POW 5.0
#define VORONOI_BRIGHTNESS_ADD 0.05
#define SIZE_MUL 1.5
#define ALPHA_MUL 0.9
#define LAYERS 4

//Colors
#define WATER_COLOR vec4(0.0, 0.8, 0.8, 1.0)

//Image size and displacement
#define UV_MUL 3.0
#define UV_DISPLACEMENT_STRENGTH 0.15
#define UV_DISPLACEMENT_SIZE 5.0

//Animation
#define WATER_SPEED 0.5
#define ANIMATION_SPEED 2.0

float hash1_2(vec2 x)
{
 	return fract(sin(dot(x, vec2(52.127, 61.2871))) * 521.582);   
}

vec2 hash2_2(vec2 x)
{
    return fract(sin(x * mat2x2(20.52, 24.1994, 70.291, 80.171)) * 492.194);
}	

//Simple interpolated noise
vec2 noise2_2(vec2 uv)
{
    vec2 f = smoothstep(0.0, 1.0, fract(uv));

 	vec2 uv00 = floor(uv);
    vec2 uv01 = uv00 + vec2(0,1);
    vec2 uv10 = uv00 + vec2(1,0);
    vec2 uv11 = uv00 + 1.0;
    vec2 v00 = hash2_2(uv00);
    vec2 v01 = hash2_2(uv01);
    vec2 v10 = hash2_2(uv10);
    vec2 v11 = hash2_2(uv11);

    vec2 v0 = mix(v00, v01, f.y);
    vec2 v1 = mix(v10, v11, f.y);
    vec2 v = mix(v0, v1, f.x);

    return v;
}

vec2 rotate(vec2 point, float deg)
{
 	float s = sin(deg);
    float c = cos(deg);
    return mat2x2(s, c, -c, s) * point;
}

//Cell center from point on the grid
vec2 voronoiPointFromRoot(vec2 root, float deg)
{
  	vec2 point = hash2_2(root) - 0.5;
    float s = sin(deg);
    float c = cos(deg);
    point = mat2x2(s, c, -c, s) * point;
    point += root + 0.5;
    return point;
}

float degFromRootUV(vec2 uv)
{
 	return iTime * ANIMATION_SPEED * (hash1_2(uv) + 0.2);   
}

//x - voronoi coordinates (grid step = 1)
float voronoi(vec2 uv)
{
    vec2 rootUV = floor(uv);
    float deg = degFromRootUV(rootUV);
    vec2 pointUV = voronoiPointFromRoot(rootUV, deg);

    vec2 tempRootUV;	//Used in loop only
    vec2 tempPointUV;	//Used in loop only
    vec2 closestPointUV = pointUV;
    float minDist = 2.0;
    float dist = 2.0;
    for (float x = -1.0; x <= 1.0; x+=1.0)
    {
     	for (float y = -1.0; y <= 1.0; y+=1.0)   
        {
         	tempRootUV = rootUV + vec2(x, y);
            deg = (iTime * hash1_2(tempRootUV) * ANIMATION_SPEED);
            tempPointUV = voronoiPointFromRoot(tempRootUV, deg);

            dist = distance(uv, tempPointUV);
            if(dist < minDist)
            {
             	closestPointUV = tempPointUV;
               	minDist = dist;
            }
        }
    }

    return minDist;
}

//Layered voronoi noise
float fractVoronoi(vec2 uv, float sizeMul, float alphaMul, int layers)
{
 	float noise = 0.0;
    float size = 1.0;
    float alpha = 1.0;
    vec2 uvOffset; //Used in loop only
    for(int i = 0; i < layers; i++)
    {
        uvOffset = hash2_2(vec2(size, alpha)) * iTime * WATER_SPEED;
        noise += pow(voronoi((uv + uvOffset) * size) * alpha + VORONOI_BRIGHTNESS_ADD, VORONOI_NOISE_POW);
        size *= sizeMul;
        alpha *= alphaMul;
    }

    noise *= (1.0 - alphaMul)/(1.0 - pow(alphaMul, float(layers)));
    return noise;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.x; 
    uv *= UV_MUL;
    vec2 noise2D = noise2_2(uv * UV_DISPLACEMENT_SIZE) * UV_DISPLACEMENT_STRENGTH;
    float fractVoro = fractVoronoi(uv + noise2D, SIZE_MUL, ALPHA_MUL, LAYERS);
    float res = smoothstep(-0.2, 0.3, fractVoro);
    fragColor = vec4(res) * WATER_COLOR + fractVoro;
}

#define PI 3.141592653589
#define STEPS 32
#define OCTAVES 5


mat3 rotateY(float a) {
	return mat3(
		vec3(cos(a), 0., -sin(a)),
		vec3(0, 1., 0.),
		vec3(sin(a), 0, cos(a))
		);
}

mat3 rotateX(float a) {
	return mat3(
		vec3(1., 0., 0.),
		vec3(0., cos(a), -sin(a)),
		vec3(0., sin(a), cos(a))
		);	
}

float random(vec2 st) {
	return fract(sin(dot(st, vec2(15.13123, 45.56251))) * 45666.12) *2.-1.;
}

float noise(vec2 st) {
	vec2 i = floor(st);
	vec2 f = fract(st);
	// Create grid to spread out our noise
	float a = random(i);
	float b = random(i + vec2(1.0, 0.0));
	float c = random(i + vec2(0.0, 1.0));
	float d = random(i + vec2(1.0, 1.0));
	// Create our hermite cubic interpolation
	vec2 h = f * f * (3.0 - 2.0 * f);
	// Do our bilinear interpolation:
	return 	mix(a, b, h.x) + 
			(c - a) * h.y * (1.0 - h.x) +
			(d - b) * h.x * h.y;
}

float fbm(vec3 p){
	float amp = 2.0;
	float fbm = 0.;
	float scale = 2.0;
	for(int i = 0; i < OCTAVES; i++){
		fbm += noise(p.xy) * amp;
		p *= scale;
		amp *= 0.5;
	}
	return fbm;
}

float sphere(vec3 p, float r) {
	return length(p) - 0.5 * r;
}

float fTorus(vec3 p, float smallRadius, float largeRadius) {
	p *= rotateX(-PI*0.35);
	return length(vec2(length(p.xz) - largeRadius, p.y)) - smallRadius;
}

float scene(vec3 p) {
	p *= rotateY(PI*iTime);
	float noisySphere = max(sphere(p + fbm(p)*0.5, 12.), sphere(p, 12.));
	float torus = fTorus(p, 6.9, 9.);
	return max(torus, noisySphere);
}

vec3 getNormals(vec3 p) {
	vec3 delta = vec3(0.01, 0., 0.);
	float x = scene(p + delta.xyy) - scene(p - delta.xyy);
	float y = scene(p + delta.yxy) - scene(p - delta.yxy);
	float z = scene(p + delta.yyx) - scene(p - delta.yyx);
	return normalize(vec3(x, y, z));
}

float rayMarching(vec3 pos, vec3 dir, out vec3 p) {
	float currentDist = 0.;
	const float min = 0.01;
	const float max = 1000.0;
	for(int i = 0; i < STEPS; ++i) {
		p = pos + (dir * currentDist);
		float distance2Obj = scene(p);
		if(distance2Obj < min) {
			break;
		}
		currentDist += distance2Obj;
		if(currentDist > max) {
			currentDist = 0.;
			break;
		}
	}
	return currentDist;
}


// Standard Blinn lighting model.
// This model computes the diffuse and specular components of the final surface color.
vec3 calculateLighting(vec3 pointOnSurface, vec3 surfaceNormal, vec3 lightPosition, vec3 cameraPosition, vec3 col)
{
	vec3 fromPointToLight = normalize(lightPosition - pointOnSurface); // Get normal
	float diffuseStrength = clamp( dot( surfaceNormal, fromPointToLight ), 0.0, 1.0 );
	vec3 diffuseColor = diffuseStrength * col;
	vec3 reflectedLightVector = normalize( reflect( -fromPointToLight, surfaceNormal ) );
	vec3 fromPointToCamera = normalize( cameraPosition - pointOnSurface );
	float specularStrength = pow( clamp( dot(reflectedLightVector, fromPointToCamera), 0.0, 1.0), 10.0);
	// Ensure that there is no specular lighting when there is no diffuse lighting.
	specularStrength = min( diffuseStrength, specularStrength );
	vec3 specularColor = specularStrength * vec3( 1.0 );
	vec3 finalColor = diffuseColor + specularColor;
	return finalColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 st = gl_FragCoord.xy / iResolution.xy * 2. - 1.;
	st.x *= iResolution.x / iResolution.y;

	// Define camera properties
	vec3 cameraPos = vec3(0., 0., -10.0);
	vec3 cameraDir = vec3(st.x, st.y, 1.);
	vec3 color = vec3(length(st))*0.05;
	vec3 p;
	float r = rayMarching(cameraPos, cameraDir, p);

	if(r > 0.0) {
		vec3 normals = getNormals(p);
		vec3 lightPosA = vec3(-12., 0., 0.);
		vec3 lightPosB = vec3(9., 0., -10.);
		float fbmCol = abs(fbm(p*4.0));
		vec3 colA = mix(vec3(cos(iTime)*0.5+0.5, .5, 0.25), vec3(0.1, 0.5, 0.9), fbmCol);
		vec3 colB = mix(colA, vec3(0.9, 0.9, 0.9), fbmCol);
		color = calculateLighting(p, normals, lightPosA, cameraPos, colA);
		color += calculateLighting(p, normals, lightPosB, cameraPos, colB);	
	}

	fragColor = vec4(color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates in [-1,1]^2
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ndc = 2.0*uv-vec2(1.0);


    float aspectRatio = iResolution.x/iResolution.y;
    vec2 res = vec2(aspectRatio,1.0); // don't stretch out the screen

    ndc *= res; // rescale ndc to account for the aspect ratio

    // first line point (note that coordinates are originally given in [-1,1]^2 and then
    // remapped to account for stretching)
    vec2 v0 = res * vec2(-cos(iTime),-sin(iTime));
    //vec2 v0 = res*vec2(-1,-1);

    // second line point - reflected around (0,0)
    vec2 v1 = -v0;
    // normalized direction from v0 to v1
    vec2 dir = normalize(v1-v0);


    // pixel normalized coordinates (aspect ratio preserved)
    vec2 p = ndc;
    // some point on the line
    vec2 c = v0;
    // unit length normal of the line
    vec2 n = vec2(-dir.y,dir.x);
    // signed distance from p to the line
    float dist = dot(p-v0, n);

    // thickness of the line
    float thickness = 0.01 + 0.04*abs(sin(2.0*iTime));
    //vec3 col = vec3(abs(dist)<thickness);
    vec3 col = vec3(1.0-smoothstep(abs(dist), 0.0, thickness));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define scale 5.
#define pi 3.14159265359

float rnd(vec2 p)
{
 return fract(sin(dot(p, vec2(12.9898, 78.233)))*43562.34532);   
}

mat2 rot(float a)
{
 return mat2(cos(a), -sin(a), sin(a), cos(a));   
}


float circle(vec2 p, float r)
{
    float c;
    float ss = 0.009;
    c = length(p);
    c = smoothstep(r-ss, r+ss, c);  
	return c;    
}

float circles(vec2 p, float r)
{

 float c1 = (1.0-circle(p+vec2(0.5, 0.0), r))*(step(-0.47, p.x));   
float c2 = (1.0-circle(p-vec2(0.5, 0.0), r))*(step(p.x, 0.47)); 

    return c1+c2;
}

//rotation coord system that rotates based on an id that is randomized

vec2 rotCoord(vec2 p)

{
    float id = rnd(floor(p*scale));
    vec2 m = floor(p*scale);
    float index = mod(m.x+m.y, 2.0);


    p = fract(p*scale)-0.5;

    if(index>0.0)
    {
        p = p*rot(pi/2.);
    }

    if(id*scale>0.25*scale)
    {
        p = p*rot(pi/2.);
    }


 return p;   
}

float vig(vec2 st)
{
 st *=  1.0 - st.yx;
    float vig = st.x*st.y*15.;
    vig = pow(vig, 0.19);
    return vig;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 st = uv;
    float v = vig(uv);
    uv+=iTime/8.;
    st+=iTime/8.;
    //uv = uv*2.-1.;
    uv.x*=iResolution.x/iResolution.y;
    uv = rotCoord(uv);
	float tx0 = vec3(texture(iChannel0, st)).x*1.9;

    	float tx1 = vec3(texture(iChannel1, st)).x+0.2;


    // Time varying pixel color
    vec3 col = vec3(212., 123., 15.)/155.*tx0/1.;
	col = mix(col,vec3(.3, 0., 0.)*tx1, 1.0-circles(uv, 0.48));

    ///////////////////////
    col = vec3(.5, 0., 0.)*tx1;//;
	col = mix(col,vec3(212., 123., 15.)/155.*tx0/1.8, 1.0-circles(uv, 0.48));

    // Output to screen
    fragColor = vec4(col*v,1.0);
}

