// Color computation inspired by: https://www.shadertoy.com/view/Ms2SD1

// Computes normal analytical for improved precision

#define PI  3.141592654
#define TAU (2.0*PI)

const float gravity = 1.0;
const float waterTension = 0.01;

const vec3 skyCol1 = vec3(0.6, 0.35, 0.3);
const vec3 skyCol2 = vec3(1.0, 0.3, 0.3);
const vec3 sunCol1 =  vec3(1.0,0.5,0.4);
const vec3 sunCol2 =  vec3(1.0,0.8,0.7);
const vec3 seaCol1 = vec3(0.1,0.2,0.2);
const vec3 seaCol2 = vec3(0.8,0.9,0.6);

vec2 wave(in float t, in float a, in float w, in float p) {
  float x = t;
  float y = a*sin(t*w + p);
  return vec2(x, y);
}

vec2 dwave(in float t, in float a, in float w, in float p) {
  float dx = 1.0;
  float dy = a*w*cos(t*w + p);
  return vec2(dx, dy);
}

vec2 gravityWave(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh(k*h));
  return wave(t, a ,k, w*iTime);
}

vec2 capillaryWave(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh(k*h));
  return wave(t, a, k, w*iTime);
}

vec2 gravityWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt(gravity*k*tanh(k*h));
  return dwave(t, a, k, w*iTime);
}

vec2 capillaryWaveD(in float t, in float a, in float k, in float h) {
  float w = sqrt((gravity*k + waterTension*k*k*k)*tanh(k*h));
  return dwave(t, a, k, w*iTime);
}

mat2 mrot(in float a) {
  float c = cos(a);
  float s = sin(a);
  return mat2(c, s, -s, c);
}

mat2 mtrans(in mat2 m) {
  return mat2(m[0][0], m[1][0], m[0][1], m[1][1]);
}

vec4 sea(in vec2 p, in float ia) {
  float y = 0.0;
  vec3 d = vec3(0.0);

  float k = 1.0;
  float kk = 1.3;
  float a = ia*0.19;
  float aa = 1.0/(kk*kk);

  float h = 10.0;
  p *= 0.5;

  float angle = 0.0;

  for (int i = 0; i < 3; ++i) {
    mat2 fr = mrot(angle);
    mat2 rr = mtrans(fr);
    vec2 pp = fr*p;
    y += gravityWave(pp.y + float(i), a, k, h).y;
    vec2 dw = gravityWaveD(pp.y + float(i), a, k, h);

    vec2 d2 = vec2(0.0, dw.x);
    vec2 rd2 = rr*d2;

    d += vec3(rd2.x, dw.y, rd2.y);

    angle += float(i);
    k *= kk;
    a *= aa;
  }

  for (int i = 3; i < 7; ++i) {
    mat2 fr = mrot(angle);
    mat2 rr = mtrans(fr);
    vec2 pp = fr*p;
    y += capillaryWave(pp.y + float(i), a, k, h).y;
    vec2 dw = capillaryWaveD(pp.y + float(i), a, k, h);

    vec2 d2 = vec2(0.0, dw.x);
    vec2 rd2 = rr*d2;

    d += vec3(rd2.x, dw.y, rd2.y);

    angle += float(i);
    k *= kk;
    a *= aa;
  }

  vec3 t = normalize(d);
  vec3 nxz = normalize(vec3(t.z, 0.0, -t.x));
  vec3 nor = cross(t, nxz);

  return vec4(y, nor);
}

vec3 sunDirection() {
  vec3 dir = normalize(vec3(0, 0.13, 1));
  return dir;
}

vec3 skyColor(vec3 rd) {
  vec3 sunDir = sunDirection();

  float sunDot = max(dot(rd, sunDir), 0.0);

  vec3 final = vec3(0.0);

  final += mix(skyCol1, skyCol2, rd.y);

  final += 0.5*sunCol1*pow(sunDot, 30.0);

  final += 4.0*sunCol2*pow(sunDot, 300.0);

  return final;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q=fragCoord.xy/iResolution.xy; 
  vec2 p = -1.0 + 2.0*q;
  p.x *= iResolution.x/iResolution.y;

  vec3 ro = vec3(0.0, 10.0, 0.0);
  vec3 ww = normalize(vec3(0.0, -0.1, 1.0));
  vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize(p.x*uu + p.y*vv + 2.5*ww);

  vec3 col = vec3(0.0);

  float dsea = (0.0 - ro.y)/rd.y;

  vec3 sunDir = sunDirection();

  vec3 sky = skyColor(rd);

  if (dsea > 0.0) {
    vec3 p = ro + dsea*rd;
    vec4 s = sea(p.xz, 1.0);
    float h = s.x;    
    vec3 nor = s.yzw;
    nor = mix(nor, vec3(0.0, 1.0, 0.0), smoothstep(0.0, 200.0, dsea));

    float fre = clamp(1.0 - dot(-nor,rd), 0.0, 1.0);
    fre = pow(fre, 3.0);
    float dif = mix(0.25, 1.0, max(dot(nor,sunDir), 0.0));

    vec3 refl = skyColor(reflect(rd, nor));
    vec3 refr = seaCol1 + dif*sunCol1*seaCol2*0.1; 

    col = mix(refr, 0.9*refl, fre);

    float atten = max(1.0 - dot(dsea,dsea) * 0.001, 0.0);
    col += seaCol2*(p.y - h) * 2.0 * atten;

    col = mix(col, sky, 1.0 - exp(-0.01*dsea));

  } else {
    col = sky;
  }

  fragColor = vec4(col,1.0);
}

float SinLerp(float min, float max, float t) {
 	return mix(min, max, sin(t) * 0.5 + 0.5);   
}

float Sin01(float t) {
 	return SinLerp(0., 1., t);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(uv,0);

    float t = iTime * .3;

    // polar
    vec2 st = vec2(atan(uv.x, uv.y), length(uv));
    st.x = st.x/6.28+0.5;
	col = vec3(st.x);


    //st.x += st.y * SinLerp(0., 2., iTime);
    uv = st;



    // zig zag
    float p = 1.; // petals
    float x = uv.x * p;// + t * 0.5;
    float y = uv.y * SinLerp(.2, 1., t);

    // RINGS
    float r = y + sin( uv.y * 7. * SinLerp(1., 2., t) - t*3.5);
    // FLOWER
    float z = min(fract(x), fract(1.-x)); 


    col = vec3(z);
    col = vec3(r + z);
    float s = 20.;//SinLerp(4., 15., t); // split of the petal
    //col = vec3(r + fract( z * s) );
    col = vec3(fract(r + fract( z * s) ));

    // Output to screen
    fragColor = vec4(col, 1.);
}

#define repeat(p, span) mod(p, span) - (0.5 * span)

const float pi = acos(-1.);
const float pi2= pi * 2.;

mat2 rot(float a)
{
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

mat3 camera(vec3 ro, vec3 ta)
{
    vec3 up = vec3(0, 1, 0);
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

float sdBox(vec3 p, vec3 b)
{
    return length(max(abs(p) - b, 0.0));
}

float map(vec3 p)
{
    vec3 z = p;

    float scale = 2.0;
    float sum = scale;
    float d = 1e5;

    float time = abs(sin(iTime * 0.5) * 4.0);
    float s = min(floor(time), 3.0) + 1.0;

    for (float i = 0.; i < s; i++)
    {
        float td = sdBox(z, vec3(0.5)) / sum;

        z = abs(z) - vec3(0, 2.0, 0);
        d = min(td, d);

        z.xy *= rot(pi * 0.25);
        z *= scale;
        sum *= scale;
    }

    return d;
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0001, 0.0);
    float d = map(p);
    vec3 n = d - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx));
    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

	vec3 col = vec3(0);

    float dist = 5.0;
    vec3 ro = vec3(cos(iTime) * dist, 1, sin(iTime) * dist);
    vec3 ta = vec3(0, 0, 0);

    vec3 ray = camera(ro, ta) * normalize(vec3(uv, 1.8));

    vec3 p = ro;
    float d = 0., t = 0.;

    for (int i = 0; i < 64; i++)
    {
        d = map(p);
        if (d < 0.01) break;
        p += ray * d;
    }

    if (d < 0.01)
    {
        vec3 n = normal(p) * 0.5 + 0.5;
        col = n;
    }
    else
    {
        col = vec3(0.5, 0.8, 1.5) * abs(uv.y * 0.5 - 1.0) ;
    }


    fragColor = vec4(col,1.0);
}

float circle(in vec2 _st, in float _radius){
    vec2 dist = _st-vec2(0.5);
	return 1.-smoothstep(_radius-(_radius*0.01),
                         _radius+(_radius*0.01),
                         dot(dist,dist)*4.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord/2.0  - 0.1 * iResolution.xy) / iResolution.y+0.35;

    //background gradient colour
    float pct = 1.8-st.y*1.6;

    //the loop repeats 50 times, so there are 50 circles drawn in total
    for(int i=0; i<50;i++){
        //change i to float so I can multiply by it
        float i_float = float(i);
        //here the circle function is called. The location of each circle is 
        //adjusted by the sin of the time value.
        //The 50 circles are in different positions because the incremented i_float 
        //value is used:
    	pct += circle(st+(0.2*sin(iTime/3.-i_float*st.yx)),0.001)*st.y*1.5;
    }

	pct=pct-1.;

    // Output to screen, adjust colour
    fragColor = vec4(pct+.1,pct+.1,pct,1.0)*vec4(0.9,0.,0.,0.)+vec4(0.,0.0,0.3,0.);
}

vec2 mul(vec2 a, vec2 b) {
	return  mat2(a,-a.y,a.x) * b;
}
vec2 e (vec2 a) {
	return exp(a.x)*vec2(cos(a.y),sin(a.y));
}
vec2 ln (vec2 a) {
	return vec2(log(length(a)),atan(a.y,a.x));
}
vec2 pw (vec2 a, vec2 b) {
	return e(mul(ln(a),b));
}
vec2 pw (vec2 a, float n) {
	float f = n * atan(a.y,a.x);
    return pow(length(a),n) * vec2(cos(f),sin(f));
}
vec4 col (vec2 U) {
	#define R iResolution.xy
    float t = iTime*sqrt(iTime)/50.;
    U = (U-0.5*R)/R.y;
    U *= 1.+2./(1.-U.y);
    U *= 1000.*exp(-fract(t)*17.);
    float c = cos(iTime), s = sin(iTime);
    U *= mat2(c,-s,s,c);
    U.x += .367878;
    vec2 z = U;
    float o = mod(floor(t),5.);
    float n = 2.6;
    if (o==1.) n = 1.7;
    if (o==2.) n = -2.1;
    if (o==3.) n = -2.8;
    if (o==4.) n = 2.;
    n += .3*sin(1243.*floor(t));
    for (float i = 0.; i < 20.; i++) {
        if (length(z)==0.) return vec4(0);
    	z = ln(z);
        z = pw(z,n);
        if (length(z)>1e4) return vec4(1);
        z = e(z);
    }
   	return min(abs(z.xyxy),1.);
}
void mainImage( out vec4 Q, in vec2 U )
{
    Q = 0.25*(
        col(U+vec2(.5,0))+
        col(U+vec2(0,.5))+
        col(U-vec2(.5,0))+
        col(U-vec2(0,.5))
    )*exp(-.5*length(U-0.5*R)/R.y);
}

