const float escapeRadius = 128.;
const float speed = .225;

//const vec2 origin = vec2(-0.05,.6805);
const vec2 origin = vec2(0.);

vec2 cMul(vec2 a, vec2 b)
{
	return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}

vec2 cPow(vec2 z, float p)
{

    float r = pow(length(z),abs(p));

   	float theta = atan(z.y, z.x)*p;

    return r*vec2(cos(theta), sin(theta));
}

float dist(vec2 pos, vec2 c, out float iter)
{
    vec2 z = pos;
    vec2 dz = vec2(0.);

	const float maxIter = 250.;

    const float power = 2.;


    float m2;
    float i=0.;
    for(;i<maxIter;i++)
    {
        dz = 2.* mat2(z,-z.y,z.x) * dz + 1.;
		//z = cPow(z,power)+c;
        z = mat2(z,-z.y,z.x)*z+c;

        m2 = dot(z,z);
        if( m2>escapeRadius)
            break;
    }


    iter = i/maxIter;

    return sqrt( m2/dot(dz, dz))*0.5f*log(m2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tz = 0.5-0.5*abs(cos(speed*iTime));

    float zoom = pow( 0.5, 13.0*tz );	

	vec2 uv = (2. * fragCoord - iResolution.xy ) / iResolution.y;

    vec2 c = origin + uv;//*zoom;

	uv.x *= iResolution.x/iResolution.y;

    float iterations = 0.;


    float p = abs(sin(iTime*.3));

    float d = abs(dist(c, mix(vec2(0., 1.), vec2(0., .65), p), iterations));

    float t = clamp(pow(4.*d/zoom,.2),0.,1.);

    fragColor = vec4((1.-t)*vec3(0.8, 0.,0.6),1.);
}

// By Dag Brynildsen Tholander 2019 06 10

vec3 lightDir = vec3(1.,1.,1.);
vec3 lightColor = vec3(1,.8,.75);

float noise(vec2 p)
{
    return fract(sin(p.x*12.+p.y*525.)*1256.);
}

float smoothNoise(vec2 p)
{
    vec2 id = floor(p*1.);
    vec2 ld = fract(p*1.);
    ld = ld*ld*(3.-2.*ld);
    float bl = noise(id);
    float br = noise(id+vec2(1., 0.));
    float b = mix(bl, br, ld.x);

    float tl = noise(id+vec2(0., 1.));
    float tr = noise(id+vec2(1., 1.));
    float t = mix(tl, tr, ld.x);

    float bt = mix(b, t, ld.y);

    return bt;
}

float map(vec3 p)
{
    float a = p.y/1.3;
    p+=vec3(cos(a), 0, sin(a)); // achieves the effect of 3d noise by morphing the 2d noise
    float base = 0.;
    base+=1.15*pow(smoothNoise(p.xz/30.), .5);
    base+=.25*pow(smoothNoise(p.xz/10.), 3.);
    base+=.125*pow(smoothNoise(p.xz/5.), 3.);
    base+=.02*pow(smoothNoise(p.xz), 2.);
    base+=.005*smoothNoise(p.xz*3.);
    return base*30.-p.y*1.3; // as suggested by Dave_Hoskins
}

vec3 normal(vec3 p)
{
	float delta = 0.01;
    vec3 x = vec3 (delta, 0.00, 0.00);
	vec3 y = vec3 (0.00, delta, 0.00);
	vec3 z = vec3 (0.00, 0.00, delta);

    float val = map(p);
    vec3 grad = vec3(val - map(p - x), val - map(p - y), val - map(p - z));
    return -normalize(grad);
}

float ray(vec3 ro, vec3 rd, float minD, float maxD, float stepsize)
{
    float t = minD;
    float lastVal;

    while(t < maxD)
    {
        vec3 p = ro+rd*t;
        float val = map(p);

        if(val>0.)
            return t-(abs(val)/(abs(val)+abs(lastVal)))*stepsize; // my way of doing a linear interpolation to get closest to the 'terrain'

        t+=stepsize;
        stepsize*=1.01; // increase stepsize with distance, increases performance but causes artifacts and errors
        lastVal = val;
    }
    return t;
}

vec3 lighting(vec3 n, vec3 ambientLight)
{
    return  max(dot(normalize(lightDir), n), 0.0) * lightColor * 1. +ambientLight;
}

vec3 fog(vec3 rgb, vec3 rd, float dist, float maxDist)
{
    vec3 fogColor = mix(vec3(.4,.4,.7), vec3(0), rd.y*1.2);
    fogColor += vec3(.3,.2,.2)*pow(1.-abs(rd.y), 6.);
    fogColor+=.75*lightColor*pow(clamp(dot(normalize(rd), normalize(lightDir)), 0., 1.),5.);
	if(dist>maxDist-0.5)
    {
        fogColor+= lightColor*pow(clamp(dot(normalize(rd), normalize(lightDir)), 0., 1.),500.);
    }
    return mix(rgb, fogColor, dist/maxDist);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // cam setup
    vec3 camRot = vec3(0, iTime/10., 0.);
    vec3 camDir = vec3(sin(camRot.y), 0, cos(camRot.y));
    vec3 camRight = cross(camDir, vec3(0,1.,0));
    vec3 camPos = vec3(0., 30., iTime);

    vec3 rayDir = camDir + camRight*2.5*(uv.x-0.5)+ vec3(0,1.,0)*2.5*(uv.y-0.5); // changes were made kind of as suggested by Shane
    float viewDist = 100.;

	// raymarching
	float rayResult = ray(camPos, rayDir, 0.5, viewDist, 0.06);
    vec3 hitPoint = rayResult*rayDir+camPos;

    // Triplanar texturing

    vec3 n = normal(camPos+rayDir*rayResult);
    float xAlpha = pow(abs(n.x), 3.);
    float yAlpha = pow(abs(n.y), 5.);
    float zAlpha = pow(abs(n.z), 3.);
    vec3 terrainColor = vec3(0);
    terrainColor += texture(iChannel0, vec2(hitPoint.z, hitPoint.y)/3.).xyz*xAlpha;
    terrainColor += texture(iChannel1, vec2(hitPoint.x, hitPoint.z)/3.).xyz*yAlpha;
    terrainColor += texture(iChannel0, vec2(hitPoint.x, hitPoint.y)/3.).xyz*zAlpha;

    // Final color
    vec3 col = fog(lighting(n, vec3(.1,.1,.1)).xyz*terrainColor, rayDir,rayResult,viewDist);

    //col = vec3(smoothNoise(uv)); visualize noise

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define BLACK_COL vec3(23, 32, 38) / 255.0
#define WHITE_COL vec3(245, 248, 250) / 255.0

float map(vec3 p) {
    return length(mod(p, 2.0) - 1.0) - 1.3;
}

vec3 getNormal(vec3 p) {
    float t = map(p);
    vec2 d = vec2(0.001, 0.0);
    return normalize(vec3(t - map(p + d.xyy), t - map(p + d.yxy), t - map(p + d.yyx)));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;

    vec3 camDir = normalize(vec3(uv * 1.0, (sin(iTime) * 0.5 + 0.5) * 0.75 + 0.25));
    vec3 camPos = vec3(1.0, (cos(iTime) * 3.0) - 1.57 , - iTime * 2.5);

    float t = -0.5;
    for(int i = 0 ; i < 100; i += 1) {
        t += map(camDir * t + camPos);
    }
    vec3 surf = camDir * t + camPos;
    vec3 light = normalize(vec3(0.0, 0.0, 1.0));
    vec3 normal = getNormal(surf);

    vec3 col = mix(BLACK_COL, WHITE_COL, dot(light, normal));

    fragColor = vec4(col, 1.0);
}

