const float escapeRadius = 128.;

const vec2 origin = vec2(-0.05,.6805);
#define experimental 0

vec2 cMul(vec2 a, vec2 b)
{
	return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}

vec2 cPow(vec2 z, float p)
{

    float r = pow(length(z),abs(p));

   	float theta = atan(z.y, z.x)*p;

    return r*vec2(cos(theta), sin(theta));
}

float dist(vec2 pos, out vec2 lastPos, out vec2 derivative, out float iter)
{
    vec2 z = vec2(0.0);
    vec2 dz = vec2(0.0);
#if experimental
	float maxIter = abs(sin(iTime*0.5)*100.)+10.;
#else
	const float maxIter = 1024.;
#endif

#if experimental
    float power = abs(sin(iTime*0.1)*3.);
#else
    const float power = 2.;
#endif

    float m2;
    float i=0.;
    for(;i<maxIter;i++)
    {
        dz = 2.* mat2(z,-z.y,z.x) * dz + 1.;
		z = cPow(z,power)-abs(pos);

        m2 = dot(z,z);
        if( m2>escapeRadius)
            break;
    }

	derivative = dz;
    iter = i/maxIter;
    lastPos = z;
    return sqrt( m2/dot(dz, dz))*0.5f*log(m2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float tz = 0.5-0.5*cos(0.225*iTime);
    float zoom = pow( 0.5, 13.0*tz );	

	vec2 uv = (2. * fragCoord - iResolution.xy ) / iResolution.y;

    vec2 c = origin + uv*zoom;

	uv.x *= iResolution.x/iResolution.y;

    vec2 z=vec2(0.,0.);
    float iterations = 0.;
    vec2 dz = vec2(0.,0.);
    float d = abs(dist(c, z, dz, iterations));

    float t = clamp(pow(4.*d/zoom,.2),0.,1.);

    fragColor = vec4((1.-t)*vec3(0.56, 0.3,0.1),1.);
	if (iterations==1.) fragColor = vec4(texture(iChannel0, z, 0.0).xyz, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv.y = fract(uv.y*1.);
    uv.y -= .5;

    float T = (iTime + uv.y*3.*uv.x) * 2.5;



    vec3 col = vec3(0);


    float t = uv.x;// + iTime * .2;
    float waves = 5.;
    float s = cos(t * 3.14*2. * waves);
    float h = mix(.0, .05, cos(T+3.14)*.5+.5);

    s = s*h+h;

    float th = .15;//mix(0., .2, uv.x);
    float R = (1.-step(s+th, uv.y));
    float G = (   step(s-th, uv.y));
    float B = (   step(s + (sin(T))*.5*th, uv.y));

    col = vec3(R,G,B);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define scale 4.

//classic rotation matrix
mat2 rot(float a)
{
 return mat2(cos(a), -sin(a), sin(a), cos(a));   
}

//classic hash function
float rnd(vec2 p)
{
 return fract(sin(dot(p, vec2(12.9898, 78.233)))*43526.235342);  
}

//classic function for rotating a coordinate sytem based on
//the floored valaues of a scaled system (used for making all
//kinds of truchets (almost said all kinds of gains)
vec2 rotCoord(vec2 p)
{

 vec2 ipod = floor(p*scale);  
 vec2 fpod = fract(p*scale)-0.5;

    float index = rnd(ipod);

    if(index >0.75)
    {
     p = fpod*rot(3.14);   
    }
    else if(index >0.5)
    {
     p = fpod*rot(3.14/2.);   
    }    
    else if(index >0.25)
    {
     p = fpod*rot(-3.14/2.);   
    }    
    else
    {
     p = fpod;   
    }


 return p+0.5;   
}

//function for creating the gold square plating

float shadeSquares(vec2 p)
    {	
    //I decided to add some small ring shapes to the plating
    vec2 coor2 = fract(p*20.);
 	coor2 = coor2-0.5;
 float c = (1.0-smoothstep(0.1, 0.101,length(coor2)))
     -(1.0-smoothstep(0.05, 0.051,length(coor2)));


        //and this is for the gold squares
    p = p*2.0-1.0;
    p = abs(p);
    vec2 st = smoothstep(98.2, 99.9, (p*100.));
    float border = max(st.x, st.y);


    p = floor(p*10.)/10.;
    float sq = max(p.x, p.y)-border/6.;

  //this adds the circles to the squares and returns everything
 return sq-max(0., c*sq/10.);   
}


//creates a circle
//p = uv or other position, r is radius, ss is smoothstep amount 
float circle(vec2 p, float r, float ss)
{

 ///float ss = 0.0009;
    float c = length(p/1.35);
    float ci = smoothstep(r-ss, r+ss, c);

 return ci;   
}


//creates a ring, one circle subtracted by another
//p = uv or other position,
//r is radius, 
//th is thickness of ring
//ss is smoothstep amount

float ring(vec2 p, float r, float th, float ss)
{
 //   p = p*2.0-1.0;
    //p = abs(p*5.)-0.4;
    float c1 = 1.0-circle(p, r, ss);
    float c2 = 1.0-circle(p, r-th, ss);

    float ri = c1-c2;
    return ri;
}

//vignetting for screen
float vig (vec2 st)
{
    st *=  1.0 - st.yx;
    float vig = st.x*st.y*15.;
    vig = pow(vig, 0.15); 
    return vig;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float v = vig(uv);//get vignett shade
    uv.x*=iResolution.x/iResolution.y;

    uv+=iTime/5.;//move pattern diagonally down with time


    //create textures
    float tx0 = vec3(texture(iChannel0, uv)).x;
    //this texture moves to create a shine animation on the truchet edges
	float tx1 = vec3(texture(iChannel1, uv+iTime/10.)).x;
    float tx2 = vec3(texture(iChannel2, uv)).x;
    float tx3 = vec3(texture(iChannel3, uv)).x;

    vec2 rotUV = rotCoord(uv);
    uv = fract(uv*scale/4.);

    float shades = shadeSquares(uv);

    // Time varying pixel color
    vec3 col = vec3(0., 0., 0.);
    vec3 Dred = vec3(.8, 0., 0.)*tx0;//dred was supposed to be"dark red" but ...
    vec3 Bred = vec3(0.3, 0.1, 0.1)-tx1/3.+tx3/5.;
    vec3 gold = vec3(213., 123., 15.)/255.;
    gold =gold-tx2/10.
        +abs(sin(uv.x*3.+uv.y*3.+iTime/2.+shades*4.))/3.-0.12;

    //another color option:

    //Bred = vec3(213., 123., 15.)/255.-pow(tx1/3., 1.5)*5.;
    //Dred = Bred*tx0;
    //Bred = gold-tx1/1.;

    ///creating the gold sqaure plating and adding to screen
    col = mix(col, gold, shadeSquares(uv));

    //unused but gives me an idea for the next one!
    //col = mix(col, Dred,ring(uv-1., 0.49, 0.2, 0.0009));

    //sha is shadow, used to give the truchet some depth
    float sha = 0.1;//shadow subtraction thing

    //setting uv now to the rotated uv for truchet stuff
    uv = rotUV;

    //adding a ring to one side of the truchet tile, 
    //the upper right corner?
    col = mix(col, Dred,ring(uv-1., 0.49, 0.2, 0.0009));

    //adding a shadow below the ring, so much hacking of those variabls is just about positioning
    col = mix( col, vec3(.0, 0., 0.)-sha, ring(uv-1., 0.5, 0.01, 0.042));
    col = mix(col, vec3(.0, 0., 0.)-sha, ring(uv-1., 0.25, 0.01, 0.042));

    //adding an outine to the ring, so much hacky positioning
    col = mix( col, Bred, ring(uv-1., 0.49, 0.05, 0.0009));
    col = mix(col, Bred, ring(uv-1., 0.3, 0.05, 0.0009));

    //adding a ring to the other side of the truchet tile, 
    //the upper right corner?
    col =mix(col,  Dred, ring(uv, 0.49, 0.2, 0.0009));

    //adding a shadow below the ring, 
    col = mix( col, vec3(.0, 0., 0.)-sha, ring(uv, 0.5, 0.01, 0.042));
    col = mix(col, vec3(.0, 0., 0.)-sha, ring(uv, 0.25, 0.01, 0.042));

    //adding an outline to the ring
    col = mix( col, Bred, ring(uv, 0.49, 0.05, 0.0009));
    col = mix(col, Bred, ring(uv, 0.3, 0.05, 0.0009));

    // Output to screen
    fragColor = vec4(col*1.5*v,1.0);
}

