uint part1by1 (uint x) {
    x = (x & 0x0000ffffu);
    x = ((x ^ (x << 8u)) & 0x00ff00ffu);
    x = ((x ^ (x << 4u)) & 0x0f0f0f0fu);
    x = ((x ^ (x << 2u)) & 0x33333333u);
    x = ((x ^ (x << 1u)) & 0x55555555u);
    return x;
}

uint compact1by1(uint x) {
    x = (x & 0x55555555u);
    x = ((x ^ (x >> 1)) & 0x33333333u);
    x = ((x ^ (x >> 2)) & 0x0f0f0f0fu);
    x = ((x ^ (x >> 4)) & 0x00ff00ffu);
    x = ((x ^ (x >> 8)) & 0x0000ffffu);
    return x;
}

uvec2 unpack_morton2x16(uint p) {
    return uvec2(
        compact1by1(p),
        compact1by1(p >> 1));
}

uint pack_morton2x16(uvec2 v) {
	return part1by1(v.x) | (part1by1(v.y) << 1);
}

float map(vec2 p) {
    float d0 = length(p) - 0.5;
    float d1 = length(p + vec2(-0.25,0.0)) - 0.3;
    float d2 = length(p + vec2(0.0, 0.5)) - 0.2;
    return max(min(d0,d2), -d1);
}

#define MAX_LEVEL 7u

vec3 get_rect(uint c, uint level) {
    uvec2 v = unpack_morton2x16(c);
    float r = 1.0 / float(1u << level);
    vec2 center = (vec2(v) / float(1u << MAX_LEVEL)) * 2.0 - 1.0 + r;
    return vec3(center, r);
}

void draw_rect(uint c, uint level) {
    vec3 rc = get_rect(c, level);
    //rc.z -= 0.002;// * float(level);
    rectangle(rc.xy - rc.z, vec2(rc.z) * 2.0);
}

uint find_lsb(uint value) {
    // with GL4, this loop can be replaced with
    // a single use of findLSB()
    uint count = 0u;
    for (uint i = 0u; i < MAX_LEVEL; ++i) {
        if ((value & 3u) != 0u)
            break;
        count += 2u;
        value >>= 2u;
    }
    return count;
}

uint lowest_level(uint p) {
    uint used_levels = min(MAX_LEVEL, find_lsb(p) / 2u);
    return MAX_LEVEL - used_levels; 
}

void draw_point(uint p, uint level) {
    float l = float(1u << MAX_LEVEL);
	float x = float(p) / (l * l);
    x = x*2.0 - 1.0;
    circle(x, -0.9 + float(level)*0.01, 0.003);
}

uint surface_cells_found = 0u;
uint surface_cell_misses = 0u;
uint total_steps = 0u;

bool walk(inout uint p, inout uint level, bool is_active) {
    const uint topstep = 1u << (MAX_LEVEL * 2u);
    if (p >= topstep)
        return false;
    vec3 rc = get_rect(p, level);
    float d = map(rc.xy);
    uint stepsz = topstep >> (level * 2u);
    bool hit = abs(d) < (rc.z * sqrt(2.0));
    if (hit) {
        set_source_rgb(0.0,1.0,0.0);
        draw_point(p, level);
        fill();
        if ((level == MAX_LEVEL)||is_active) {
            draw_rect(p, level);
            stroke();
            if (level == MAX_LEVEL) {
            	surface_cells_found++;
            }
        }
    } else {
        surface_cell_misses++;
        set_source_rgb(1.0,0.0,0.0);
        draw_point(p, level);
        fill();
        if (is_active) {
            draw_rect(p, level);
            stroke();
        }
    }
    if (hit && (level < MAX_LEVEL)) {
        level += 1u;
    } else {
        p += stepsz;
        level = lowest_level(p);
    }
    if (is_active)
        return false;
    return true;
}

void paint() {
    surface_cells_found = 0u;

    set_source_rgb(vec3(0.0));
    clear();

    set_line_width_px(1.0);
    set_source_rgb(vec3(1.0));
    add_field(map(get_origin()));
    stroke();

    set_line_width_px(1.0);

    set_source_rgb(vec3(0.0));

    uint p = 0u;
    uint level = 0u;
    int maxi = iFrame % 1600;
    for (int i = 0; i < 1600; ++i) {
        total_steps++;
        if (!walk(p, level, (i == maxi)))
            break;
    }



}

void paint_post(inout vec4 fragColor, vec2 fragCoord) {

    vec3 c = PrintValue((fragCoord - vec2(0.0, 10.0)) / vec2(8.0, 15.0), 
		float(surface_cells_found), 10.0, 0.0) * vec3(0.0,1.0,0.0);

    c += PrintValue((fragCoord - vec2(0.0, 30.0)) / vec2(8.0, 15.0), 
		float(surface_cell_misses), 10.0, 0.0) * vec3(1.0,0.0,0.0);

    c += PrintValue((fragCoord - vec2(0.0, 50.0)) / vec2(8.0, 15.0), 
		float(total_steps), 10.0, 0.0);


    fragColor += vec4(c, 1.0);


}

