#define AA 1

// https://www.shadertoy.com/view/tl23RK
float sdSqCappedTorus(in vec3 p, in vec2 sc, in float ra)
{
    p.x = abs(p.x);
    float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
    return dot(p,p) + ra*ra - 2.0*ra*k;
}

vec3 hash( float n )
{
    vec3 m = n*vec3(23.0,41.0,17.0) + vec3(9.0,1.0,31.0);
    return fract( m*fract( m*0.3183099 ) );
}

vec2 map( in vec3 pos )
{
    vec3 pp = vec3(0.0);
    vec3 ww = vec3(0.0,1.0,0.0);

    float d = length(pos-pp);

    vec4 data = vec4(0.0);

    for( int i=0; i<32; i++ )
    {
        // segment parameters        
        vec3 ran = hash(float(i));
        float ra = 0.13 + 0.08*ran.x; // radius
        float ap = 1.10 + 0.80*ran.y; // aperture
        vec3  up = normalize( sin(75.0*ran.z+vec3(0.0,1.0,4.0))); // orientation

        // world to torus transformation
        vec2 sc = vec2(sin(ap),cos(ap));
        vec3 ou = normalize(cross(ww,up));
        vec3 vv = cross(ou,ww);
        vec3 uu =  sc.x*ou + sc.y*ww;
             ww = -sc.y*ou + sc.x*ww;
        vec3 cpos = (pos-pp)*mat3(uu,ww,vv) + ra*vec3(-sc.x,sc.y,0.0);

        // distance evaluation        
        float tmp = sdSqCappedTorus(cpos, sc, ra );
        if( tmp<d )
        {
            d = tmp;
            data = vec4( float(i), cpos.xy, ap );
        }

        // prepare next segment        
        pp += 2.0*ra*sc.x*uu;
        ww = sc.y*uu - sc.x*ww;
    }

    return vec2(sqrt(d) - 0.035, // distance
                data.x + 0.5+0.5*(atan(data.y,data.z))/data.w // u
               );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ).x + 
					  e.yyx*map( pos + e.yyx*eps ).x + 
					  e.yxy*map( pos + e.yxy*eps ).x + 
					  e.xxx*map( pos + e.xxx*eps ).x );
}

#define ZERO min(iFrame,0)

float calcAO( in vec3 pos, in vec3 nor )
{
	float ao = 0.0;

	vec3 v = normalize(vec3(0.7,0.5,0.2));
	for( int i=ZERO; i<12; i++ )
	{
		float h = abs(sin(float(i)));
		vec3 kv = v + 2.0*nor*max(0.0,-dot(nor,v));
		ao += clamp( map(pos+nor*0.01+kv*h*0.2).x*3.0, 0.0, 1.0 );
		v = v.yzx; if( (i&2)==2) v.yz *= -1.0;
	}
	ao /= 12.0;
	ao = ao + 2.0*ao*ao;
	return clamp( ao*2.5, 0.0, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera movement	
	float an = 0.15*(iTime-8.0);
    vec3 ta = vec3( 0.25, -0.06, -0.75 );
	vec3 ro = ta + vec3( 1.7*cos(an), 0.6, 1.7*sin(an) );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv =          ( cross(uu,ww));

    // render
    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.6*ww );

        // raymarch
        const float tmax = 3.5;
        float t = 0.5;
        float m = 0.0;
        for( int i=0; i<256; i++ )
        {
            vec3 pos = ro + t*rd;
            vec2 h = map(pos);
            m = h.y;
            if( h.x<0.001 || t>tmax ) break;
            t += h.x;
        }

        // shade/light
        vec3 col = vec3(0.1 - 0.015*length(p) + 0.05*rd.y );
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float fre = clamp(1.0+dot(nor,rd),0.0,1.0);
			float occ = calcAO(pos, nor);
            float amb = 0.5 + 0.5*nor.y;
            // material
            vec3 mat = 0.5 + 0.5*cos( m*0.06 + vec3(0.00,1.0,1.8) + 1.0 );
            mat += 0.05*nor;
            // lighting
            col = mat*1.5*occ*vec3(amb+fre*fre*col*0.4);
            //col *= 4.0*smoothstep( 0.9, 1.0, sin(1.0*m+iTime*3.0) );
            col *= 1.0-smoothstep( 0.98, 1.0, sin(1.0*m+iTime*3.0) );
            col += fre*occ*0.5*vec3(0.5,0.7,1.0)*smoothstep(0.0,0.1,reflect(rd,nor).y);
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // cheap dithering
    tot += sin(fragCoord.x*114.0)*sin(fragCoord.y*211.1)/512.0;

	fragColor = vec4( tot, 1.0 );
}

#define MAX_ITER 100
#define EPS 0.00001
#define MAX_DIST 20.0
#define PI2 6.28318530718 

vec3 hsv2rgb(in vec3 c) {
    vec3 rgb = clamp(abs(mod(c.x*7.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0);
	return c.z * mix(vec3(2.0), rgb, c.y);
}

float sphere(vec3 pos, float radius) {
    return length(pos) - radius;
}

float distfunc(vec3 pos, vec4 o1) {
	return sphere(pos - o1.xyz, o1.w);
}

vec3 surfnorm(vec3 pos, vec4 o1) {
    vec2 eps = vec2(1.0, EPS);
	return normalize(vec3(
    	distfunc(pos + eps.yxx, o1) - distfunc(pos - eps.yxx, o1),
	    distfunc(pos + eps.xyx, o1) - distfunc(pos - eps.xyx, o1),
    	distfunc(pos + eps.xxy, o1) - distfunc(pos - eps.xxy, o1)
    ));
}

vec3 calcray(vec3 campos, vec3 camtgt, vec3 camup, vec2 st) {
    vec3 camdir = normalize(camtgt - campos);
    vec3 camright = normalize(cross(camup, campos));
	camup = cross(camdir, camright);

    return normalize(camright * st.x + camup * st.y + camdir);
}

vec4 raymarch(vec3 campos, vec3 raydir, vec4 o1) {
	float totaldist = 1.0;
	vec3 pos = campos;
	float dist = EPS;

	for (int i = 1; i < MAX_ITER; i++) {
    	if (dist < EPS || totaldist > MAX_DIST)
        	break;

    	dist = distfunc(pos, o1);
	    totaldist += dist;
    	pos += dist * raydir;
    }

    return vec4(pos, dist);
}

vec2 normuv(in vec2 uv, in vec2 res) {
	uv = -2.0 + 4.0 * uv.xy / res.xy;
    uv.x *= res.x / res.y;

    return uv;
}

float normsin(float x) {
    return sin(x) * 1.5 + 1.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = -normuv(fragCoord.xy, iResolution.xy);
	vec2 mouse_uv = iMouse.xy / iResolution.xy;

    vec3 campos = vec3(1, EPS, 1);
    vec3 camtgt = vec3(1.0, 2.0, 1.0);
    vec3 camup = vec3(1.0, 1.0, 2.0);

    float angle = iTime * 1.5;
    mat2 rmat = mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );

	camup.xz = rmat * camup.xz;

    vec4 sphere_obj = vec4(2.0, 2.0, 1.0, 3.0);

    vec3 st_raydir = calcray(campos, camtgt, camup, uv);
    vec3 mouse_raydir = calcray(campos, camtgt, camup, mouse_uv);

    vec4 stmarch = raymarch(campos, st_raydir, sphere_obj);
    stmarch.y *= exp(sin(iTime) * 10.0);
    vec4 msmarch = raymarch(campos, mouse_raydir, sphere_obj);

    vec3 color = vec3(5.0, 5.0, 5.0);

    if (stmarch.w < EPS) {
        vec3 norm = surfnorm(stmarch.xyz, sphere_obj);

        float az = atan(stmarch.z / stmarch.x);
        float inc = acos(stmarch.y / length(stmarch.xyz));
		float d = acos(cos(az) * cos(abs(inc)));
        float twist = pow(inc, 1.0);

        color = hsv2rgb(vec3(
            normsin((twist + iTime) * 3.0),
            1.0, 
            pow(normsin((az+twist+iTime)*5.0),2.25)
        ));
    }

    fragColor = vec4(color, 4.0);
}

/*
Shader coded live on twitch (https://www.twitch.tv/nusan_fx)
Original Bonzomatic shader can be found here: http://lezanu.fr/LiveCode/LostTree.glsl
*/

#define time iTime

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);
}

float noise(vec3 p) {
  vec3 ip=floor(p);
  p=fract(p);
  p=smoothstep(0.0,1.0,p);
  vec3 st=vec3(7,137,235);
  vec4 val=dot(ip,st) + vec4(0,st.y,st.z,st.y+st.z);
  vec4 v = mix(fract(sin(val)*5672.655), fract(sin(val+st.x)*5672.655), p.x);
  vec2 v2 = mix(v.xz,v.yw, p.y);
  return mix(v2.x,v2.y,p.z);
}

float tri(float t) {
  return abs(fract(t)-0.5);
}

float smin(float a, float b, float h) {
  float k=clamp((a-b)/h*.5+.5,0.0,1.0);
  return mix(a,b,k) - k*(1.0-k)*h;
}

vec3 light=vec3(6,0,0);

float at=0.0;
float at2=0.0;
float map(vec3 p) {

  vec3 bp=p;

  p.xz *= sign(p.y);

  float trunk = step(bp.y,0.0);

  p.y=-abs(p.y);

  float t=time*2.0;
  float rotamount = trunk*0.5+0.5;
  float rot2 = 0.3 + (1.0-trunk)*0.2;
  float noi = 1.0 + (1.0-trunk)*2.0;
  p.xy *= rot(sin(t*0.5) * 0.2 * p.y * rotamount);
  p.zy *= rot(sin(t*0.6) * 0.25 * p.y * rotamount);

  p.y += 0.53;


  p+=(noise(p*6.0)-.5)*0.05 * noi;
  p+=(noise(p*2.0)-.5)*0.2 * noi;  
  vec3 bp2=p;

  float d = 10000.0;
  for(int i=0; i<7; ++i) {
    p.xz = abs(p.xz);
    float sizey = 0.2 - 0.005*float(i) - (1.0-trunk)*0.03;
    float sizex = 0.53 - 0.07*float(i);
    d = min(d, max(length(p.xz)-0.1*sizex, abs(p.y-sizey)-sizey));
    p.xy *= rot(rot2);
    p.zy *= rot(rot2);
    p.y += sizey*1.9;    
  }

  d = min(d, max(length(p.xz)-0.01, abs(p.y-0.3)-0.3));

  d += (1.0-trunk)*0.007;

  float leaf = length(p-vec3(0,0,0.1))-0.2;
  leaf = max(leaf, 0.3-length(p-vec3(0,0.31,0)));

  leaf += -0.1+noise(bp2*3.0)/7.0 + (1.0-trunk);

  d = min(d, leaf);
  d *= 0.5;

  float planet = length(bp-vec3(0,3,0))-3.2;
  float tris = tri(bp.x)*0.2 + tri(bp.z*0.7+.2)*0.3 + tri(bp.z*1.8+.2)*0.1;
  float pdist = 5.6 + tris;
  planet = max(planet, pdist-length(bp-vec3(0,6.0,0)));
  d=min(d, planet);

  float ast = (length(bp-light) - 1.0);
  at += 0.2/(0.2+ast);

  float ast2 = (length(bp+light) - 1.2);
  at2 += 0.2/(0.2+ast2);

  d=min(d, ast*0.6);
  d=min(d, ast2*0.6);

  return d;
}

void cam(inout vec3 p) {

  p.yz *= rot(sin(time*.5)*0.4-0.2);
  p.xz *= rot(time*0.2);

}

vec3 sky(vec3 r) {
  return mix(vec3(0), vec3(0.5,0.6,1.0), pow(clamp(-r.y*0.5+0.7,0.0,1.0),5.0));
}

float getao(vec3 p, vec3 n, float d) {
  return clamp(map(p+n*d)/d,0.0,1.0)*0.5+0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 s=vec3(0,0,-8);
  vec3 r=normalize(vec3(-uv, 0.8));

  cam(s);
  cam(r);

  s.y -= 1.0;



  light.xy *= rot(time);
  light.yz *= rot(time*.7);

  vec3 p=s;
  float i=0.0;
  bool outside = false;
  for(i=0.0; i<100.0; ++i) {
    float d=map(p);
    if(d<0.001) {
      i += d/0.001;
      break;
    }
    if(d>100.0) {
      outside = true;
      break;
    }
    p+=r*d;
  }


  vec3 col = vec3(0);

  vec3 lcol = vec3(1.0,0.7,0.2);
  vec3 lcol2 = vec3(0.3,1.0,0.5);

  col += pow(at * 0.1,0.6) * 0.8*lcol;
  col += pow(at2 * 0.1,0.6) * 0.4*lcol2;

  float fog=1.0-clamp(length(p-s)/100.0,0.0,1.0);

  vec2 off=vec2(0.01,0);
  vec3 n=normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));

  vec3 l = normalize(light-p);

  float fre = pow(1.0-abs(dot(n,r)), 4.0);

  float ao = pow(getao(p, n, 1.0) * getao(p, n, 0.5) * pow(getao(p, n, 0.05),3.0), 0.4);

  // hack: if close to one of the lights, we direct the normal toward the light to get full illumination
  if(length(light-p)<2.0) n = l;
  if(length(-light-p)<2.0) n = -l;

  col += max(0.0, dot(n,l)) * fog * lcol * 40.0 * ao / pow(length(light-p),2.0);
  col += max(0.0, dot(n,-l)) * fog * lcol2 * 30.0 * ao / pow(length(-light-p),2.0);


  if(outside) {
    col += sky(r);
    vec3 stars = vec3(smoothstep(0.0,1.0,noise(r*53.0)), smoothstep(0.0,1.0,noise(r*33.0)), smoothstep(0.0,1.0,noise(r*127.0)));
    stars = mix(vec3(1.0), pow(stars, vec3(10.0)), 0.7);
    col += vec3(6) * smoothstep(0.9,1.0,noise(r*120.0)) * stars;
  } else {
    col += sky(reflect(r,n)) * fre * ao;
    col += sky(n) * fre * ao;
  }

  fragColor = vec4(col, 1);
}

#define MAX_STEPS 32
#define MAX_DISTANCE 100.

float sphere(vec3 at) {
    float radius = .5;
	return length(at) - radius;
}

float ground(vec3 at) {
	return at.y + 1.;
}

float world(vec3 at) {
	return min(sphere(at), ground(at));
}

float trace(vec3 camera, vec3 coord) {
    float L = 0.;

    for(int i = 0; i < MAX_STEPS; ++i) {
    	float d = world(camera + coord * L);
        L += d;
        if(L > MAX_DISTANCE) break;
    }

    return L;
}

vec3 getNormals(vec3 coord) {
	vec2 e = vec2(.01, 0);
    float w = world(coord);

    return normalize(vec3(world(coord + e.xyy) - w,
                          world(coord + e.yxy) - w,
                          world(coord + e.yyx) - w));
}

vec3 diffLight(vec3 lightPos, vec3 coord) {
    vec3 norm = getNormals(coord);
    vec3 lightDir = normalize(lightPos - coord);
    return vec3(max(dot(norm, lightDir), .0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec3 camera = vec3(0., 0., 3.);
    vec3 coord = normalize(vec3(uv, -2.));


  	float color = trace(camera, coord);
    vec3 light = diffLight(vec3(cos(iTime), 2.+sin(iTime), 2), (camera + coord * color));

    fragColor = vec4(light, 1);
}

precision highp float;

mat2 rot(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c,s,-s,c);
}

const float pi = acos(-1.0);
const float pi2 = pi*2.0;

vec2 pmod(vec2 p, float r) {
    float a = atan(p.x, p.y) + pi/r;
    float n = pi2 / r;
    a = floor(a/n)*n;
    return p*rot(-a);
}

float box( vec3 p, vec3 b ) {
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float ifsBox(vec3 p) {
    for (int i=0; i<5; i++) {
        p = abs(p) - 1.0;
        p.xy *= rot(iTime*0.3);
        p.xz *= rot(iTime*0.1);
    }
    p.xz *= rot(iTime);
    return box(p, vec3(0.4,0.8,0.3));
}

float map(vec3 p, vec3 cPos) {
    vec3 p1 = p;
    p1.x = mod(p1.x-5., 10.) - 5.;
    p1.y = mod(p1.y-5., 10.) - 5.;
    p1.z = mod(p1.z, 16.)-8.;
    p1.xy = pmod(p1.xy, 5.0);
    return ifsBox(p1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 cPos = vec3(0.0,0.0, -3.0 * iTime);
    // vec3 cPos = vec3(0.3*sin(iTime*0.8), 0.4*cos(iTime*0.3), -6.0 * iTime);
    vec3 cDir = normalize(vec3(0.3, 0.0, -1.0));
    vec3 cUp  = vec3(0.5, 1.0, 0.0);
    vec3 cSide = cross(cDir, cUp);

    vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir);

    // Phantom Mode https://www.shadertoy.com/view/MtScWW by aiekick
    float acc = 0.0;
    float acc2 = 0.0;
    float t = 0.0;
    for (int i = 0; i < 99; i++) {
        vec3 pos = cPos + ray * t;
        float dist = map(pos, cPos);
        dist = max(abs(dist), 0.02);
        float a = exp(-dist*3.0);
        if (mod(length(pos)+24.0*iTime, 30.0) < 3.0) {
            a *= 2.0;
            acc2 += a;
        }
        acc += a;
        t += dist * 0.5;
    }

    vec3 col = vec3(acc * 0.01, acc * 0.011 + acc2*0.002, acc * 0.012+ acc2*0.005);
    fragColor = vec4(col,1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float Smiley(vec2 uv, vec2 p, float size) {
    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(0.), .45, .05);

    mask -= Circle(uv, vec2(-.13, .2), .07, .01);
    mask -= Circle(uv, vec2(.13, .2), .07, .01);

    float mouth = Circle(uv, vec2(0., 0.), .3, .02);
    mouth -= Circle(uv, vec2(0., .1), .3, .02);

    mask -= mouth;

    return mask;
}

