//////////////////////////////////////////////////////////////////////////////////
// Infinite Yin Yang Zoom - Copyright 2017 Frank Force
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
//////////////////////////////////////////////////////////////////////////////////

const float zoomSpeed			= 1.5;	// how fast to zoom (negative to zoom out)
const float zoomScale			= 0.05;	// how much to multiply overall zoom (closer to zero zooms in)
const float saturation			= 0.5;	// how much to scale saturation (0 == black and white)
const float turnSpeed			= 10.0;	// how fast to rotate (0 = no rotation)
const float dotSize 			= 0.3;	// how much to scale recursion at each step
const int   recursionCount		= 6;	// how deep to recurse
const float blur				= 4.0;	// how much blur
const float outline				= 0.015; // how thick is the outline
const float brightness			= 0.;

//////////////////////////////////////////////////////////////////////////////////

const float pi = 3.14159265359;
const float e = 2.718281828459;
float RandFloat(int i) { return (fract(sin(float(i)) * 43758.5453)); }
vec2 Rotate(vec2 p, float theta)
{
    float c = cos(theta);
    float s = sin(theta);
    return vec2((p.x*c - p.y*s), (p.x*s + p.y*c));
}
vec4 HsvToRgb(vec4 c) 
{
    float s = c.y * c.z;
    float s_n = c.z - s * .5;
    return vec4(s_n) + vec4(s) * cos(2.0 * pi * (c.x + vec4(1.0, 0.6666, .3333, 1.0)));
}

float GetFocusRotation(int i) 
{ 
    float theta = 2.0*pi*RandFloat(i);
    return theta + turnSpeed*mix(-1.0, 1.0, RandFloat(30+i))*iTime; 
}

vec2 GetFocusPos(int i) 
{ 
    bool side = (RandFloat(50+i) < 0.5);
    vec2 p = vec2(0.0, side? -0.5 : 0.5); 
    return Rotate(p, GetFocusRotation(i));
}

//////////////////////////////////////////////////////////////////////////////////

float YinYang2( vec2 p, out float dotDistance, float co, float scale)
{
   	float b = blur*scale/min(iResolution.y, iResolution.x);
    float d = dotSize;

    float c = 1.0;
    float r;

    // bottom
    r = length(2.0*p + vec2(0, 1));
    if (p.x < 0.0)
	   c = mix(c, 0.0, smoothstep(1.0-b, 1.0+b, r));
    dotDistance = r;

    // top
    r = length(2.0*p - vec2(0, 1));
    if (p.x >= 0.0)
		c = mix(0.0, c, smoothstep(1.0-b, 1.0+b, r));
    if (p.y >= 0.0)
         dotDistance = r;

    // outline
    r = length(p);
    c = mix(c, co, smoothstep(1.0-b, 1.0, r));

    return c;
}

vec4 RecursiveYinYang(vec2 p, int iterations, float scale)
{
    // recursive iteration
    float co = 0.0;
    for (int r = 0; r < recursionCount; ++r)
    {
        // apply rotation
  		float theta = -GetFocusRotation(iterations + r);
        p = Rotate(p, theta);

        float dotDistance = 0.0;
        co = YinYang2(p, dotDistance, co, scale);

        if (dotDistance > dotSize || r == recursionCount)
        {
            float co2 = (p.y < 0.0)? 0.0 : 1.0;
   			float b = blur*scale/min(iResolution.y,iResolution.x);

            co = mix(co2, co, smoothstep(dotSize+outline,dotSize+outline+b,dotDistance));
            int i2 = ((dotDistance < dotSize+outline+b) && (p.y > 0.0))? 1 : 0;
            float value = brightness*fract(0.41*float(iterations + r + i2));
            float hue = 0.133*float(iterations + r + i2);
            return vec4(hue, saturation*co, co - value, 1.0); // stop if outside or reached limit
        }

		// check if top or bottom
        co = (p.y > 0.0)? 1.0 : 0.0;
        p.y += mix(0.5, -0.5, co);

        scale *= 2.0/dotSize;
        p *= 2.0/dotSize;		// apply recursion scale
        p = Rotate(p, -theta);	// cancel out rotation
    }
    return vec4(0);
}

//////////////////////////////////////////////////////////////////////////////////

void mainImage( out vec4 color, in vec2 uv )
{
    // fixed aspect ratio
	vec2 p = (2.0*uv-iResolution.xy)/min(iResolution.y,iResolution.x);
    vec2 p2 = p;

    // wander center
	p.x += 0.3*sin(0.234*iTime);
	p.y += 0.3*sin(0.2*iTime);

	// get time 
	float timePercent = iTime*zoomSpeed;
	int iterations = int(timePercent);
	timePercent -= floor(timePercent);

	// update zoom, apply pow to make rate constant
    const float recursionSize = 2.0 / dotSize;
	float zoom = pow(e, -log(recursionSize)*timePercent);
	zoom *= zoomScale;

	// get focus offset
	vec2 offset = GetFocusPos(iterations);
	for (int i = 0; i < 7; ++i)
		offset += (GetFocusPos(iterations+i+1) / recursionSize) * pow(1.0 / recursionSize, float(i));

    // apply zoom and offset
	p = p*zoom + offset;

    // make the yin yang
    color = RecursiveYinYang(p, iterations, zoom);

    // wander hue
    color.x += (0.1*p2.y + 0.1*p2.x + 0.05*iTime);

    // map to rgp space
    color = HsvToRgb(color);
}

// Cube-mapped Double Quad Truchet by Martijn Steinrucken aka BigWings - 2019
// countfrolic@gmail.com  Twitter: @The_ArtOfCode
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// 
// Playing around with truchet tiles. This makes use of the normal quad tiling
// but has two exit points per edge instead of one making for a lot more random
// looking effect. The lines from entry to exit point are made of segmented
// bezier splines which isn't terribly performant but looks nice.
// 
// In order to get this onto a sphere without any poles I used a cubemapping
// technique which works well with quad truchet because it seemlessly fits
// corners where three edges meet.
//
// I didn't make completely random entry-exit point combinations. Perhaps 
// someone can come up with a clever algorithm for this. Instead, I made a bunch
// of presets that I choose from randomly, subsequently I rotate the entry-exit
// points by a random offset to get the variety you see. There should be many more
// variations though if the points were truly random. A quick calculation (and I
// might be wrong) yields 8 factorial divided by two different combinations,
// which is more than 20 thousand
//
// Set SHOWGRID to true to be able to see the truchet tiles.
//
// Use these to change the effect

#define SHOWGRID false
#define BEZIER_STEPS 8
#define GRID_SIZE 5.
#define SPLINE_WIDTH .1
#define SPLINE_COLOR vec3(1)
#define SPLINE_SOFTNESS .5
#define SHADOW_WIDTH 3.
#define CYCLE_VARIATIONS 0.

// entry-exit point presets
float g[64] = float[64] (
	0., 3., 1., 5., 2., 7., 4., 6.,
    0., 1., 2., 3., 4., 7., 5., 6.,
    0., 7., 1., 5., 2., 6., 3., 4.,
    0., 4., 1., 3., 2., 5., 6., 7.,
    0., 5., 1., 4., 2., 7., 3., 6.,
    0., 6., 1., 5., 2., 4., 3., 7.,
    0., 2., 1., 5., 3., 4., 6., 7.,
    0., 7., 2., 6., 3., 4., 5., 1.
);

float N21(vec2 p) {
	p = fract(p*vec2(123.34,345.56));
    p += dot(p, p+34.23);
    return fract(p.x*p.y);
}

mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c,-s,s,c);
}

float dLineSeg(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a, ba = b - a;
	float h = clamp(dot(pa,ba) / dot(ba,ba), 0., 1.);	
	return length(pa - ba * h);
}

// distance to segmented cubic bezier
float dBezier(vec2 p, vec4 a, vec4 b) {

    vec2 p1=a.xy, p2=p1+a.zw, p3=b.xy+b.zw, p4=b.xy;

    float d = 100.;

    vec2 pb = p1;
    vec2 ppb;

    for(int i=0;i<BEZIER_STEPS;i++) {
    	float t = float(i+1)/float(BEZIER_STEPS);

        ppb = pb;

        vec2 p12 = mix(p1, p2, t);
        vec2 p23 = mix(p2, p3, t);
        vec2 p34 = mix(p3, p4, t);

        vec2 p1223 = mix(p12, p23, t);
        vec2 p2334 = mix(p23, p34, t);

        pb = mix(p1223, p2334, t);

        d = min(d, dLineSeg(p, ppb, pb));
    }

    return d;
}


// returns position and tangent that we need to draw bezier spline
vec4 GetVec(float i, float s) {

    i = mod(i, 8.);
    // i  ->  0   1   2   3   4   5   6   7
    // x  -> -1   1   2   2   1  -1  -2  -2
    // y  ->  2   2   1  -1  -2  -2  -1   1

    float a = 6.2832*i/(8./9.);
    vec2 pos = vec2(
        round(cos(a-.5*3.1415-.3)*2.),
        round(cos(a-.3)*2.)
    );

    i = floor(i/2.);

    // i  ->  0  1  2  3
    // x  ->  0 -1  0  1
    // y  -> -1  0  1  0

    float m2 = mod(i, 2.); 		// 0 1 0 1 0 1
   	vec2 tangent = vec2(
        i==1. ? -1. : m2,
        i==0. ? -1. : 1.-m2
    );

    // you can scale tangent to get different looking splines
    //tangent *= 3.;//mix(.25, 2.5, fract(sin(s*345.23)*5434.));

    return vec4(pos.x, pos.y, tangent.x, tangent.y)*.25;
}

// draw a spline with a shadow
vec4 Spline(vec2 uv, float w, int k, float n, float s) {
	float d = dBezier(uv, GetVec(g[k+0]+n, s+1.), GetVec(g[k+1]+n, s+2.));
    float m = smoothstep(w, w*SPLINE_SOFTNESS, d);

    vec3 col = m*SPLINE_COLOR;
    float shadow = max(m, smoothstep(w*SHADOW_WIDTH, 0., d));

    return vec4(col, shadow);
}

vec4 QuadTruchetTex(vec2 uv) {
    vec2 gv = fract(uv)-.5;
	vec2 id = floor(uv);
    float n = N21(id);  

    float t = iTime*CYCLE_VARIATIONS;
    int k = int(floor(fract(n*3425.12+floor(t*3.))*8.))*8;
    float s = n*10.;
    n = floor(n*8.)+floor(t*2.);
    float w = SPLINE_WIDTH;

    // draw four entry-exit splines
    vec4 s1 = Spline(gv, w, k, n, s);
    vec4 s2 = Spline(gv, w, k+2, n, s+2.);
    vec4 s3 = Spline(gv, w, k+4, n, s+4.);
    vec4 s4 = Spline(gv, w, k+6, n, s+6.);

    // composite splines
    vec4 col = mix(s1, s2, s2.a);
    col = mix(col, s3, s3.a);
    col = mix(col, s4, s4.a);

    if(SHOWGRID && (gv.x>.48||gv.y>.48)) col.r = 1.;

    return col;
}

vec3 GetRayDir(vec2 uv, vec3 ro) {
	vec3 f = normalize(vec3(0)-ro),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f, r),
        c = ro + f,
        i = c + uv.x*r + uv.y*u,
        rd = normalize(i-ro);
    return rd;
}

// returns cubemap coordinates
// xy = uv coords for face of cube, z = cube index (-3,-2,-1, 1, 2, 3)
vec3 WorldToCube(vec3 p) {
	vec3 ap = abs(p);
    vec3 sp = sign(p);
    float m = max(ap.x, max(ap.y, ap.z));
    vec3 st;
    if(m==ap.x)
        st = vec3(p.zy, 1.*sp.x);
    else if(m==ap.y)
        st = vec3(p.zx, 2.*sp.y);
    else
        st = vec3(p.xy, 3.*sp.z);

    st.xy /= m;

    //mattz' trick for less distortion in the corners
    // st.xy = atan(st.xy*tan(0.868734829276))/0.868734829276;

    // iq version, no trig, short and sweet
    //st.xy *= 3.0/(2.0+abs(st.xy));
    //st.xy *= 1.25 - 0.25*st.xy*st.xy;

    // mattz's no trig version
    st.xy *= (1.45109572583 - 0.451095725826*abs(st.xy));


    return st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 M = (iMouse.xy-.5*iResolution.xy)/iResolution.y;

    // set up camera
    vec3 ro = vec3(0,0.,-2.5);
    ro.yz *= Rot(-M.y*3.+sin(iTime*.1));
    vec3 rd = GetRayDir(uv, ro);

    // marching loop
    float dO=0., dS;
    vec3 p;
    for(float i=0.; i<100.; i++) {
    	p = ro + rd * dO;
        dS = length(p)-1.;
        dS = min(dS, -(length(p)-4.));	// background

        if(dS<.001) break;
        dO += dS;
    }

    vec4 col = vec4(0);

    // handle material
    if(dS<.001) {
        float d = length(p-ro);
        vec3 n = normalize(p);
        float fresnel = 1.+dot(rd, n);
        fresnel *= fresnel;
        fresnel *= fresnel;

       	p.xz *= Rot(iTime*.1);
        vec3 cc = WorldToCube(p);
        col = QuadTruchetTex(cc.xy*GRID_SIZE+cc.z*10.);

        // soften edge of main sphere a bit
        if(length(p)<1.2) col = mix(col, vec4(.5), fresnel);

        // darken background
        col /= max(1., d*.3);
    }

    // vignette
    col *= vec4(1.-dot(uv,uv));

    fragColor = vec4(col);
}

#define PI_4 0.78539815
#define PI_2 1.5707963

#define SIZE iResolution.y*.1
#define SF SIZE/min(iResolution.x,iResolution.y)

#define rand1(p) fract(sin(p* 78.233)* 43758.5453) 


mat2 rot(float a){
	float ca = cos(a);
    float sa = sin(a);
    return mat2(ca,-sa,sa,ca);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;

    vec2 guv = uv*SIZE;
    vec2 gid = floor(guv);
    guv = fract(guv) - .5;

    float gidHash = floor((rand1(gid.x + gid.y*100.) + iTime*.1)*2.);    
    guv *= rot(PI_2 * (gidHash));

    float sf = SF*SIZE;

    // float m = 1. - (smoothstep(guv.x-SF, guv.x, guv.y) - smoothstep(guv.x, guv.x+SF, guv.y));        
    float m = 1. - smoothstep(SF,0.,abs(guv.y-guv.x));      

    fragColor = vec4(m);
}

const float twoPi = 6.283;

vec3 hash3(vec2 p) {
	vec3 q = vec3(dot(p, vec2(127.1, 311.7)), dot(p, vec2(269.5, 183.3)), dot(p, vec2(419.2, 371.9)));
	return fract(sin(q) * 43758.5453);
}

// extremely questionable implementation of iq’s Voronoise
float voronoise(vec2 x) {
	vec2 cell = floor(x);
	vec2 cellCoordinate = fract(x);

	float accum = 0.;
	float weight = 0.;
	for(int x = -2; x <= 2; x++) {
		for(int y = -2; y <= 2; y++) {
			vec2 cellOffset = vec2(y, x);
			vec3 noiseValue = hash3(cell + cellOffset); // for Perlin noise, ditch the first two components
			float cellDistance = length(cellOffset - cellCoordinate + noiseValue.xy);
			float smoothedDistance = (1.0 - smoothstep(0., 1.414 /* sqrt(2) */, cellDistance));
			accum += noiseValue.z * smoothedDistance;
			weight += smoothedDistance;
		}
	}
	return accum / weight;
}

// palette function also from an iq article
vec3 palette(float v) {
    return vec3(0.5) + 0.5 * vec3(cos(twoPi * (v + vec3(0.0, 0.333, 0.667))));
}

float stripe(float v, float w) {
	float halfW = w * 0.5;
	float normV = fract(v);
	float alias = fwidth(v);
	return min(smoothstep(0.5 - halfW - alias, 0.5 - halfW, normV), smoothstep(0.5 + halfW + alias, 0.5 + halfW, normV));
}

vec2 direction(float a) {
	return vec2(cos(a), sin(a));
}

vec2 rotate(vec2 p, float a) {
	float c = cos(a);
	float s = sin(a);

	return vec2(c * p.x - s * p.y, s * p.x + c * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 3. * fragCoord/iResolution.x;
    uv.y += 0.1 * sin(uv.x + iTime * 0.1);
	// color

	vec2 noiseCoordinate1 = uv * 8. + direction(fract(iTime * 0.043) * twoPi) * sin(fract(iTime * 0.07) * twoPi);
	float noiseValue1 = voronoise(noiseCoordinate1);
	vec2 noiseCoordinate2 = uv * 9. + direction(fract(iTime * -0.01) * twoPi) * sin(fract(iTime * 0.023) * twoPi) * 2.;
	float noiseValue2 = voronoise(noiseCoordinate2);
	vec2 noiseCoordinate3 = uv * 11. + direction(fract(iTime * 0.0074) * twoPi) * sin(fract(iTime * 0.019) * twoPi) * 3.;
	float noiseValue3 = voronoise(noiseCoordinate3);

	float noiseValue = noiseValue1 * noiseValue2 * noiseValue3 * 3.; // is this normalization factor right? probably not
	vec3 color = palette(3. * noiseValue);

	uv = rotate(uv, cos(iTime * 0.001) * 0.04 * twoPi);
	// distort
	uv += 0.014 * (sin(uv * 8. + iTime * 0.4) + sin(uv * 11. + iTime * 0.31) * vec2(1., -1.));

	// grid

	vec2 diagonalDirection = vec2(sqrt(3.0) * 0.5, 0.5);
	const float lineScale = 10.;
	const float lineWidth = 0.02;

	float v = max(max(stripe(dot(uv, diagonalDirection) * lineScale, lineWidth), stripe(dot(uv, vec2(-diagonalDirection.x, diagonalDirection.y)) * lineScale, lineWidth)), stripe((uv.y + 0.15) * lineScale, lineWidth));

    fragColor = vec4(v * color,1.0);
}

