#define time iTime
float PI = acos(-1.);


// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise (vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

void moda (inout vec2 p, float rep)
{
    float per = 2.*PI/rep;
    float a = atan(p.y, p.x);
    float l = length(p);
    a = mod(a,per)-per*0.5;
    p = vec2(cos(a),sin(a))*l;
}


void mo (inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if (p.y > p.x) p =p.yx;
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}


// polynomial smooth iq
float smin (float a, float b, float k)
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float stmin (float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b),0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}

float torus (vec3 p, vec2 t)
{
    vec2 q = vec2(length(p.xy)-t.x,p.z);
    return length(q)-t.y;
}

float inner_body (vec3 p)
{
    vec3 pp = p;
    p.y += 1.;
    float t = torus (p.xzy, vec2(0.8,0.5));

    p=pp;
    float c = cyl(p.xzy, .4-abs(p.y)*p.y*0.5+sin(p.y*2.+time)*0.1+0.15, 1.5);

    return stmin(c,t,0.2,3.);
}

float first_tentacles (vec3 p)
{
    moda(p.xz, 7.);
    p.x -= 1.5;
    p.x += sin(p.y*2.+time)*0.3;
    float c1 = cyl(p.xzy, 0.2-p.y*0.1, 2.); 

    p.y -= 2.;
    p.xy *= rot(time*0.5);
    float t = torus(p, vec2(0.3,0.05));
    moda(p.xy, 5.);
    p.x -= 0.5;
    p.y += sin(p.x*5.+time)*0.1;
    float c2 = cyl(p.yzx, 0.05-p.x*0.1, 0.2);

    return min(min(t,c2),c1);
}

float second_tentacles (vec3 p)
{
    vec3 pp = p;
    p.y += 2.5;
    float t = torus(p.xzy,vec2(2.5,1.));

    p = pp;
    p.xz *= rot(PI/7.);
    moda(p.xz, 7.);
    p.x -= 2.;
    p.x += sin(p.y*2.-time)*0.3;
    float c1 = cyl(p.xzy, 0.2-p.y*0.3, 2.);
    return smin(c1,t, 0.5);
}

float beating_dots (vec3 p)
{
    p.y += 2.5;
    moda(p.xz, 15.);
    p.x -= 3.5;
    return length(p)-(0.2+exp(-fract(time))*0.15);
}

float background (vec3 p)
{
    p.x += texNoise(p.yz*0.04).r*0.6;
    p.y += texNoise(p.xz*0.04).r*0.5;
    return -cyl(p,20.,1e10);
}

int mat_id;        
float SDF (vec3 p)
{
    float b = background(p);
    mo(p.xz,vec2(2.5,1.));
    p.xz *= rot(PI/2.);
    mo(p.yz, vec2(3.8));
    p.xz*=rot(time*0.5);
    float s = min(stmin(beating_dots(p),second_tentacles(p),0.1,3.),
                  min(inner_body(p),first_tentacles(p))
                 );
    float d = min(b,s);
    if (d == b) mat_id = 1;
    if (d == s) mat_id = 2;
    return d;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.01,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float lighting (vec3 n, vec3 l)
{return dot(n, normalize(l))*0.5+0.5;}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.001,0.001,-20.5), p = ro, col = vec3(0.);
    vec3 rd = normalize(vec3 (uv,1.));

    float shad = 0.;
    bool hit = false;

    for (float i =0.; i<64.; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/64.;
            hit = true;
            break;
        }
        p+=d*rd*0.85;
    }
    float t = length(ro-p);
    if (hit) 
    {
        vec3 n = getnorm(p);
        vec3 l = vec3(0., -5., -2.);
        if (mat_id == 1) col = vec3(shad)*0.4;
        if (mat_id == 2) col = mix(vec3(0.1,0.,0.2),vec3(0.8,1.,0.8),lighting(n,l));
    }
    else col = vec3(0.);

    fragColor = vec4(col,1.);
}

