vec3 map (vec3 p)
{
	for (int i = 0; i < 16; ++i)
	{
		p = abs(p/dot(p,p)-vec3(0.64,0.46,0.93));    
	}	
	return p/50.;
}

vec4 raymarch(vec3 ro, vec3 rd)
{
	vec3 c = vec3(0,0,0);
    float t = 0.0;
	for(int i=0; i<50; ++i)
	{
		t+=0.5;
		c+=map(ro+t*rd);
	}
	return vec4(c,1.0);
}	

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord-iResolution.xy) / iResolution.y;
	vec3 ro = vec3 (0.0,3.0*cos(iTime * 0.05),6.0) ;
	vec3 rd = normalize(vec3(uv,2.0)) ;
    rd = vec3(dot(vec3(1,0,0),rd), dot(vec3(0, cos(iTime * 0.3),sin(iTime * 0.3)),rd), dot(vec3(0,-sin(iTime * 0.3),cos(iTime * 0.3)),rd));
    rd = vec3(dot(vec3(cos(iTime * 0.1),sin(iTime * 0.1),0),rd), dot(vec3(-sin(iTime * 0.1),cos(iTime * 0.1),0),rd), dot(vec3(0,0,1),rd));
    fragColor = raymarch(ro,rd);
}

#define PI 3.141592653589
#define OCTAVES 5
#define STEPS 32
#define MIN_DIST 0.1
#define MAX_DIST 10.0

mat3 rotateX(float a) {
    return mat3(
        vec3(1., 0., 0.),
        vec3(0., cos(a), -sin(a)),
        vec3(0., sin(a), cos(a))
        );
}

//  Simplex 3D Noise
//  by Ian McEwan, Ashima Arts
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 );
  vec4 p = permute( permute( permute(
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 ))
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1),
                                dot(p2,x2), dot(p3,x3) ) );
}

float fbm(vec3 p) {
  float n = 0.0;
  float a = 0.5;
  float s = 2.0;
  for(int i = 0; i < OCTAVES; i++) {
    n += snoise(p + vec3(0., 0., iTime*0.1)) * a;
    p *= s;
    a *= 0.5;
  }
  return n;
}

float sphere(vec3 p, float r) {
    return length(p) - 0.5 * r;
}

float scene(vec3 p) {
    return sphere(p + fbm(p*0.4), 12.0);
}

vec3 getNormals(vec3 p) {
    vec3 delta = vec3(0.001, 0., 0.);
    float x = scene(p + delta.xyy) - scene(p - delta.xyy);
    float y = scene(p + delta.yxy) - scene(p - delta.yxy);
    float z = scene(p + delta.yyx) - scene(p - delta.yyx);
    return normalize(vec3(x, y, z));
}


float rayMarch(vec3 pos, vec3 dir, out vec3 p) {
    float currentDist = 0.;
    for(int i = 0; i < STEPS; ++i) {
        p = pos + (dir * currentDist);
        float distanceToObj = scene(p);
        if(distanceToObj < MIN_DIST) {
            break;
        }
        currentDist += distanceToObj;
        if(currentDist > MAX_DIST) {
            currentDist = 0.;
            break;
        }
    }
    return currentDist;
}

// Standard Blinn lighting model.
// This model computes the diffuse and specular components of the final surface color.
vec3 calculateLighting(vec3 pointOnSurface, vec3 surfaceNormal, vec3 lightPosition, vec3 cameraPosition)
{
    vec3 fromPointToLight = normalize(lightPosition - pointOnSurface); // Get normal
    float diffuseStrength = clamp( dot( surfaceNormal, fromPointToLight ), 0.0, 1.0 );
    vec3 diffuseColor = diffuseStrength * vec3(abs(fbm(pointOnSurface)*2.0)); // Here the color
    vec3 reflectedLightVector = normalize( reflect( -fromPointToLight, surfaceNormal ) );
    vec3 fromPointToCamera = normalize( cameraPosition - pointOnSurface );
    float specularStrength = pow( clamp( dot(reflectedLightVector, fromPointToCamera), 0.0, 1.0), 10.0);
    // Ensure that there is no specular lighting when there is no diffuse lighting.
    specularStrength = min( diffuseStrength, specularStrength );
    vec3 specularColor = specularStrength * vec3( 1.0 );
    vec3 finalColor = diffuseColor + specularColor;
    return finalColor;
}

float rect(vec2 st, float horz, float vert, float soft) {
    float h = smoothstep(-horz, -horz+soft, st.x);
    h *= smoothstep(horz, horz-soft, st.x);
    float v =  smoothstep(-vert, -vert+soft, st.y);
    v *= smoothstep(vert, vert-soft, st.y);
    return h * v;// * d;
}

float crossShape(vec2 st, float thick, float size, float soft) {
    float c = rect(st, thick, size, soft);
    c += rect(st, size, thick, soft);
    return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 st = gl_FragCoord.xy / iResolution.xy * 2. - 1.;
	st.x *= iResolution.x / iResolution.y;

    vec3 cameraPos = vec3(0., 0., -10.);
    vec3 cameraDir = vec3(st.x, st.y, 1.0);
    vec3 p;
    vec3 color = vec3(0.);
    float dist = rayMarch(cameraPos, cameraDir, p);

    //Red cross
    vec3 redCross = vec3(crossShape(st, 0.003, 0.02, 0.001), 0., 0.);


    if(dist > 0.){
        vec3 lightPos = vec3(-1., 7., -10.);
        float camDist = normalize(length(cameraPos - p));
        vec3 normal = getNormals(p);
        color = calculateLighting(p, normal, lightPos, cameraPos);
    }

    color += redCross;
    fragColor = vec4(color, 1.);

}

