// Plasma Cube by adelciotto
// References:
// - https://github.com/ajweeks/RaymarchingWorkshop
// - https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm 

#define RETRO_MODE 1

const float RETRO_PIXEL_SIZE = 4.0; 
const float PI = 3.1415926535; 
const float MIN_DIST = 0.0; 
const float MAX_DIST = 100.0; 
const float EPSILON = 0.001; 
const float GAMMA_CORRECTION = 0.4545; 
const int MAX_MARCHING_STEPS = 63; 

float t = 0.0; 

mat2 rotate(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, s, - s, c);
}

float cubeSDF(vec3 p) {
    p.xz *= rotate(t);
    p.yx *= rotate(t * 0.6);
    p.zx *= rotate(t * 0.4);
    p.y += sin(t * 0.2);
    vec3 d = abs(p) - vec3(1.0);
    float dist = length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
    return dist - 0.1;
}

vec3 estimateNormal(vec3 p) {
    return normalize(
        vec3(
            cubeSDF(vec3(p.x + EPSILON, p.yz)) - cubeSDF(vec3(p.x - EPSILON, p.yz)),
            cubeSDF(vec3(p.x, p.y + EPSILON, p.z)) - cubeSDF(vec3(p.x, p.y - EPSILON, p.z)),
            cubeSDF(vec3(p.xy, p.z + EPSILON)) - cubeSDF(vec3(p.xy, p.z - EPSILON))
        )
    );
}

// modified plasma effect from https://www.bidouille.org/prog/plasma
vec3 plasma(vec3 p, float scale) {
    p *= scale;

    float time = t * 0.3;
    float v1 = sin(p.x + time);
    float v2 = sin(p.y + time);
    float v3 = sin(p.z + time);
    float v4 = sin(p.x + p.y + p.z + time);
    float v5 = sin(length(p) + 1.7 * time);
    float v = v1 + v2 + v3 + v4 + v5;

    v *= 2.0;
    vec3 col = vec3(sin(v * PI), sin(v * PI + 2.0 * PI / 3.0), sin(v * PI + 4.0 * PI / 3.0));
    return col * 0.5 + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    fragCoord = gl_FragCoord.xy;
    #if RETRO_MODE
    fragCoord = ceil(fragCoord / RETRO_PIXEL_SIZE) * RETRO_PIXEL_SIZE;
    #endif
    vec2 uv = vec2(fragCoord - 0.5 * iResolution.xy);
    uv = 2.0 * uv.xy / iResolution.y;

    t = iTime + 0.33 * sin(uv.x * 1.76 + uv.y + iTime);

    vec3 camPos = vec3(0, 0, - 6);
    vec3 at = vec3(0, 0, 0);
    vec3 camForward = normalize(at - camPos);
    vec3 camRight = normalize(cross(vec3(0.0, 1.0, 0.0), camForward));
    vec3 camUp = normalize(cross(camForward, camRight));
    vec3 rayDir = normalize(uv.x * camRight + uv.y * camUp + camForward * 2.0);

    float depth = MIN_DIST;
    vec3 col = vec3(0.0);
    for(int i = 0; i < MAX_MARCHING_STEPS; i ++ ) {
        vec3 p = camPos + depth * rayDir;
        float dist = cubeSDF(p);
        if (dist < EPSILON) {
            vec3 light = normalize(vec3(sin(t) * 1.0, cos(t * 0.5) + 0.5, - 0.5));
            vec3 norm = estimateNormal(p);
            vec3 directional = vec3(1.80, 1.27, 0.99) * max(dot(norm, light), 0.0);
            vec3 ambient = vec3(0.02, 0.02, 0.02);
            vec3 diffuse = plasma(p, 1.0) * (directional + ambient);
            col = diffuse;
            break;
        }
        depth += dist;
        if (depth >= MAX_DIST) {
            break;
        }
    }

    col = pow(col, vec3(GAMMA_CORRECTION));
    fragColor = vec4(smoothstep(0.0, 1.0, col), 1.0);
 }

#define TAU 6.2831853
#define VIDEO false
float wave(float x, float freq, float phase) {
    return (sin(x * freq + phase) + 1.) * 0.5;
}

float rand(inout float seed) {
    seed += wave(seed, 1., 12.) * 0.6;
    return fract(sin(seed)*1000000.);
}

float rand (in vec2 st) {
    return fract(sin(dot(st.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


float choose(vec3 v, inout float seed) {
    return v[int(mod(seed++, 3.))];
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = rand(i);
    float b = rand(i + vec2(1.0, 0.0));
    float c = rand(i + vec2(0.0, 1.0));
    float d = rand(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
        (c - a)* u.y * (1.0 - u.x) +
        (d - b) * u.x * u.y;
}

#define OCTAVES 6
float fbm (in vec2 st) {
    // Initial values
    float value = 0.0;
    float amplitude = .5;
    float frequency = 0.;
    //
    // Loop of octaves
    for (int i = 0; i < OCTAVES; i++) {
        value += amplitude * noise(st);
        st *= 2.;
        amplitude *= .5;
    }
    return value;
}

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

vec3 omni(vec3 v, inout float seed) {
    float p = rand(seed);
    vec3 res = v;
    for (int i = 0; i < 3; i++) {
        if (p < 0.15) {
            vec2 tmp = vec2(choose(v, seed), choose(v, seed));
            tmp = rotate2d((choose(v, seed)+1.) * TAU) * tmp;
            res[i] = wave(tmp.x, tmp.y*5.+0.6, choose(v, seed));
        } else if (p < 0.2) {
            res[i] = pow(choose(v, seed), choose(v, seed)+0.2);
        } else if (p < 0.3) {
            float s = choose(v, seed) + choose(v, seed);
            res[i] = mix(choose(v, seed), rand(s), choose(v, seed)*0.03);
        } else if (p < 0.35) {
            res[i] = length(vec2(choose(v, seed), choose(v, seed))) / 1.4;
        } else if (p < 0.40) {
            res[i] = pow(min(choose(v, seed), choose(v, seed)), 0.8);
        } else if (p < 0.48) {
            res[i] = noise(vec2(choose(v, seed), choose(v, seed)));
        } else if (p < 0.50) {
            res[i] = 1. - choose(v, seed);
        } else if (p < 0.55) {
            res[i] = choose(v, seed);
        } else if (p < 0.65) {
            res[i] = pow(max(smoothstep(choose(v, seed), choose(v, seed), choose(v, seed)), choose(v, seed)), 2.);
        } else if (p < 0.75) {
            float r = rand(seed) + 0.5;
            res[i] = pow(abs(choose(v, seed) - r + choose(v, seed)*r), 0.5);
        } else if (p < 0.85) {
            float modulus = choose(v, seed);
            res[i] = mod(choose(v, seed), modulus) + modulus/2.;
        } else {
        	res[i] = fbm(vec2(choose(v, seed), choose(v, seed)) * 30. * pow(choose(v, seed), 3.));
        }
    }
    res = vec3(rotate2d(choose(v, seed)) * res.xy, res.z);

	return res;
}


