float mball(vec3 p){

	p.z= abs(p.z);

	float h= max( 0.0, max( 2.0 - rtime, rtime - ROUND_TIME + 2.0 ) );

	p.z -= h;

	return max( length(p)-.5, -p.z );
}



float map(vec3 p){
	float r=INF, rc;

	r= min( r, length(p-tball(vec3(0,0,0)))-.2 );

	r= min( r, mball(p-tball(vec3( 0,0,0))) );
	r= min( r, mball(p-tball(vec3( 4,0,0))) );
	r= min( r, mball(p-tball(vec3(-4,0,0))) );

	return r;
}



