			hit= true;
			break;
		}
		t += dis;
	}

	if( hit ){

		vec3 n= normalize(vec3(
			map(vec3(p.x+E,p.y,p.z))-map(vec3(p.x-E,p.y,p.z)),
			map(vec3(p.x,p.y+E,p.z))-map(vec3(p.x,p.y-E,p.z)),
			map(vec3(p.x,p.y,p.z+E))-map(vec3(p.x,p.y,p.z-E))
		));

