    else if (c ==  8)col = float3(111.4,  79.2,  37.2)/255.0;
    else if (c ==  9)col = float3( 66.9,  57.4,   0.0)/255.0;
    else if (c == 10)col = float3(153.7, 102.6,  89.1)/255.0;
