struct NC
{
    int i0;
    int i1;
    int w;
};


NC near(float3 col)
{
    const float3 W=float3(0.299,0.587,0.114);
    NC res;
    float rv=100.0;
    float luma0=dot(col,W);
    for(int i=0;i<15;i++)
    {
        for(int j=i+1;j<16;j++)
        {
            for(int f=0;f<4;f++)
            {
                float3 icol=mix(pal(i),pal(j),float(f)*.25);
                float3 dist=(col-icol);
                dist*=W;

                float d=length(dist*dist);
                if(d<rv)
                {
                    res=NC(i,j,f);
                    rv=d;
                }
            }
        }
    }
    return res;
}


vec3 col(int x, int y)
{
	float time=iTime;
	vec2 uv = vec2(float(x), float(y))/160.0;
    vec2 uv0=uv;
	float i0=1.1;
	float i1=0.9;
	float i2=0.5;
	float i3=0.6;
	float i4=0.0;
    float r=0.0;
    float g=0.0;
    float b=0.0;
    float w=0.0;
	for(int s=0;s<4;s++)
	{
		vec2 d;
		d=vec2(cos(uv.y*i0-i4+time/i1),-sin(uv.x*i0-i4+time/i1))/i3;
        d+=vec2(-d.y,d.x)*0.7;
		uv.xy+=d;

		i0=(i0-0.2)*1.1;
		i1=(i1-2.0)*1.05;
		i2=(i2-1.51)*1.06;
		i3*=0.8237;
		i4+=0.05;
	    r+=(sin(uv.x-time)*0.75+0.5)/i2;
	    b+=(sin(uv.y+time)*0.75+0.5)/i2;
	    g+=(sin((uv.x+uv.y+sin(time*0.5))*0.5)*0.75+0.5)/i2;
        w+=1.0/i2;
	}
    r/=w;
    g/=w;
    b/=w;
	return vec3(r,g,b);
}

