    int iy=int(fc.y)/sc;

    int bx=int(iResolution.x)/sc-320;
    int by=int(iResolution.y)/sc-200;

    o=border(iy);
    if(!((ix<=bx/2)||(ix>=320+bx/2)))
    {
        ix-=bx/2;

        if(!((iy<=by/2)||(iy>=200+by/2)))
        {
            iy-=by/2;

            int x0=(ix/8)*8;
            int y0=(iy/8)*8;

            int fx=ix-x0;
            int fy=iy-y0;

            NC nc=near(col(x0+4, y0+4));

            o = vec4(pal(nc.i0),1.0);
            if(iMouse.z>0.)
            {
                switch(nc.w)
                {
                    case 1:if((fx%2+fy%2)==2)o = vec4(pal(nc.i1),1.0);break;
                    case 2:if((fx+fy)%2==0)o = vec4(pal(nc.i1),1.0);break;
                    case 3:if((fx%2+fy%2)<2)o = vec4(pal(nc.i1),1.0);break;
                }
            }
            else
            {
                switch(nc.w)
                {
                    case 1:if(ilen(fx-3,fy-3)<2)o = vec4(pal(nc.i1),1.0);break;
                    case 2:if(ilen(fx-3,fy-3)<6)o = vec4(pal(nc.i1),1.0);break;
                    case 3:if(ilen(fx-3,fy-3)>=2)o = vec4(pal(nc.i1),1.0);break;
                }
            }
        }
    }
    if(sc>1)
    {
        o*=1.0-abs(fract(fc.y/float(sc))-0.65);
    }
}

const float FREQ_RANGE = 64.0;
const float PI = 3.1415;
const float RADIUS = 0.6;
const float BRIGHTNESS = 0.2;
const float Amplitude = 0.0;

//convert HSV to RGB
vec3 hsv2rgb(vec3 c){
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

float getfrequency(float x) {
	return texture(iChannel0, vec2(floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE, 0.25)).x + 0.06;
}

float getfrequency_smooth(float x) {
	float index = floor(x * FREQ_RANGE) / FREQ_RANGE;
    float next = floor(x * FREQ_RANGE + 1.0) / FREQ_RANGE;
	return mix(getfrequency(index), getfrequency(next), smoothstep(0.0, 1.0, fract(x * FREQ_RANGE)));
}

float getfrequency_blend(float x) {
    return mix(getfrequency(x), getfrequency_smooth(x), 0.5);
}

vec3 doHalo(vec2 fragment, float radius) {
	float dist = length(fragment);
	float ring = 1.0 / abs(dist - radius);

	float b = dist < radius ? BRIGHTNESS * 0.3 : BRIGHTNESS;

	vec3 col = vec3(0.0);

	float angle = atan(fragment.x, fragment.y);
	col += hsv2rgb( vec3( ( angle + iTime * 0.25 ) / (PI * 2.0), 1.0, 1.0 ) ) * ring * b;

	float frequency = max(getfrequency_blend(abs(angle / PI)) - 0.02, 0.0);
	col *= frequency;

	// Black halo
	col *= smoothstep(radius * 0.5, radius, dist);

	return col;
}

vec3 doLine(vec2 fragment, float radius, float x) {
	vec3 col = hsv2rgb(vec3(x * 0.23 + iTime * 0.12, 1.0, 0.0));

	float freq = abs(fragment.x * 0.5);

	col *= (1.0 / abs(fragment.y)) * BRIGHTNESS * getfrequency(freq);	
	col = col * smoothstep(radius, radius * 1.8, abs(fragment.x));

	return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 fragPos = fragCoord / iResolution.xy;
	fragPos = (fragPos - 0.5) * 2.0;
    fragPos.x *= iResolution.x / iResolution.y;

	vec3 color = vec3(0.0134, 0.052, 0.1);
	color += doHalo(fragPos, RADIUS);
	color += doLine(fragPos, RADIUS, fragPos.x);

	color += max(luma(color) - 1.0, 0.0);

	fragColor = vec4(color, 1.0);
}

vec3 lightDirection = vec3(1., 1., -.7);

vec3 trans(vec3 p){
    return mod(p, 4.) - 2.;
}

float dfPlane(vec3 p){
    return p.y + 0.5;
}

float dfSphere(vec3 p){
    return length(trans(p)) - .7;
}

float distanceFunc(vec3 p){
    return dfSphere(p);
}

vec3 getNormal(vec3 p){
    float d = 0.0001;
    return normalize(vec3(
        distanceFunc(p + vec3(  d, 0.0, 0.0)) - distanceFunc(p + vec3( -d, 0.0, 0.0)),
        distanceFunc(p + vec3(0.0,   d, 0.0)) - distanceFunc(p + vec3(0.0,  -d, 0.0)),
        distanceFunc(p + vec3(0.0, 0.0,   d)) - distanceFunc(p + vec3(0.0, 0.0,  -d))
    ));
}

mat3 kaiten(float frec){
    float r = 2. * frec * 3.14159265358979;
    return mat3(
        cos(r),  0., sin(r),
            0.,  1.,     0.,
        -sin(r), 0., cos(r)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);

    vec3 cameraPos = vec3(0., sin(iTime * 0.5) * 3., -5. + iTime);
    float screenZ = 2.5;
    vec3 rayDirection = normalize(vec3(p, screenZ)) * kaiten(iTime*0.025);

    vec3 col = vec3(0.);

    float depth = 0.0;
    for(int i = 0; i < 499; i++){
        vec3 rayPos = cameraPos + rayDirection * depth;
        float dist = distanceFunc(rayPos);
        depth += dist;

        if(dist < 0.0001){
            col = vec3(1.) * clamp(dot(getNormal(rayPos), lightDirection), 0.2, 1.0);
            break;
        }
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}

