vec3 lighting(vec3 cameraPos, vec3 normal, ScenePoint sp) {
    vec3 albedo = sp.color;
    vec3 specular = vec3(1.);
    float shininess = 100.;

    vec3 V = normalize(cameraPos - sp.p);

    vec3 sum = vec3(0.);
    for(int i = 0; i < 4; ++i) {
        vec3 L = -lights[i].dir;
        vec3 H = normalize(V + L);

        float difFactor = max(0., dot(L, normal));
        float specFactor = pow(max(0., dot(H, normal)), shininess);
        sum += lights[i].diffColor * albedo * difFactor +
               lights[i].specColor * specular * specFactor;
    }
    return sum;
}

vec3 shadeAndReflection(vec3 cameraPos, vec3 rd, ScenePoint sp) {
    vec3 result;

    vec3 normal = normal(sp);
    vec3 light = lighting(cameraPos, normal, sp);
    float ao = cheapAo(sp.p, normal, .15);

    bool hit;
    result = light * ao;

#if REFLECTIONS
    ScenePoint reflectionSp;
    vec3 reflected = reflect(rd, normal);
    hit = rm(sp.p + reflected * BIAS_EPSILON, reflected, reflectionSp, SECONDARY_STEPS, 1);
    if(hit) {
        result = mix(light, lighting(cameraPos, reflected, reflectionSp), .5) * ao;
    }
#endif

#if SHADOWS
    ScenePoint shadowSp;
    hit = rm(sp.p + normal * BIAS_EPSILON, -lights[0].dir, shadowSp, SECONDARY_STEPS, 1);
    if(hit) {
        result *= vec3(.2);
    }
#endif

    return result;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    lights[0].dir = normalize(vec3(0., -.3, -1.));
    lights[0].diffColor = vec3(1.);
    lights[0].specColor = vec3(1.);

    lights[1].dir = normalize(vec3(1., 0., 0.));
    lights[1].diffColor = vec3(.3);
    lights[1].specColor = vec3(.05);

    lights[2].dir = normalize(vec3(0., 1., 0.));
    lights[2].diffColor = vec3(.3);
    lights[2].specColor = vec3(.05);

    lights[3].dir = normalize(vec3(-1., 0., 0.));
    lights[3].diffColor = vec3(.3);
    lights[3].specColor = vec3(.05);

    vec2 uv = (gl_FragCoord.xy - .5 * iResolution.xy) / iResolution.y;

    float curve = stoppingCurve(iTime * .5 + 12., 5.);
    float curve2 = stoppingCurve(iTime * .8 + 195., 50.);

    vec3 cameraPos = vec3(6. * (curve - .5) + iTime, 1.9 * (curve2 + .5), 10.);
    vec3 lookAt = vec3(iTime, 0., 0.);
    vec3 rd = cam2world(vec3(uv, 1.), cameraPos, lookAt); 

    ScenePoint sp;
    vec3 col = vec3(.05);

    bool hit = rm(cameraPos, rd, sp, PRIMARY_STEPS, 1);
    if(hit) {
        col = shadeAndReflection(cameraPos, rd, sp);
    }

    col = pow(col, vec3(1. / 2.2));
    fragColor = vec4(col, 1.);
}

#define MOD3 vec3(.1031,.11369,.13787)
#define grad(x) length(vec2(dFdx(x),dFdy(x)))

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

float perlin_noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return 	mix(
        		mix(
                	mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))), 
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                       	w.x),
                	mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))), 
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                       	w.x),
                	w.z),
        		mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))), 
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                       	w.x),
                   	mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))), 
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                       	w.x),
                	w.z),
    			w.y);
}

float fbm(vec3 p)
{
    float r = 0.;
    float m = 0.5;
    for (int i=0; i<5;i++){
        r += perlin_noise(p) * m;
        p *= 2.;
        m *= 0.5;
    }
    return r;
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy) - vec2(0.5);
    uv *= vec2(iResolution.x/iResolution.y,1.);
    float d = length(uv);
    float n = fbm(vec3(uv*0.2,iTime*0.025) * 5.)*0.5 + 0.5;
    d += n*2.;
    d = pow(d,5.);
    float e = d+.5;
    int m = int(e-.5);
    e = abs(fract(e)-.5)/grad(e);
    float h = fbm(vec3(float(m)*13480.09134,1026.7392,7492.1264)+iTime*.1)*3.;
    fragColor = vec4(hsv2rgb(vec3(h,1.,1.)),1.) * vec4(clamp(e,0.,1.));
}

const float E = 0.001;


mat3 rotY(float r){return mat3(cos(r), 0., sin(r), 0., 1., 0., -sin(r), 0., cos(r));}
mat3 rotX(float r){return mat3(1., 0., 0., 0., cos(r), sin(r), 0., -sin(r), cos(r));}
mat3 rotZ(float r){return mat3(cos(r), sin(r), 0., -sin(r), cos(r), 0., 0., 0., 1.);}

float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float plane(vec3 p){
    //p = rotY(iTime / 4.) * p;
    float crazy = abs(p.x/2. - floor(p.x/2. + .5)) * 2.;
    float crazy2 = abs(p.z/2. - floor(p.z/2. + .5)) * 2.;
    float var1 = sin(p.x + iTime) * cos(p.z + iTime) / 2. + p.y + 5.;
	float var2 = -crazy * crazy2 / 2. * (sin(iTime) * .5 + .5) + p.y + 5.;
	//return mix(var1, var2, cos(iTime / 8.) / .5 + .5);
    return var1;
}


float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float sph1(vec3 p){
    return sphere(p - vec3(sin(iTime * 2.), 0., 0.), .2);
}

float box(vec3 p, vec3 b){
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

vec3 normal2(vec3 p){
    return normalize(vec3(
        plane(vec3(p.x + E, p.y, p.z)) - plane(vec3(p.x - E, p.y, p.z)),
        plane(vec3(p.x, p.y + E, p.z)) - plane(vec3(p.x, p.y - E, p.z)),
        plane(vec3(p.x, p.y, p.z + E)) - plane(vec3(p.x, p.y, p.z - E))
    ));
}

vec3 carpos(float t){
    return vec3(sin(t / 2.) * 4., 0., sin(t) * 4. + 8.);
}

float cyl(vec3 p, float r, float h){
	return max(length(p.xy) - r, abs(p.z) - h);
}

float map(vec3 p){

    float pl = plane(p);

    vec3 cp = carpos(iTime);
    vec3 cp2 = carpos(iTime + E);
    vec2 dir = normalize(cp2.xz - cp.xz);

    vec3 bp = carpos(iTime - (sin(iTime) * .5 + .5));
    bp.y -= plane(bp);
    bp.y += .4;

    cp.y -= plane(cp);
    vec3 n2 = normal2(cp);
    cp.y += 0.4;

    mat3 rot = rotY(-atan(dir.x, dir.y) - 1.57) * rotZ(-acos(n2.x) - 1.57) * rotX(acos(n2.z) - 1.57);
    cp = rot * (p - cp);

    float autoke = box(cp, vec3(.4, .1, .2));

    float whl = 1000.;
    whl = min(whl, cyl(cp - vec3(.3, .2, .2), .1, .02));
    whl = min(whl, cyl(cp - vec3(-.3, .2, .2), .1, .02));
    whl = min(whl, cyl(cp - vec3(-.3, .2, -.2), .1, .02));
    whl = min(whl, cyl(cp - vec3(.3, .2, -.2), .1, .02));


    float sph = sph1(p - bp);

    return min(pl, smin(sph, min(autoke, whl), .2));
}


vec3 normal(vec3 p){
    return normalize(vec3(
        map(vec3(p.x + E, p.y, p.z)) - map(vec3(p.x - E, p.y, p.z)),
        map(vec3(p.x, p.y + E, p.z)) - map(vec3(p.x, p.y - E, p.z)),
        map(vec3(p.x, p.y, p.z + E)) - map(vec3(p.x, p.y, p.z - E))
    ));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.y /= iResolution.x / iResolution.y;

    vec3 sky = vec3(0., .5, 1.);
    vec3 c = sky;

    vec3 ro = vec3(0., 0., -3.);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1.0));
    float t = 0.;

    for (int step = 0; step < 200; step++){
    	vec3 p = rotX(.5) * (ro + t * rd);
        float d = map(p);
        if (d < E){
            vec3 n = normal(p);
            float shading = dot(n, normalize(ro - p));
            c = vec3(1.);

            if (sph1(p) < E)
            	c *= texture(iChannel0, vec2(atan(p.x), atan(p.y))).x;
            if (plane(p) < E)
                c = vec3(.8, .3, .0);
            c *= shading;
            c = mix(c, sky, smoothstep(0., .5, length(p - ro) / 100.));
            break;
        }
        else if (d > 100.)
        {
            break;
        }
        t += d;
    }

    fragColor = vec4(c,1.0);
}

const int numTriangles = 500;
const float GRID_SIZE = 0.05;
const float ARBITRARY_OFFSET = 17.567;
const float RADIUS = 7.0;
const float EPSILON = 0.0001;
const float WORLD_SPEED = 1.0;

// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash( uint x ) {
    x += ( x << 10u );
    x ^= ( x >>  6u );
    x += ( x <<  3u );
    x ^= ( x >> 11u );
    x += ( x << 15u );
    return x;
}



// Compound versions of the hashing algorithm I whipped together.
uint hash( uvec2 v ) { return hash( v.x ^ hash(v.y)                         ); }
uint hash( uvec3 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z)             ); }
uint hash( uvec4 v ) { return hash( v.x ^ hash(v.y) ^ hash(v.z) ^ hash(v.w) ); }



// Construct a float with half-open range [0:1] using low 23 bits.
// All zeroes yields 0.0, all ones yields the next smallest representable value below 1.0.
float floatConstruct( uint m ) {
    const uint ieeeMantissa = 0x007FFFFFu; // binary32 mantissa bitmask
    const uint ieeeOne      = 0x3F800000u; // 1.0 in IEEE binary32

    m &= ieeeMantissa;                     // Keep only mantissa bits (fractional part)
    m |= ieeeOne;                          // Add fractional part to 1.0

    float  f = uintBitsToFloat( m );       // Range [1:2]
    return f - 1.0;                        // Range [0:1]
}



// Pseudo-random value in half-open range [0:1].
float random( float x ) { return floatConstruct(hash(floatBitsToUint(x))); }
float random( vec2  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec3  v ) { return floatConstruct(hash(floatBitsToUint(v))); }
float random( vec4  v ) { return floatConstruct(hash(floatBitsToUint(v))); }

vec2 getTriangleCenter(vec2 pos) {
    vec2 gridPoint = vec2(floor(pos.x), floor(pos.y));
    vec2 delta = pos - gridPoint;
    if (delta.x + delta.y < 1.0) return vec2(gridPoint.x + 1.0/3.0, gridPoint.y + 1.0/3.0);
    else return vec2(gridPoint.x + 2.0/3.0, gridPoint.y + 2.0/3.0);
}

float fastNoise(vec2 pos, float offset) {
    return random(vec3(pos, 0.0) + vec3(ARBITRARY_OFFSET, 2.0*ARBITRARY_OFFSET, 5.0*ARBITRARY_OFFSET));
}

bool insideTriangle(vec2 P, vec2 A, vec2 B, vec2 C) {
    vec2 v1 = B - A;
    vec2 v2 = C - A;

    float divisor = determinant(mat2(v1, v2));

    float a = (determinant(mat2(P, v2)) - determinant(mat2(A, v2)))/divisor;
    if (a <= -EPSILON) return false; // free anti-aliasing

    float b = -(determinant(mat2(P, v1)) - determinant(mat2(A, v1)))/divisor;
    if (b <= -EPSILON) return false; // free anti-aliasing

    return (a + b) <= 1.0 + EPSILON; // free anti-aliasing
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 A = vec2(0.5, 0.5);
    //vec2 B = vec2(0.0, 1.0);
    //vec2 C = vec2(1.0, 1.0);

    float width = iResolution.x/iResolution.y;

    vec2 worldOffset = vec2(0.8*iTime, 0.2*iTime) * WORLD_SPEED;
    //worldOffset += vec2(0.8, 0.2)*1000.0;

    // coordinates centered on screen: height = 1.0, width depends on resolution
    vec2 pos = (fragCoord/iResolution.y + vec2(-0.5*width, -0.5))/GRID_SIZE;
    pos += worldOffset;
    vec2 triangleCenter = getTriangleCenter(pos);

    // black background
    float val = 0.0;

    float start_x = floor(min(pos.x, worldOffset.x));
    float start_y = floor(min(pos.y, worldOffset.y));
    float end_x = floor(max(pos.x, worldOffset.x) + 1.0);
    float end_y = floor(max(pos.y, worldOffset.y) + 1.0);

    for (float x  = start_x; x <= end_x; x += 1.0) {
        for (float y  = start_y; y <= end_y; y += 1.0) {
            vec2 dir, offset, A, B, C;
            float distFromCircle, moveSpeed, rotation, dirVariance;
            mat2 rotMat, dirRotMat;

            // triangle 1
            vec2 S = vec2(x + 1.0/3.0, y + 1.0/3.0);
            dir = S - worldOffset;

           	moveSpeed = 0.01 * fastNoise(S, 1.0);
            distFromCircle = max(0.0, dir.x*dir.x + dir.y*dir.y - RADIUS*RADIUS);
            rotation = distFromCircle*(fastNoise(S, 2.0) - 0.5);
            rotMat = mat2(cos(rotation), -sin(rotation), sin(rotation), cos(rotation));
            offset = dir*distFromCircle*moveSpeed;
            A = offset + S + rotMat*vec2(-1.0/3.0, -1.0/3.0);
            B = offset + S + rotMat*vec2(+2.0/3.0, -1.0/3.0);
            C = offset + S + rotMat*vec2(-1.0/3.0, +2.0/3.0);
            if (insideTriangle(pos, A, B, C)) {
               	val = fastNoise(S, 0.0);
                break;
           	}

            // triangle 2
            S = vec2(x + 2.0/3.0, y + 2.0/3.0);
            dir = S - worldOffset;

            moveSpeed = 0.01 * fastNoise(S, 1.0);
            distFromCircle = max(0.0, dir.x*dir.x + dir.y*dir.y - RADIUS*RADIUS);
            rotation = distFromCircle*(fastNoise(S, 2.0) - 0.5);
            rotMat = mat2(cos(rotation), -sin(rotation), sin(rotation), cos(rotation));
            offset = dir*distFromCircle*moveSpeed;
            A = offset + S + rotMat*vec2(-2.0/3.0, +1.0/3.0);
            B = offset + S + rotMat*vec2(+1.0/3.0, -2.0/3.0);
           	C = offset + S + rotMat*vec2(+1.0/3.0, +1.0/3.0);
            if (insideTriangle(pos, A, B, C)) {
                val = fastNoise(S, 0.0);
                break;

            }
        }
    }

    vec3 col = vec3(val, val, val);

    // Output to screen
    fragColor = vec4(col,1.0);
}

