// Inspired by https://vimeo.com/124721382
mat2 rotate(float a) {
  return mat2(cos(a), -sin(a), sin(a), cos(a));
}

float sphereSDF (vec3 p, float r) {
  return length(p) - r;
}

float map (vec3 p) {
  vec3 p1 = p;
  p1.xz *= rotate(iTime * .3);
  p1.yz *= rotate(iTime * .2);
  float s1 = sphereSDF(p1, .6);
  s1 += sin((p1.x + p1.y * p1.z) * (3.14 * 10.) - iTime * 2.) * 
      .015 - sin((p1.x - p1.y * p1.z) * (3.14 * 12.) - iTime) * .01;

  p.z -= iTime * .2;
  vec3 c = vec3(2., 1.3, 2.);
  p = mod(p, c) - .5 * c;

  float s2 = sphereSDF(p, .3);
  s2 += sin((p.x + p.y * p.z) * 20. + iTime * 2.) * 
      .03 + cos(length(p.x - p.y * p.z) * 65. - iTime) * .01;

  return min(s1 * .3, s2 * .28);
}

float trace (vec3 ro, vec3 rd) {
  float e = .001;
  float d = e * 2.;
  float t = 0.;
  for (int i = 0; i < 60; i++) {
    if (d < e || t > 50.) continue;
    d = map(ro + rd * t);
    t += d;
  }
  return t;
}

vec3 getNormal (vec3 p) {
  vec2 e = .01 * vec2(-1., 1.);
  vec3 nor = e.xyy*map(p+e.xyy) +
    e.yxy*map(p+e.yxy) +
    e.yyx*map(p+e.yyx);// +
    //e.xxx*map(p+e.xxx); // comment to get white aura

  return normalize(nor);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
  uv.x *= iResolution.x / iResolution.y;
  float color;

  // camera
  vec3 ro = vec3(0., 0., 3.);
  vec3 rd = normalize(vec3(uv, -3.));

  float t = trace(ro, rd);
  vec3 p = ro + rd * t;

  // lighting
  vec3 light = normalize(vec3(1.));
  vec3 nor = getNormal(p);
  float diff = clamp(dot(nor, light), 0., 1.);

  // float color = p.z;
  color += diff;
  float fog = 1. / (1. + t * t * .1);
  color *= fog * 1.5;

  fragColor = vec4(vec3(color), 1.);
}

float FiveTriangle(vec2 uv, float rotate)
{
    float N = 5.;
    float a = atan(uv.x, uv.y) + rotate;
    float b = 3.1415926*2./N;
    float d = cos(floor(a/b+.5)*b-a) + (1.-abs(fract(a/b+.5)-.5)*2.);
    return d*length(uv);
}

void WaveFlag(vec2 uv, inout vec3 col)
{
    vec2 p = uv;
    p = 2.*p-1.;
    p.x *= iResolution.x/iResolution.y;
    vec2 p1 = p, p2 = p, p3 = p, p4 = p, p5 = p;
    p1 -= vec2(-1.3, .5);
    float gap = .42;
 	float r = .61;
    float a = 3.7;
    p2 = p1 + r * vec2(cos(a), sin(a));
    a -= gap;
    p3 = p1 + r * vec2(cos(a), sin(a));
    a -= gap;
    p4 = p1 + r * vec2(cos(a), sin(a));
    a -= gap;
    p5 = p1 + r * vec2(cos(a), sin(a));
    col = mix(col, vec3(.9, .8, 0.), 1.-smoothstep(.5, .6, FiveTriangle(p1*2.5, 3.14)));
    col = mix(col, vec3(.9, .8, 0.), 1.-smoothstep(.59, .85, FiveTriangle(p2*8.8, 3.14/2.)));
    col = mix(col, vec3(.9, .8, 0.), 1.-smoothstep(.59, .85, FiveTriangle(p3*8.8, 3.14/3.)));
    col = mix(col, vec3(.9, .8, 0.), 1.-smoothstep(.59, .85, FiveTriangle(p4*8.8, 3.14/4.)));
    col = mix(col, vec3(.9, .8, 0.), 1.-smoothstep(.59, .85, FiveTriangle(p5*8.8, 3.14/5.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
 	uv.y += sin(iTime+uv.x*15.)*.03; // may be noise wave, it's so rhythmic
	vec3 col = vec3(0.);
    vec3 bgcol = mix(vec3(.9, .3, 0.), vec3(.9, .8, .1), uv.y);
    bgcol = vec3(.8, 0., 0.);
    col += bgcol;
	WaveFlag(uv, col);

    fragColor = vec4(col,1.0);
}

const float PI = 3.1415926;
const float EPS = .001;

vec3 hsv(float h, float s, float v){return ((clamp(abs(fract(h+vec3(0,2,1)/3.)*6.-3.)-1.,0.,1.)-1.)*s+1.)*v;}

vec2 rot(vec2 p, float a){
    return vec2(p.x*cos(a)-p.y*sin(a), p.x*sin(a)+p.y*cos(a));
}

vec3 rep(vec3 p, float a){
    return mod(p-.5*a,a)-.5*a;
}

float dBox(vec3 p){
    vec3 d = abs(rep(p,2.))-.15;
    return length(max(d,0.0));
}

float indexer(float i){
    return floor(8192.*i)/8.;
}

vec2 map(vec3 p){
    vec3 q=p;
    // use IFS
    // https://qiita.com/kaneta1992/items/21149c78159bd27e0860
    for(int i=0;i<10;++i){
        q=abs(q)-1.3;
        q.xy=rot(q.xy,4.9);
        q.xz=rot(q.xz,-1.75);
    }
    float index = floor(indexer(q.x)+indexer(q.y)+indexer(q.z))/10.;
    q.xy=rot(q.xy,iTime);
    q.yz=rot(q.yz,iTime);
    return vec2(dBox(q),index);
}

vec3 normal(vec3 p){
    vec2 xy=vec2(EPS,.0);
    return normalize(vec3(
        map(p+xy.xyy).x-map(p-xy.xyy).x,
        map(p+xy.yxy).x-map(p-xy.yxy).x,
        map(p+xy.yyx).x-map(p-xy.yyx).x
    ));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.xy;
    vec3 ro=vec3(0.,.0,-10.+mod(iTime,64.)*abs(sin(iTime/64.))), rd=normalize(vec3(p,1.8)), N;
    ro.xy=rot(ro.xy, PI/2.+PI);
    ro.xz=rot(ro.xz, PI/2.);
    vec3 ray=ro;
    vec2 d=vec2(EPS);
    for(int i=0;i<32;++i){
        d=map(ray);
        if(d.x<EPS){
            N=normal(ray);
            break;   
        }
        ray+=d.x*rd;
    }

    vec3 L=normalize(vec3(-1.));
    float hl=.5+.5*max(0.,dot(N,L));
    fragColor = vec4(hsv(EPS*d.y,.98,1.4)/ray.z+.88*hl, 1.);
}

