float maxcomp(in vec3 p ) { return max(p.x,max(p.y,p.z));}
float sdBox( vec3 p, vec3 b )
{
  vec3  di = abs(p) - b;
  float mc = maxcomp(di);
  return min(mc,length(max(di,0.0)));
}

const mat3 ma = mat3( 0.60, 0.00,  0.80,
                      0.00, 1.00,  0.00,
                     -0.20, 0.00,  0.30 );

vec4 map( in vec3 p )
{
    float d = sdBox(p,vec3(1.0));
    vec4 res = vec4( d, 1.0, 0.0, 0.0 );

    //float ani = smoothstep( -0.2, 0.2, -cos(0.5*iTime) );
    //float ani =  -10.0*cos(0.5*iTime );
    float ani;
    ani=1.0;

	float off = 1.5*sin( 0.01*iTime );
    //off=0.005*iTime;
    off=0.0005;

    float s = 1.0;
    for( int m=0; m<4; m++ )
    {
        p = mix( p, ma*(p+off), ani );

        vec3 a = mod( p*s, 2.0 )-1.0;
        s *= 3.0;
        vec3 r = abs(1.0 - 3.0*abs(a));
        float da = max(r.x,r.y);
        float db = max(r.y,r.z);
        float dc = max(r.z,r.x);
        float c = (min(da,min(db,dc))-1.0)/s;

        if( c>d )
        {
          d = c;
          res = vec4( d, min(res.y,0.3*da*db*dc), (1.0+float(m))/4.0, 0.0 );
        }
    }

    return res;
}

vec4 intersect( in vec3 ro, in vec3 rd )
{
    float t = 0.0;
    vec4 res = vec4(-1.0,-1.1,1.0,1.0);
	vec4 h = vec4(1.0);
    for( int i=0; i<64; i++ )
    {
		if( h.x<0.002 || t>15.0 ) break;
        h = map(ro + rd*t);
        res = vec4(t,h.yzw);
        t += h.x;
    }
	if( t>10.0 ) res=vec4(-1.0);
    return res;
}

float softshadow( in vec3 ro, in vec3 rd, float mint, float k )
{
    float res = 1.0;
    float t = mint;
	float h = 1.0;
    for( int i=0; i<32; i++ )
    {
        h = map(ro + rd*t).x;
        res = min( res, k*h/t );
		t += clamp( h, 0.005, 0.1 );
    }
    return clamp(res,0.0,1.0);
}

vec3 calcNormal(in vec3 pos)
{
    vec3  eps = vec3(.001,0.0,0.0);
    vec3 nor;
    nor.x = map(pos+eps.xyy).x - map(pos-eps.xyy).x;
    nor.y = map(pos+eps.yxy).x - map(pos-eps.yxy).x;
    nor.z = map(pos+eps.yyx).x - map(pos-eps.yyx).x;
    return normalize(nor);
}

// light
vec3 light = normalize(vec3(1.0,0.9,0.3));

vec3 render( in vec3 ro, in vec3 rd )
{
    // background color
    vec3 col = mix( vec3(0.3,0.2,0.1)*0.5, vec3(0.7, 0.9, 1.0), 0.5 + 0.5*rd.y );

    vec4 tmat = intersect(ro,rd);
    if( tmat.x>0.0 )
    {
        vec3  pos = ro + tmat.x*rd;
        vec3  nor = calcNormal(pos);

        float occ = tmat.y;
		float sha = softshadow( pos, light, 0.01, 64.0 );

		float dif = max(0.1 + 0.9*dot(nor,light),0.0);
		float sky = 0.5 + 0.5*nor.y;
        float bac = max(0.4 + 0.6*dot(nor,vec3(-light.x,light.y,-light.z)),0.0);

        vec3 lin  = vec3(0.0);
        lin += 1.00*dif*vec3(1.10,0.85,0.60)*sha;
        lin += 0.50*sky*vec3(0.10,0.20,0.40)*occ;
        lin += 0.10*bac*vec3(1.00,1.00,1.00)*(0.5+0.5*occ);
        //lin += 0.25*occ*vec3(0.15,0.17,0.20);	 

        /*vec3 matcol = vec3(
            0.5+0.5*cos(0.0+2.0*tmat.z),
            0.5+0.5*cos(1.0+2.0*tmat.z),
            0.5+0.5*cos(2.0+2.0*tmat.z) );
        col = matcol * lin;*/
        col=lin;
    }

    return pow( col, vec3(0.4545) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    float ctime = iTime;
    // camera
    //vec3 ro = 1.1*vec3(2.5*sin(0.25*ctime),1.0+1.0*cos(ctime*.13),2.5*cos(0.25*ctime));
    vec3 ro = vec3(0.55*sin(0.25*ctime),0.5+1.0*cos(ctime*.13),1.5+cos(0.25*ctime));
    vec3 ww = normalize(vec3(0.0) - ro);
    vec3 uu = normalize(cross( vec3(0.0,1.0,0.0), ww ));
    vec3 vv = normalize(cross(ww,uu));
    vec3 rd = normalize( p.x*uu + p.y*vv + 2.5*ww );

    vec3 col = render( ro, rd );

    fragColor = vec4(col,1.0);
}


void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir )
{
    float time = iTime*0.25 + 0.01*iMouse.x;
    float anim = 1.1 + 0.5*smoothstep( -0.3, 0.3, cos(0.1*iTime) );

    vec3 col = render( fragRayOri + vec3(0.0,1.0,2.5), fragRayDir );
    fragColor = vec4( col, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)-0.5;
    vec2 uv2 = uv;
    vec2 uv3;
    float stalk;
    float petal;
    float center;
    float clouds;
    uv.x *= iResolution.x/iResolution.y;
	uv *= 5.;
    uv3 = uv;

    uv3 -= vec2(0.2, 0.0);
    if(uv2.y < 0.15)
   		stalk = 1.- smoothstep(0.80, 1.0, abs((uv3.x + sin(uv3.y * 0.5)) * 20.));

    uv -= vec2(-0.2, 0.8);
    float r = cos(atan(uv.y, uv.x) * 7.) * .6 + 0.1;
    petal = smoothstep(r-0.05, r, 1.-length(uv));

    center = smoothstep(0.8 - 0.01, 0.8, 1.-length(uv));

    vec2 c0 = uv3;
    c0.x += iTime;
    c0 = mod(c0, vec2(8., 4.)) - vec2(1.8, 1.4);

    if(uv2.y > 0.05)
    {
    	r = (sin(c0.x * 8.) * 0.8 + 9.);
    	c0 *= 5.;
    	clouds += 1.-smoothstep(r-0.1, r, length(vec2(c0.x, c0.y * 3.)));
    }


    // mix layer together
    vec3 col = vec3(0.0, 0.6, 1.0);
    col = mix(col, vec3(1.0, 1.0, 1.0), clouds);
    col = mix(col, vec3(0.2, 0.6, 0.2), stalk);
    col = mix(col, vec3(1.0, 0.4, 0.4), petal);
    col = mix(col, vec3(1.0, 1.0, 0.4), center);

    fragColor = vec4(col, 1.0);
}

/*
Shader coded live on twitch (https://www.twitch.tv/nusan_fx)
The shader was made using Bonzomatic.
You can find the original shader here: http://lezanu.fr/LiveCode/SableBat.glsl
*/

float time;
float pi=acos(-1.0);

mat2 rot(float a) {
  float ca=cos(a);
  float sa=sin(a);
  return mat2(ca,sa,-sa,ca);  
}

float smin(float a, float b, float h) {
  float k = clamp((a-b)/h*.5+.5,0.0,1.0);
  return mix(a,b,k) - k * (1.0-k) * h;
}

float wings(vec3 p) {

  p.x = abs(p.x);

  float flop = pow(max(0.0,sin(time*4.0 - p.x*0.15)*0.8+0.2),10.0);
  p.xz *= rot(clamp(p.x,0.0,2.0) * 0.3 * flop);

  float ang = p.x + p.y*0.5;
  float decal = -2.0;
  p.y -= decal;
  p.xy *= rot(-0.2 * clamp(ang*0.9-ang*ang*0.3, -5.0, 2.0));
  p.y += decal;

  vec3 p2 = p;
  float size = 0.4;
  p2.x = (fract(p2.x/size+0.5)-0.5)*size;
  p2.y -= clamp(p2.y,-2.0 + cos(p.x*0.5)*1.5,0.0);
  float d = max(length(p2)-0.2, abs(p.x)-5.0);

  p.xz *= rot(p.x * 0.012);  

  vec3 p3 = p;
  float size2 = 0.3;
  p3.x = (fract(p3.x/size2+0.5)-0.5)*size2;
  p3.y -= clamp(p3.y,-1.0 + cos(p.x*0.5)*0.8,-0.5);
  p3.z =abs(p.z+0.1)-0.15;
  d = smin(d, max(length(p3)-0.17, abs(p.x)-4.2), 0.2);
  d = smin(d, p.z+0.16, -0.12);


  d*=0.5;

  return d;

}

float body(float d, vec3 p) {

  vec3 bp = p;
  vec3 bp2 = p;

  p.y-=0.4;
  p.zy *= rot(abs(p.y)*0.2);

  vec3 p2 = p;

  p2.y -= clamp(p2.y,-1.0,2.0);

  d = smin(d, length(p2)-0.5 + p.y*0.19, 0.8);
  d = smin(d, length(bp+vec3(0,1.3,-0.9))-0.9, 0.2);
  //bp.x=abs(bp.x)-0.4;
  bp.x=smin(bp.x,-bp.x, 0.2)+0.4;
  bp.xy *= rot(bp.x*0.5);
  d = smin(d, -(length(bp+vec3(0,1.3,-1.4))-0.12), -0.6);
  d = smin(d, -(length(bp+vec3(-0.17,0.7,-1.4))-0.1), -0.2);

  bp2.x = abs(bp2.x);
  bp2.xz *= rot(-bp2.x*0.3 + bp2.y*0.14);
  bp2.yz *= rot(-0.6);
  bp2 -= vec3(0.1,0.0,-1.2);
  float hear = length(bp2 + vec3(-0.5,2.5,-1.2))-0.6;
  hear = smin(hear, -(length(bp2 + vec3(-0.5,2.5,-1.6))-0.6), -0.3);
  hear = smin(hear, -(length(bp2 + vec3(-0.9,2.1,-1.1))-0.1), -0.4);

  d=smin(d, hear, 0.17);

  return d;
}

float claws(float d, vec3 p) {

  p.x=-abs(p.x);
  vec3 bp=p;

  p += vec3(0.5,-2.0,0);

  p.zy *= rot(sin(time*4.0+1.0)*0.5+0.3);
  p.y -= 0.5;

  p.x=-abs(p.x);
  p.xz *= rot(0.4);
  p.x=-abs(p.x);
  p.xz *= rot(0.2);

  float donut = length(vec2(length(p.zy)-0.6, p.x)) - 0.2;
  donut = smin(donut, -(length(p-vec3(0,1,-0.4))-0.7), -1.4 + clamp(-bp.x*0.5,0.0,1.0)-0.4);
  bp.xy *= rot(0.15);
  donut = smin(donut, max(length(bp.xz+vec2(0.2,0.0))-0.12, abs(bp.y-1.0)-1.0), 0.2);
  d = smin(d, donut, 0.2);

  return d;

}

float noise(vec2 p) {
  vec2 ip=floor(p);
  p=fract(p);
  p=smoothstep(0.0,1.0,p);
  vec2 st=vec2(37,133);
  vec4 val = fract(sin(dot(ip, st) + vec4(0,st.x, st.y, st.x+st.y))*2375.655);
  vec2 v = mix(val.xz,val.yw, p.x);
  return mix(v.x, v.y, p.y);
}

float mat = 0.0;
float map(vec3 p) {

  vec3 tp = p;
  tp.z += time*10.0;
  tp.y += sin(time*0.7)*5.0;
  float f = noise(tp.xz*0.08)*0.5;
  f += noise(tp.xz*0.16)*0.25;
  f += noise(tp.xz*0.32)*0.125;
  f = abs(f-0.5);
  float d2 = 8.0-tp.y + f*13.0;
  d2 *= 0.7;  


  p.yx *= rot(sin(time*0.7) * 0.3);
  p.yz *= rot(-0.7 + sin(time) * 0.3);


  p.y += sin(time*4.0 + 2.5 + abs(p.x)*0.4)*0.3;

  float d = wings(p);

  d = body(d, p);

  d = claws(d, p);


  d = min(d, d2);

  mat = abs(d-d2)<0.01?1.0:0.0;

  return d;
}

void cam(inout vec3 p) {

  p.yz *= rot(sin(time)*0.2+0.2);
  p.xz *= rot(2.7 + time*0.4);
  p.yz *= rot(sin(time)*0.1-0.1);

}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  // We mod the time to make it repeat, because as we scroll the ground, the lack of precision start to be visible
  time = mod(iTime, 50.0);

  vec3 s=vec3(0,0,-18);
  vec3 r=normalize(vec3(-uv, 1));

  cam(s);
  cam(r);

  vec3 p=s;
  float i=0.0;
  float dd=0.0;
  for(i=0.0; i<100.0; ++i) {
    float d=map(p);
    if(d<0.001) {
      i += d/0.001;
      break;
    }
    if(dd>100.0) {
      dd=100.0;
      break;
    }
    p+=r*d;
    dd+=d;
  }

  float curmat=mat;

  vec2 off=vec2(0.01,0);
  vec3 n = normalize(map(p)-vec3(map(p-off.xyy), map(p-off.yxy), map(p-off.yyx)));

  float itfog = pow((1.0-i/100.0)*1.1,3.0);
  float fog = 1.0-clamp(dd/100.0,0.0,1.0);

  vec3 l=normalize(-vec3(1,3,-2));
  vec3 h=normalize(l-r);
  float fre=pow(1.0-abs(dot(n,r)),3.0);

  float ao = clamp(map(p+n*0.5)/0.5,0.0,1.0);

  vec3 col = vec3(0);
  vec3 diff = mix(vec3(0.4,0.5,0.9)*2.0, vec3(0.9,0.6,0.5)*2.0, curmat);
  col += max(0.0, dot(n,l)) * fog * ao * (diff +  2.0*pow(max(0.0,dot(h,n)), 10.0)*(1.0-curmat));
  col += (n.y * 0.5 + 0.5) * vec3(1,0.5,0.3) * 0.8 * fog;
  col += fre * vec3(1,0.7,0.5) * 3.0 * fog * ao * (-n.y*.5+.5) * (1.0-curmat*0.6);

  vec3 rr = normalize(p);
  vec2 skyuv = vec2(abs(atan(rr.x,rr.z)), rr.y);
  float ff = (-noise(skyuv*vec2(4,30))*0.1-noise(skyuv*vec2(6,19)*2.0)*0.05) * 0.33;
  vec3 sky = mix(vec3(1,0.7,0.5)*0.3, vec3(1.0,0.2,0.2)*3.0, pow(max(0.0,r.z), 80.0)) * pow(max(0.0,-r.y*0.5+1.2 + ff),5.0);
  col += sky * pow((1.0-fog)*1.2, 3.0);

  fragColor = vec4(col, 1);
}

#define squv *= iResolution.x/iResolution.y;
#define PI 3.14159
#define pi PI
#define twopi 6.2831854

mat2x2 rot( float d ) {
    return mat2x2 ( cos(d), sin(d),
                    -sin(d), cos(d) );
}

float map( float a, float a1, float a2, float b1, float b2 ) {
    return (a - a1) / (a2 - a1) * (b2 - b1) + b1;
}

float totalAtan( float y, float x ) {
    float v = 0.;

    if(x>0.)v=atan(y/x);
    if(y>=0. && x<0.)v=pi+atan(y/x);
    if(y<0. && x<0.)v=-pi+atan(y/x);
    if(y>0. && x==0.)v=pi/2.;
    if(y<0. && x==0.)v=-pi/2.;
    if (v<0.) v=v+2.*pi;

    return v;
}

float dsemicircle( vec2 p, vec2 o, float r, float nr, float xr ) {

    //float rcos = dot(normalize(p-o), vec2(1.,0.));

    //nr += 0.01;
    //xr += 0.01;
    if(nr < 0.) {
        float abnr = abs(nr);
        float ffnr = floor( abnr / ( twopi ) ) + 1.;
        float addition = ffnr * twopi;
        nr += addition;
    }
    //nr += pi;
    nr = mod(nr, twopi);

    //nr %= 2.*PI;
    //xr %= 2.*PI;
    xr = clamp(xr, 0., twopi);


    float rd = totalAtan(p.y-o.y, p.x-o.x);
    float dr = 0.;
    float xnr = xr+nr;

    bool isContained = false;

    if(rd >= nr && rd <= xnr) { isContained = true; }
    if(xnr >= twopi) {
        dr = xnr - twopi;
        if(rd+0.001 <= dr) { isContained = true; }
    }
    if(!isContained) {
        if(rd < xnr) {
        float rav = (dr+nr)/2.;
        if(dr == 0.) dr = xnr;
        //float chr = mix(dr, nr, step(rav, rd));
        float chr = rd >= rav ? nr : 
            rd-xnr+twopi >= nr-rd ? xnr : nr;
        rd = chr;
        } else {
            float rav = (twopi+xnr)/2.;
            float chr = rd >= rav ? nr : xnr;
            rd = chr;
        }
    }

    mat2x2 ro = rot(rd);

    vec2 rr = ro * vec2(r, 0.);
    vec2 pr = rr + o;

    return distance(pr, p);
    //return rd;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mo = iMouse.xy / iResolution.xy;

    mo.x squv uv.x squv

    // Time varying pixel color
    ///vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(pow(sin(dsemicircle( uv, vec2(0.5), distance(mo, vec2(0.5)), iTime, PI)*64.+iTime)/2.+0.5, 0.07));


    //vec3 col = vec3(dsemicircle(uv, vec2(0.5), distance(mo, vec2(0.5)), 0.)/pi/2.);
    // Output to screen
    fragColor = vec4(col,1.0);
}

//
// Based on "Flame in the Wind" by kuvkar (https://www.shadertoy.com/view/4tXXRn)
//

float GetNoise(vec2 uv) // -> (0.25, 0.75)
{
    float n = (texture(iChannel0, uv).r - 0.5) * 0.5;
    n += (texture(iChannel0, uv * 2.0).r - 0.5) * 0.5 * 0.5;

    return n + 0.5;
}

mat2 GetRotationMatrix(float angle)
{
    mat2 m;
    m[0][0] = cos(angle); m[0][1] = -sin(angle);
    m[1][0] = sin(angle); m[1][1] = cos(angle);

    return m;
}


#define paramWindSpeedMagnitude -80.0
#define flamePersonalitySeed 1000.0

// -----------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5); // (x=[-0.5, 0.5], y=[-0.5, 0.5])
    vec2 flameSpacePosition = uv + vec2(0.0, 0.5); // (x=[-0.5, 0.5], y=[0.0, 1.0])
    uv.y /= (iResolution.x / iResolution.y); // obey aspect ratio

    float paramTime = iTime;

    ////////////////////////////////////////

    //
    // Flame time
    //

    #define FlameSpeed 0.23
    float flameTime = paramTime * FlameSpeed;

    //
    // Apply wind
    //

    // Rotation angle
    float windAngle = -sign(paramWindSpeedMagnitude) * 0.4 * smoothstep(0.0, 100.0, abs(paramWindSpeedMagnitude));

    // Rotation angle is higher the higher we go
    windAngle *= flameSpacePosition.y;

    // Make rotation angle time-dependent
    windAngle *= (1.0 + sin(iTime)) / 2.0;

    // Rotate around bottom
    uv = GetRotationMatrix(windAngle) * (uv + vec2(0.0, 0.5)) - vec2(0.0, 0.5);


    //
    // Get noise for this fragment and time
    //

    #define NoiseResolution 0.7
    float fragmentNoise = GetNoise(uv * NoiseResolution + vec2(flamePersonalitySeed)/777.0 + vec2(0.0, -flameTime/0.8));


    //
    // Rotate fragment based on noise and vertical extent
    //

    float angle = (fragmentNoise - 0.5);

    // Magnify rotation amount based on distance from center of quad
    angle /= max(0.1, length(uv));

    // Straighten the flame at the bottom and make full turbulence higher up
    angle *= smoothstep(-0.1, 0.5, flameSpacePosition.y);

    // Smooth the angle
    angle *= 0.45;

    // Rotate!
    uv += GetRotationMatrix(angle) * uv;    



    //
    // Calculate thickness
    //

    #define FlameWidth 0.5
    float thickness = 1.0 - smoothstep(0.1, FlameWidth, abs(uv.x));

    // Taper flame depending on randomized height
    float variationH = fragmentNoise * 1.4;
    thickness *= smoothstep(1.3, variationH * 0.5, flameSpacePosition.y); // Taper up
    thickness *= smoothstep(-0.15, 0.15, flameSpacePosition.y); // Taper down

    // Focus (less halo, larger body)
    #define FlameFocus 2.0
    thickness = pow(clamp(thickness, 0.0, 3.0), FlameFocus);


    //
    // Emit
    //

    vec3 col1 = mix(vec3(1.0, 1.0, 0.6), vec3(1.0, 1.0, 1.0), thickness);
    col1 = mix(vec3(1.0, 0.4, 0.1), col1, smoothstep(0.3, 0.8, thickness));    

    // Blend with background
    float alpha = smoothstep(0.0, 0.5, thickness);
    fragColor = mix(vec4(1.0), vec4(col1, 1.0), alpha);
}

