float sphereDist(vec3 p, float r){
    vec3 q = fract(p)*2.0-1.0;
    return length(q)-r;
}

float marchRay(vec3 o, vec3 r){
 	float t = 0.0;
    for(int i = 0; i < 512; i++){
		vec3 p = o+r*t;
        float d = sphereDist(p, 0.25f);
        t += d*0.25;
    }
    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
