mat3 camera(vec3 ro, vec3 ta)
{
    vec3 up = normalize(vec3(0, 1, 0));
    vec3 cw = normalize(ta - ro);
    vec3 cu = normalize(cross(cw, up));
    vec3 cv = normalize(cross(cu, cw));
    return mat3(cu, cv, cw);
}

vec3 tex3D(sampler2D tex, vec3 p, vec3 n)
{
    vec3 blending = abs(n);
    blending = normalize(max(blending, 0.00001));

    // normalized total value to 1.0
    float b = (blending.x + blending.y + blending.z);
    blending /= b;

    vec4 xaxis = texture(tex, p.yz);
    vec4 yaxis = texture(tex, p.xz);
    vec4 zaxis = texture(tex, p.xy);

    // blend the results of the 3 planar projections.
    return (xaxis * blending.x + yaxis * blending.y + zaxis * blending.z).rgb;
}

float sdBox(vec3 p, vec3 b)
{
    return length(max(abs(p) - b, 0.0));
}

float sdSphere(vec3 p, vec3 offs, float r)
{
    return length(p - offs) - r;
}

float map(vec3 p)
{
    float d1 = sdSphere(p, vec3(-2, -1, 0), 1.0);
    float d2 = sdBox(p, vec3(0.5));
    return min(d1, d2);
}

vec3 normal(vec3 p)
{
    vec2 e = vec2(0.0001, 0.0);
    float d = map(p);
    vec3 n = d - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx));
    return normalize(n);
}

