#define S( a, b, t) smoothstep( a, b, t)
#define sat(x) clamp(x, 0.0, 1.0)

float remap01(float a, float b, float t){
	return sat((t-a)/(b-a));//clamp to 0-1;
}
float remap(float a, float b, float c, float d, float t){
	return remap01( a, b, t)*(d-c) + c;
}

vec2 within(vec2 uv, vec4 rect){

    return (uv - rect.xy)/(rect.zw - rect.xy);//2d projection function;


}

vec4 Eye(vec2 uv,float side, vec2 m, float smile){
    uv -= .5;
    uv.x *= side;
    float d = length(uv);

    vec4 irisCol = vec4(.3,.5,1.,1.);
    vec4 col = mix(vec4(1.),irisCol, S(.1,.7,d));
    //can add sat function to clamp brighter area;
    col.a = S(0.5, 0.48, d);
    col.rbg *= 1.0 - S(0.45,0.5,d)*.5*(-uv.y -uv.x*side);//eye outline

    d = length(uv - m*.5);

    col.rgb = mix(col.rgb, vec3(0.0), S(0.3,0.28,d));//iris outline

    irisCol.rgb *= 1.0 + S( 0.25, 0.05, d);//make irisColor brighter
    float irisMask = S(0.28,0.25,d);
    col.rgb = mix(col.rgb, irisCol.rgb, irisMask);
    d = length(uv - m*.6);

    float pupilSize = mix(.4,.16, smile);//mix also use to coutrol float
    float pupilMask = S(pupilSize,pupilSize*.85,d);
    pupilMask *= irisMask;//cut out pupil's outside area
    col.rgb = mix(col.rgb, vec3(0.0), pupilMask);//pupil

    //eye highlight
    float t = iTime*3.;
    vec2 offs = vec2( sin(t + uv.y*25.), sin(t + uv.x*25.));
    offs *= .01*(1. - smile);//depend ux.x change the light shape
    uv += offs;
    float highlight = S(.1, .09, length(uv - (- 0.15, 0.15)));
    highlight += S(.07, .05, length(uv - (0.08,-0.08)));
    col.rgb = mix(col.rgb, vec3(1.), highlight);




    return col;
}
vec4 Brow(vec2 uv, float smile){
    float offs = mix(.2, 0.,smile);
    uv.y += offs;

	float y = uv.y;
    uv.y += uv.x*mix(.5,.8,smile)- mix(.1,.3,smile); //brow move
    uv.x -= mix(0.,.1,smile);
    uv -= .5;

    vec4 col = vec4(0.);

    float blur = .1;

    float d1 = length(uv);
    float s1 = S(.45,.45 - blur, d1);
    float d2 = length(uv - vec2(.1, - .2)*.7);//*.7 get biger cricle
    float s2 = S(.5,.5-blur,d2);

    float browMask = sat(s1-s2);//clamp to 0 1

    float colMask = remap01(.7,.8,y)*.7;//get a gradient
    colMask *= S(.6, 1., browMask);
    colMask *= smile;
   	vec4 browCol = mix(vec4(.4,.1,.2,sat((browMask+4.)*browMask-0.8)), vec4(.9,.7,.5,1),colMask);
    col = browCol;

    //shadow
    uv.y += .15 - offs*.35;//let shadow'position a little bit down
    blur += mix(0.,.1,smile);


    d1 = length(uv);
    s1 = S(.45,.45 - blur, d1);
    d2 = length(uv - vec2(.1, - .2)*.7);//*.7 get biger cricle
    s2 = S(.5,.5-blur,d2);
    float shadowMask = sat(s1-s2);

    col = mix(col*1.2, vec4(0., 0., 0.,S(0., 1.,shadowMask)*.5),1.-browMask);




    return col;
}

vec4 Mouth(vec2 uv, float smile){
    uv -= .5;
    uv.y *= 1.5;
	vec4 col = vec4 (.5, .18, .05, 1.);

    uv.y -= uv.x*uv.x*2.*smile;//as uv.x biger  y move up more;

    uv.x *= mix(2.5, 1. ,smile);
    float d = length(uv);
    col.a = S( .5, .49, d );

    //teeth
    vec2 tUv = uv;
    tUv.y += (abs(tUv.x)*.7 + .1)*(1.-smile);
    float td = length(tUv - vec2( 0. , .6));//move tooth to point;
    vec3 toothCol = vec3(1.)*S(.6,0.35,d);//tooth shadow
    col.rgb = mix(col.rgb,toothCol,S(0.4,0.37,td));

    td = length(uv - vec2(0.,- .5));
    col.rgb = mix(col.rbg, vec3(1.,0.5, 0.5) ,S(.5, .2, td));
    return col;
}

vec4 Head(vec2 uv){
	vec4 col = vec4 (0.8,0.6,0.4,1.0);

    float d = length(uv);

    col.a = S(.5 , 0.49, d);

    float edgeShade = remap01( 0.35, 0.5,d );
    edgeShade *= edgeShade;
    col.rgb *= 1.0 - edgeShade*0.5;//col is vec4;

    //make a outline;
    col.rgb = mix(col.rgb, vec3(0.6, 0.3, 0.1),S(0.47,0.48,d));

    //highlight
    float highlight_alpha = S( 0.41, 0.405, d);
    highlight_alpha *= remap(0.41, -0.2 , 0.75, 0.0, uv.y);
    highlight_alpha *= S(.18,.19,length(uv - vec2(.2,.075)));
    //remap(highest point,lowest point,max return value,min return value,input)
    col.rbg = mix(col.rgb, vec3(1.0),highlight_alpha);



    d = length(uv-vec2(0.25,-0.2));//vec2 is create position;
    float cheek_alpha = S(.2,.01,d)*0.4;
    cheek_alpha *= S(0.17,0.16,d);//let cheek have little shape
    col.rgb = mix(col.rgb,vec3(1.,.1,.1),cheek_alpha);

    return col;
}

vec4 Smiley(vec2 uv,vec2 m, float smile){
	vec4 col = vec4 (0.0);
    float side = sign(uv.x);//sign, if x>0 return1,x=0 return 0,x<0 return -1;
    uv.x = abs(uv.x); //mirror  

    vec4 head = Head(uv);
    //vec4 eye = Eye(uv);
    vec4 eye = Eye(within(uv, vec4(0.03,-.1,.37,.25)),side, m, smile);
    vec4 mouth = Mouth(within(uv, vec4(-0.3,-0.4,.3,-0.1)), smile);//project mouth rect
    //within( minX, minY, maxX, maxY)
    vec4 brow = Brow(within(uv, vec4(0.02,0.17,.4,0.43)),smile);

    col = mix(col , head , head.a);//blend colors as  head's alpha;
    col = mix(col , eye, eye.a);
    col = mix(col , mouth, mouth.a);
    col = mix(col , brow, brow.a);
    return col;
}


