// noise function taken from https://www.shadertoy.com/view/4sdGD8

lowp vec3 permute(in lowp vec3 x) { return mod( x*x*34.+x, 289.); }
lowp float snoise(in lowp vec2 v) {
  lowp vec2 i = floor((v.x+v.y)*.36602540378443 + v),
      x0 = (i.x+i.y)*.211324865405187 + v - i;
  lowp float s = step(x0.x,x0.y);
  lowp vec2 j = vec2(1.0-s,s),
      x1 = x0 - j + .211324865405187, 
      x3 = x0 - .577350269189626; 
  i = mod(i,289.);
  lowp vec3 p = permute( permute( i.y + vec3(0, j.y, 1 ))+ i.x + vec3(0, j.x, 1 )   ),
       m = max( .5 - vec3(dot(x0,x0), dot(x1,x1), dot(x3,x3)), 0.),
       x = fract(p * .024390243902439) * 2. - 1.,
       h = abs(x) - .5,
      a0 = x - floor(x + .5);
  return .5 + 65. * dot( pow(m,vec3(4.))*(- 0.85373472095314*( a0*a0 + h*h )+1.79284291400159 ), a0 * vec3(x0.x,x1.x,x3.x) + h * vec3(x0.y,x1.y,x3.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // moving light
	//vec3 lightSource = vec3(0.5+5.0*(cos(time)), 5.0, 10.0 + 2.0*(sin(time)));

    // startionary light
    vec3 lightSource = vec3(0.5, 5.0, 10.0);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord / iResolution.xy) - 0.5;


    // generatying rays to cast from camera based on coordinates
    		// z serves to modify field of view
    		// y moves to look up and to horizon
    vec3 rayDir = normalize( vec3(uv.x, uv.y + sin(time/4.0)+1.0, 5));



    vec3 color = vec3(0, 0.3, 0.5);
    bool brokeSurface = false;
    for (float i = 1.0; i < 100.0; i+=1.0){
        float d = distance(i * rayDir, lightSource);

			color += lightIntensity 
                * pow(2.7, -1.0 * pow(d,1.3) * density) //diffuse based on distance to light
                * pow(2.7, -1.0 * i * density) //diffuse based on distance to camera (0,0,0)
                //stationary noise
                //* pow(snoise(rayDir.xz*50.0), 3.0);
        		// shifting noise
				* pow(snoise(rayDir.xz*i + time/4.0), 3.0);

        if (!brokeSurface && (i * rayDir).y > surface){
            brokeSurface = true;
        	color.y += snoise(rayDir.xz*i + time/4.0)* lightIntensity*10.0* pow(2.7, -1.0 * i * density) ;
        	i = 40.0;
        }

    }

	color *= pow(dot(rayDir, normalize(lightSource))-0.2, 6.0);

    // Output to screen
    fragColor = vec4(color,1.0);
}

