// A non polynomial smoothstep() function that I found has a simple
// analytic inverse.
//
// In yellow, the inverse_smoothstep. In gray, the smoothstep of the
// inverse_smoothstep, which gives the identity. In blue, the smoothstep for 
// reference and symmetry of the image.


float my_smoothstep( float x, float n )
{
    return pow(x,n) / (pow(x,n) + pow(1.0-x,n) );

    // single pow() optimization, if x is not 0.0
    //
    // return 1.0/(1.0+pow(1.0/x-1.0,n) );
}

float inverse_smoothstep( float x, float n )
{
    // nicely enough, the inverse is the function
    // itself with the parameter inverted.
    return my_smoothstep(x,1.0/n); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	
    float dx = 1.0/iResolution.y;
    vec2  p = ( vec2((iResolution.y-iResolution.x)/2.0,0.0)+fragCoord)/iResolution.y;

    vec3 col = vec3(0.0);

    float n = 1.0 + 5.0*pow(0.5 +0.5*sin(0.5*iTime),2.0);

    if( p.x>0.0 && p.x<1.0 )
    {
        // background
        col = vec3( 0.2 + 0.02*mod(floor(p.x*10.0)+floor(p.y*10.0),2.0) );

        // gray: smoothstep of inverse_smoothstep = identity
        {
        float y = my_smoothstep( inverse_smoothstep( p.x, n ), n );
        float di = abs(p.y-y)/sqrt(2.0)/dx;
        col = mix( col, vec3(0.5,0.5,0.5), 1.0-smoothstep(0.0, 1.5, di ) );
        }

        // blue: smoothstep
        {
        float y = my_smoothstep( p.x, n );
        float dy = my_smoothstep(p.x-sign(p.x-0.5)*dx, n) - y; // distance estimation
        float di = abs(p.y-y)/sqrt(dx*dx+dy*dy);            // http://iquilezles.org/www/articles/distance/distance.htm
        col = mix( col, vec3(0.0,0.4,0.8), 1.0-smoothstep( 0.0, 2.0, di ) );
        }

        // yellow: inverse_smoothstep
        {
        float y = inverse_smoothstep( p.x, n );
        float dy = inverse_smoothstep(p.x-sign(p.x-0.5)*dx, n) - y; // distance estimation
        float di = abs(p.y-y)/sqrt(dx*dx+dy*dy);                 // http://iquilezles.org/www/articles/distance/distance.htm
        col = mix( col, vec3(1.0,0.7,0.0), 1.0-smoothstep( 0.0, 2.0, di ) );
        }
    }

    fragColor = vec4( col, 1.0 );
}

