/// smooth mix
float smoothmix( float val1, float val2, float x){
	x= clamp(x,0.0,1.0);
	return mix( val1, val2, -(2.*x*x*x - 3.*x*x) );
}

/// rand 2d
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

/// classic noise 2d
float noise(vec2 p){
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

/// rand 1D
///@todo implementit it right, don't just use 2D, it's overkill
float rand(float a){
	return rand( vec2(a) );
}

/// classic noise 3d
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
float noise(vec3 p){
	vec3 a = floor(p);
	vec3 d = p - a;
	d = d * d * (3.0 - 2.0 * d);

	vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
	vec4 k1 = permute(b.xyxy);
	vec4 k2 = permute(k1.xyxy + b.zzww);

	vec4 c = k2 + a.zzzz;
	vec4 k3 = permute(c);
	vec4 k4 = permute(c + 1.0);

	vec4 o1 = fract(k3 * (1.0 / 41.0));
	vec4 o2 = fract(k4 * (1.0 / 41.0));

	vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
	vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

	return o4.y * d.y + o4.x * (1.0 - d.y);
}

/// FBM (perlin noise) 2D
///@warning require noise
#define FBM_NUM_OCTAVES 5
float fbm(vec2 x) {
	float v = 0.0;
	float a = 0.5;
	vec2 shift = vec2(100);
	// Rotate to reduce axial bias
	mat2 rot = mat2(cos(0.5), sin(0.5), -sin(0.5), cos(0.50));
	for (int i = 0; i < FBM_NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = rot * x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}

/// FBM (perlin noise) 2D
/// return value from 0.0 to 1.0
///@warning require noise
float fbm(vec3 x) {
	float v = 0.0;
	float a = 0.5;
	vec3 shift = vec3(100);
	for (int i = 0; i < FBM_NUM_OCTAVES; ++i) {
		v += a * noise(x);
		x = x * 2.0 + shift;
		a *= 0.5;
	}
	return v;
}




float max3(float a, float b, float c){return max(max(a,b),c);}
float min3(float a, float b, float c){return min(min(a,b),c);}



float mulPower( float power, float value ){ return 1.0 + power * (clamp(value,0.0,1.0) - 1.0); }



vec2 rot( vec2 p, float l ){
	float s=sin(l),c=cos(l);
	return vec2(
		c*p.x - s*p.y,
		s*p.x + c*p.y
	);
}



float cube(vec3 p, vec3 size){
	p= abs(p) - size;
	return min( 0.0, max3( p.x, p.y, p.z ) ) + max( 0.0, length( max( p, 0.0 ) ) );
}


float cylinder(vec3 p, float radius, float height ){
	p= abs(p);
	vec2 ph= vec2( length(p.xy) - radius, p.z - height );
	return min( 0.0, max( ph.x, ph.y ) ) + max( 0.0, length( max( ph, 0.0 ) ) );
}



float cone(vec3 p, float radius, float height){
	float lpxy= length(p.xy);

	float rc1= length( p - vec3(0,0,height)); /// top
	float rc2= length( vec2( lpxy - radius, p.z ) ); /// side 
	float rc3; /// more or less inside
	float rc3h; /// more or less inside
	float rc3d= length(vec2(radius,height));
	{
		float sl= height / rc3d;
		float cl= radius / rc3d;

		vec2 h= vec2( lpxy - radius, p.z );
		vec2 h2= vec2(
			cl*h.x - sl*h.y,
			sl*h.x + cl*h.y
		);

		rc3= h2.y;
		rc3h= h2.x;
	}

	if( false ){
	}else if(height < p.z ){ /// top
		// return min( rc1, rc3);
		if( 0.0 < rc3h + rc3d )
			return rc3;
		else
			return rc1;
	}else if( radius < lpxy ){ /// side outside
		if( p.z <= 0.0 || 0.0 < rc3h ){
			return rc2;
		}else{
			return min( rc2, max( rc3, -p.z) );
		}
	}else if(p.z <= 0.0){ /// bottom
		return -p.z;
	}else{ /// inside or close to side
		return min3( rc3, rc1, rc2);
	}
}



int mapMatId= 0;
const vec3 ghostPos= vec3(-14.5, 16.0, 1.2 );



float map(vec3 p){
	vec3 ph;
	float r, rc;

	/// ground
	mapMatId= 0;
	r= p.z;

	/// snowman balls
	ph= p-vec3(0,0,0.4);
	rc= length(ph)-0.5 - 0.05;
	ph.z -= 0.7;
	rc= min( rc, length(ph)-0.4 - 0.05 );
	ph.z -= 0.6;
	rc= min( rc, length(ph)-0.3 - 0.05 );

	/// breast
	ph= p-vec3(0.0,-0.30,1.25);
	ph.x= abs(ph.x)-0.15;
	rc= min( rc, length(ph)-0.17 - 0.05);

	/// snow 2 apply
	if( rc < r ){
		mapMatId= 5;
		r= rc;
	}

	/// snow modification
	#ifndef DEBUG_MODE
	r += 0.1 * fbm( p*1.9 );
	#endif

	/// nose
	ph= p - vec3( -0.0, -0.1, 1.7 );
	ph.yz= vec2( ph.z, -ph.y );
	rc= cone( ph, 0.05, 0.4 ) - 0.02;
	if( rc < r ){
		mapMatId= 2;
		r= rc;
	}

	/// eyes & lips & nipples
	ph= p-vec3(0.0,-0.27,1.8); /// eyes
	ph.x= abs(ph.x)-0.1;
	rc= length(ph)-0.05;
	ph= p-vec3(0.0,-0.27,1.55); /// lips
	ph.x= abs(ph.x)-0.05;
	rc= min( rc, length(ph)-0.04 );
	ph.x -= 0.10;
	ph.y -= 0.03;
	ph.z -= 0.02;
	rc= min( rc, length(ph)-0.04 );
	ph= p-vec3(0.0,-0.47,1.26); /// nipples
	ph.x= abs(ph.x)-0.18;
	rc= min( rc, length(ph)-0.04);
	if( rc < r ){ /// apply
		mapMatId= 3;
		r= rc;
	}

	/// hat
	ph= p-vec3(0.0,-0.0,1.97);
	rc= cylinder(ph,0.22,0.02);
	rc= min( rc, cylinder(ph,0.15,0.2) );
	if( rc < r ){ /// apply
		mapMatId= 7;
		r= rc;
	}

	/// animal feeder
	{
		ph= p- vec3(-6.5,18.0,0.0);

		/// columns
		vec3 ph2= ph;
		ph2.xy= abs(ph2.xy) - 0.8;
		rc= cube(ph2,vec3(0.1,0.1,1.0));

		/// roof
		ph2= ph;
		ph2.x= abs(ph2.x) - 0.5;
		ph2.z -= 1.36;
		ph2.xz= rot( ph2.xz, 0.25*3.141592653589 );
		rc= min( rc, cube(ph2,vec3(0.9,1.1,0.03)));

		/// side walls
		ph2= ph - vec3(0,0,0.6);
		ph2.z= abs(ph2.z) - 0.3;
		ph2.x= abs(ph2.x) - 0.8;
		rc= min( rc, cube(ph2,vec3(0.1,0.9,0.1)));

		/// back wall
		ph2= ph - vec3(0,0,0.6);
		ph2.z= abs(ph2.z) - 0.3;
		ph2.y -= 0.8;
		rc= min( rc, cube(ph2,vec3(0.9,0.1,0.1)));

		/// apply
		if( rc < r ){
			mapMatId= 4;
			r= rc;
		}
	}

	/// trees - R.I.P. GPU
#ifdef DEBUG_MODE
	float ax=0.0, ay=0.0;
#else
	for(float ax=-1.0;ax<=1.0;ax++)for(float ay=-1.0;ay<=1.0;ay++)
#endif
	{
		const float va= 4.0;
		ph= p;
		vec3 phi= p;
		phi.xy= (floor( phi.xy / va ) + vec2(ax,ay) ) * va;

		if( 12.0 < length(phi) )
		{
			ph.xy -= phi.xy;
			ph.xy -= va/2.0;
			ph.xy += (-1.0 * 2.0*rand(phi.xy)) * va / 6.0;

			rc= cone( ph, 0.1 + 0.15*rand(phi.xy+0.04), 2.5 + 0.2*rand(phi.xy+0.05) );
			if( rc < r ){
				mapMatId= 6;
				r= rc;
			}
			ph.z -= 0.7 + 0.3*rand(phi.xy+0.01);
			rc= cone( ph, 1.0, 1.0 );

			ph.z -= 0.5 + 0.3*rand(phi.xy+0.02);
			rc= min( rc, cone( ph, 0.8, 0.8 ) );

			ph.z -= 0.4 + 0.3*rand(phi.xy+0.03);
			rc= min( rc, cone( ph, 0.6, 0.8 ) );

			rc -= 0.2;

			if( rc < r ){
				float rcb= rc;
				#ifndef DEBUG_MODE
					rc= max( rc, 0.57-fbm(24.0*p) );
				#endif
				if( rc < r )
				{
					mapMatId= rc==rcb ? 0 : 1;
					r= rc;
				}
			}
		}
	}

	/// ghost
	ph= p - ghostPos;
	if( 0.0 < ph.z )
		rc= length(ph);
	else
		rc= length(ph.xy);
	rc -= .5;
	if( rc < r ){
		r= rc;
		mapMatId= 8;
	}

	/// done
	return r;
}



/// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k )
{
	// float res = 1.0;
	// for( float t=mint; t < maxt; )
	// {
	// 	float h = map(ro + rd*t);
	// 	if( h<0.001 )
	// 		return 0.0;
	// 	res = min( res, k*h/t );
	// 	t += h;
	// }
	// return res;

    float res = 1.0;
    float ph = 1e20;
    float t= mint;
    #ifdef DEBUG_MODE
    for( int a=0;a<0x8;a++)
    #else
    for( int a=0;a<0x40;a++)
    #endif
    {
		float h = map(ro + rd*t);
		// if( h < E )
		// 	return 0.0;

		float y = h*h/(2.0*ph);
		float d = sqrt(h*h-y*y);
		res = min( res, 10.0*d/max(0.0,t-y) );
		ph = h;
		t += h;
		if( res<0.0001 || t>maxt ) break;
    }
    return clamp(res,0.0,1.0);
}



vec3 render( vec3 sp, vec3 dir ){
	vec3 p= sp;
	float dis= map(p);
	bool isHit= false;
	float t= dis;
	#ifdef DEBUG_MODE
	for(int a=0;a<0x40;a++)
	#else
	for(int a=0;a<0x200;a++)
	#endif
	{
		p= sp + t * dir;
		dis= map(p);
		if( dis < E ){
			isHit= true;
			break;
		}
		t += dis;
	}

	if( isHit ){
		int matId= mapMatId;
		vec2 h= vec2(0,E);
		vec3 nor= normalize(vec3(
			map(p+h.yxx) - map(p-h.yxx),
			map(p+h.xyx) - map(p-h.xyx),
			map(p+h.xxy) - map(p-h.xxy)
			));

		float cs= 1.0;
		for( float d=0.003; d < 0.04; d *=1.5 ){
			cs= min( cs, max( 0.0, map( p + d * nor ) / d ) );
		}

		/// shadows + dot nor sunDir
		float sun= 1.0;
		const vec3 sunDir= normalize( vec3( 0.35, -0.23, 0.9 ) );
		{
			float dsd= dot(nor,sunDir);
			if( dsd < 0.0 ){
				sun= 0.0;
			}else{
				sun= shadow( p+2.*E*nor, sunDir, 0.01, 50.0, 2.0 );
				sun= clamp( sun, 0.0, 1.0 );
				sun *= (dsd);
			}
		}

		/// base color
		vec3 baseColor= vec3(1);
		switch(matId){
			case 0: baseColor= vec3(.9); break; /// snow 1
			case 1: baseColor= vec3(.10,.50,.10); break; /// green
			case 2: baseColor= vec3(.60,.15,.05); break; /// carrot
			case 3: baseColor= vec3(.30,.30,.30) * fbm( p*28.0 ); break; /// coal
			case 4: baseColor= vec3(.70,.50,.20); break; /// wood
			case 5: baseColor= vec3(.9); break; /// snow 2
			case 6: baseColor= vec3(.20,.13,.05); break; /// tree wood
			case 7: baseColor= vec3(.25,.15,.30) * mulPower(0.75,fbm(p*19.0)); break; /// hat
            case 8: return vec3(.15,.01,.01)*pow(clamp( 9.0*(0.1 - length(vec2( abs(rot(p.xy-ghostPos.xy,-0.4).x)-.15, p.z-ghostPos.z))) , 0.0, 1.0 ), 2.0); /// ghost
			default: baseColor= vec3(1,0,0); break;
		}

		/// final result
		return baseColor
			* mulPower( 0.5, dot(nor,-dir) )
			* mulPower( 0.13, cs )

			* mulPower( mix( 0.8, 0.95, clamp(0.05*length(p), 0.0, 1.0 )), sun )

			* mulPower( 1.0, 1.0 / (1.0 + 0.00015*t*t*t) )

			* ( matId==0 ? mulPower( ( 0.050*(length(p) + 2.0) ) , fbm(p*0.9) ) : mulPower( 0.4 , fbm(p*4.1) ) )
		;
	}else{
		return vec3(0);
	}
}



