	#ifdef ANIMATED_MODE
	{
		float t= (iTime+40.0) * 1.0;
		p.x += 0.7*sin(t*0.1);
		p.y += 0.06*cos(t*0.17);
		rz -= 0.15*sin(t*0.1);
		rx += 0.07*sin(t*0.04+0.5);
	}
	#endif
	#endif

	vec3 dir= normalize( vec3( uv.x, uv.y, -4.0 ) );
	dir.yz= rot(dir.yz, rx );
	dir.xy= rot(dir.xy, -rz );

	fragColor.xyz= render( p, dir );
}
