//////////////////////////////////////////////////
// CONSTS

/**
 * Every frames that max ray marching steps of every fragments.
 * TODO: いくつにすべき？ (パフォーマンスと画質のバランス的に)
 */
const int MAX_MARCHING_STEPS = 255;

/**
 * Value of "This distance are collisioned?".
 * TODO: いくつにすべき？ (画質的に)
 */
const float EPSILON = .001;




//////////////////////////////////////////////////
// STRUCTS

/**
 * Struct of camera.
 * This camera has some properties based on physical and you can set any values that same any any real camera device.
 */
struct Camera {
	float focalLength; // mm
    vec2 sensorSize; // mm
    vec2 lensShift; // mm
    vec3 position;
    vec3 lookAt;
    float roll; // degree
};

/**
 * Struct of light.
 */
struct Light {
    vec3 position; // in global coords
    vec3 direction; // please set normalized vector
    float strength;
    vec3 specular;
    vec3 diffuse;
};

/**
 * Struct of material.
 */
struct Material {
	vec3 albedo; // RGB
    float specular;
    float diffuse;
    float ambient;
    float shininess;
};




//////////////////////////////////////////////////
// DISTANCE FUNCTIONS


/**
 * Signed function of sphene.
 * @param {float} p point of ray
 * @param {float} s sphere radius
 * @return {float} distance from p to sphere
 */
float sdSphere(vec3 p, float s) {
	return length(p) - s;
}

/**
 * Signed function of box;
 * @param {vec3} p point of ray
 * @param {vec3} b box size
 */
float sdBox(vec3 p, vec3 b) {
	vec3 d = abs(p) - b;
    return length(max(d,0.0)) + min(max(d.x,max(d.y,d.z)),0.0);
}

/**
 * Signed function of scene.
 * @param {vec3} p point of ray
 * @return {float} distance from p to scene
 */
float sdScene(vec3 p) {
    float d = sdBox(p - vec3(0., 0., sin(iTime*2.)*-2.), vec3(1., 1., 1.));
             d = min(d, sdBox(p - vec3(-3., sin(iTime*2.), 0.), vec3(1., 1., 1.)));
             d = min(d, sdSphere(p - vec3(3., sin(iTime*4. + 10.), 0.), 1.));
    return d;
}




//////////////////////////////////////////////////
// RAY MARCHING UTILITY FUNCTIONS

/**
 * Get normal vector of surface represented by signed distance function.
 * @param {p} point of marching ray tip
 */
vec3 getNormal(vec3 p) {
	return normalize(vec3(
    	sdScene(p + vec3(EPSILON, 0., 0.)) - sdScene(p + vec3(-EPSILON, 0., 0.)),
        sdScene(p + vec3(0., EPSILON, 0.)) - sdScene(p + vec3(0., -EPSILON, 0.)),
        sdScene(p + vec3(0., 0., EPSILON)) - sdScene(p + vec3(0., 0., -EPSILON))
    ));
}

/**
 * Get field of view (FOV) with camera settings.
 * @param {vec2} sensorSize vertical and horizontal camera sensor size
 * @param {float} focalLength camera focal length
 * @return {vec2} vertical and horizontal field of view (in degrees)
 */
vec2 getFieldOfView(vec2 sensorSize, float focalLength) {
	float verticalFov = degrees(2. * atan(sensorSize.y / 2. / focalLength));
    float horizontalFov = degrees(2. *atan(sensorSize.y / 2. / focalLength));
    return vec2(horizontalFov, verticalFov);
}

/**
 * Get normalized ray direction vec3. (on camera local coordinates)
 * @param {vec2} fov vertical and horizontal field of view
 * @param {float} focalLength camera focal length
 * @param {vec2} uv current UV coords.
 * @return {vec3} normalized ray direction (that correspond to current fragment coords)
 */
vec3 getRayDirection(vec2 fov, float focalLength, vec2 uv) {
    float directionX = fov.x / 2. / 90. * uv.x;
    float directionY = fov.y / 2. / 90. * uv.y;
    float directionZ = focalLength;
	return normalize(vec3(directionX, directionY, directionZ));
}

/**
 * Get mat3 that translate to camera coords.
 * @param {vec3} position camera position (on the world coords)
 * @param {vec3} lookAt coords of "Where to camera looking?" (on the world coords)
 * @param {vec3} up camera up vector (on the world coords)
 * @return {mat3} viewing transform matrix
 */
mat3 getViewingTransformMat3(vec3 position, vec3 lookAt, vec3 up) {
    vec3 z = normalize(lookAt-position);
    vec3 x = normalize(cross(z, up));
    vec3 y = normalize(cross(x, z));
	return mat3(
        vec3(x.x, y.x, z.x),
        vec3(x.y, y.y, z.y),
        vec3(x.z, y.z, z.z)
    );
}

/**
 * Get camera up vector.
 * @param {float} roll degrees that "How amount roll the screen camera?"
 */
vec3 getUpVector(float roll) {
	return vec3(sin(radians(roll)), cos(radians(roll)), 0.);
}




//////////////////////////////////////////////////
// SHADING UTILITY FUNCTIONS

/**
 * Shading with normal vector.
 * @param {vec3} normal vector (please set normalized vector)
 * @return {vec3} result color
 */
vec3 normalShading(vec3 normal) {
	return normal;
}

/**
 * Shading with lambert reflection model.
 * @param {vec3} normal normal vector (please set normalized vector)
 * @param {Light} light light information
 * @param {Material} material material information
 * @return {vec3} result color
 */
vec3 lambertShading(vec3 normal, Light light, Material material) {
	return dot(light.direction, normal) * material.albedo * light.strength;
}

/**
 * Shading with phong reflection model.
 * @param {vec3} ray marching ray vector
 * @param {vec3} cameraPosition position of camera (in world coords)
 * @param {vec3} normal normal vector (please set normalized vector)
 * @param {Light} light light information
 * @param {float} ambientLightStrength strength of ambient light
 * @param {Material} material material information
 * @return {vec3} result color
 */
vec3 phongShading(vec3 ray, vec3 cameraPosition, vec3 normal, Light light, float ambientLightStrength, Material material) {
	vec3 rayDirection = normalize(ray);
    vec3 L = normalize(ray - light.position); // vector that "surface to light"
    vec3 V = normalize(ray-cameraPosition); // vector that "surface to camera"
    vec3 R = normalize(cross(cross(rayDirection, normal), rayDirection)); // vector that reflection direction from light to the other side of the surface point
    return material.ambient * ambientLightStrength + (material.diffuse * dot(L, normal) * light.diffuse + material.specular * pow(dot(R, V), material.shininess) * light.specular);
}




//////////////////////////////////////////////////
// MAIN PROCESS

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // UV (-1〜1 | adjusted to canvas aspect ratio)
	vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    uv.y *= iResolution.y / iResolution.x;

    // Camera
    Camera camera;
    camera.focalLength = 59.8333;
    camera.sensorSize = vec2(35.9, 24.);
    camera.position = vec3(1000., 3000.*sin(iTime*2.), 5000.*sin(iTime));
    camera.lookAt = vec3(sin(iTime*3.)*1., 0., 0.);
    camera.roll = sin(iTime) * 360.;

    // Light
    Light light1;
    light1.position = vec3(0., 0., 0.);
    light1.direction = normalize(vec3(1., 0., -1.));
    light1.strength = 1.;
    light1.specular = vec3(.0, .5,  .5);
    light1.diffuse = vec3(.5, .0, .5);

    // Material
    Material material1;
    material1.albedo = vec3(1.*abs(sin(iTime)), 0., 1.);
    material1.specular = .5;
    material1.diffuse = .5;
    material1.ambient = .5;
    material1.shininess = 1.;

    // Ray Marching
    vec3 color = vec3(false);
    float depth = 1.;

    // 回転を角度で指定するバージョン (Camera 構造体にそれ用のプロパティも持たせてみている)
    // vec3 direction = getRayDirection(getFieldOfView(camera.sensorSize, camera.focalLength), camera.focalLength, uv) * getViewingTransformMat3(camera.position, camera.lookAt, getUpVector(camera.roll));

    // 見た目重視で適当に回転してみるバージョン
    vec3 direction = getRayDirection(getFieldOfView(camera.sensorSize, camera.focalLength), camera.focalLength, uv) * getViewingTransformMat3(camera.position, camera.lookAt, vec3(.01, cos(iTime), 0.));

    bool isCollisioned = false;
    for(int i=0; i<MAX_MARCHING_STEPS; i++) {
        float distance = sdScene(camera.position + direction * depth);
        if(distance < EPSILON) {

            // color = normalShading(getNormal(camera.position + direction * depth));
            color = lambertShading(getNormal(camera.position + direction * depth), light1, material1);

            break;
        }
        depth += distance;
    }

    // Render
    fragColor = vec4(color, 1.);
}

const float topCircleCount = 11.0;
const vec4 topLayerColor = vec4(0.0, 0.0, 1.0, 1.0);
const float topLayerScreenHeight = 0.9;
const float topLayerSpeed = 1.0/15.0;

const float underCircleCount = 9.0;
const vec4 underLayerColor = vec4(0.0, 0.0, 0.0, 1.0);
const float underLayerScreenHeight = 0.83;
const float underLayerSpeed = 1.0/19.0;

const float checkerLayerCount = 6.0;
const vec4 checkerLayerColor1 = vec4(0.8, 0.45, 0.0, 1.0);
const vec4 checkerLayerColor2 = vec4(0.55, 0.25, 0.0, 1.0);
const vec2 checkerLayerSpeed = vec2(1.0/45.0, 1.0/30.0);

const float triangleLayerFrequency = 20.0;
const vec4 triangleLayerColor = vec4(0.1, 0.1, 0.1, 1.0);
const float triangleLayerHeight = 0.1;
const float triangleLayerSpeed = 1.0 / 10.0;

vec4 getCurtain(vec2 fragCoord, float circleCount, vec4 color, float screenHeight, float speed)
{
    float layerHeight = iResolution.y * screenHeight;
    if (fragCoord.y > layerHeight)
    {
        return color;
    }
    else
    {
        float layerCycle = -iTime * speed;
        float circleDiameter = iResolution.x / circleCount;
        float circleRadius = circleDiameter / 2.0;
        vec2 circleCoord;
        circleCoord.x = mod(fragCoord.x + iResolution.x * fract(layerCycle), circleDiameter)
            - circleRadius;
        circleCoord.y = fragCoord.y - layerHeight;
        // I want the circles to be a little taller
        circleCoord.y *= 0.96;

        float sqrDistance = dot(circleCoord, circleCoord);
        float sqrRadius = circleRadius * circleRadius;
        float boundary = circleRadius + 1.0;
        return vec4(color.rgb, color.a * smoothstep(boundary * boundary, sqrRadius, sqrDistance));
    }
}

vec4 getChecker(vec2 fragCoord, float checkerCount, vec4 checkerColor1, vec4 checkerColor2, vec2 checkerSpeed)
{
    vec2 checkerCoord = floor((fragCoord / iResolution.x + checkerSpeed * -iTime) * checkerCount);
    vec4 col = mix(checkerColor1, checkerColor2, mod(checkerCoord.x + checkerCoord.y, 2.0));
    return col;
}

float sampleTriangleWave(float t, float width)
{
    return abs(mod(t, width) - width / 2.0);
}

vec4 getTriangle(vec2 fragCoord, float frequency, vec4 color, float height, float speed)
{
    float width =  iResolution.x / frequency;
    float t = fragCoord.x - iTime * iResolution.x * speed;

    float waveHeight = sampleTriangleWave(t, width) + iResolution.y * height;
    return vec4(color.rgb, color.a * smoothstep(waveHeight + 2.0, waveHeight, fragCoord.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col;

    vec4 checker = getChecker(fragCoord, checkerLayerCount, checkerLayerColor1, checkerLayerColor2, checkerLayerSpeed);
    col = mix(col, checker.rgb, checker.a);

    vec4 bottomTriangle = getTriangle(fragCoord, triangleLayerFrequency, triangleLayerColor, triangleLayerHeight, triangleLayerSpeed);
    col = mix(col, bottomTriangle.rgb, bottomTriangle.a);

    vec4 underLayer = getCurtain(fragCoord, underCircleCount, underLayerColor, underLayerScreenHeight, underLayerSpeed);
    col = mix(col, underLayer.rgb, underLayer.a);

    vec4 topLayer = getCurtain(fragCoord, topCircleCount, topLayerColor, topLayerScreenHeight, topLayerSpeed);
    col = mix(col, topLayer.rgb, topLayer.a);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float ring(vec2 pos, float size, float width, vec2 pixel) {
	float fa = 0.02;
	return smoothstep(width, width * fa, abs(mod(distance(pixel,pos), size)-.5 * size));	
}

//hsv2rgb by Sam Hocevar
vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;

    vec3 col = vec3(0.);

    float x1 = cos(iTime) * cos(iTime * 0.5);
    float y1 = sin(iTime) * sin(iTime * 0.5);

    float x2 = cos(iTime - 1.) * sin(iTime * 0.5);
    float y2 = sin(iTime - 1.) * cos(iTime * 0.5);

    col += ring(vec2(x1, y1), 0.15, 0.08, uv);
    col += ring(vec2(x2, y2), 0.15, 0.08, uv);

    vec3 mixcolor1 = hsv2rgb(vec3(sin(iTime * 0.06),(uv.x * uv.y) + abs(sin(iTime * 0.1)), 1.0)) * .18;

    vec3 fcol = mix(vec3(0.), col, mixcolor1);
    fragColor = vec4(fcol * 5., 1.0);
}

float dither8x8(vec2 position, float brightness) {  
  float limit = texture(iChannel0, position / iChannelResolution[0].xy).r;

  return brightness < limit ? 0.0 : 1.0;
}

// SDFs and Smooth Union from http://iquilezles.org/
float sdSphere( vec3 p, float s ){
  return length(p)-s;
}

float sminCubic( float a, float b, float k ){
    float h = max( k-abs(a-b), 0.0 )/k;
    return min( a, b ) - h*h*h*k*(1.0/6.0);
}


float intersect(vec3 point){
    float fSphere = sdSphere(vec3(cos(iTime), 1.0, 5.0 + sin(iTime)) - point, 1.0);
    float tSphere = sdSphere(vec3(cos(iTime * 1.2), 2.5, 5.0 + sin(iTime * 1.3)) - point, 0.7);
    float dSphere = sdSphere(vec3(cos(iTime * 1.7), sin(iTime * 2.3), 5.0 + sin(iTime * 0.5)) - point, 0.3);

    float sphereRes = sminCubic(sminCubic(fSphere, tSphere, 0.4), dSphere, 0.4);
	return sphereRes;
}

float march(vec3 rayOrigin, vec3 rayDirection){
	float minimumHit = 0.0;

    for(int i = 0; i < 100; i++){
    	vec3 hit = rayOrigin + rayDirection * minimumHit;
        float dist = intersect(hit);
        minimumHit += dist;
        if(dist > 100.0 || dist < 0.01) break;
    }
    return minimumHit;
}

vec3 getNormal(vec3 point){
	float distPoint = intersect(point);
    vec2 e = vec2(0.01, 0);

    vec3 norm = distPoint - vec3(intersect(point - e.xyy), intersect(point - e.yxy), 
                            	 intersect(point - e.yyx));
    return normalize(norm);
}

float lightCalculate(vec3 lightPos, vec3 point){
	vec3 lightDirection = normalize(lightPos - point);
    vec3 normal = getNormal(point);
    float brightness = clamp(dot(normal, lightDirection), 0.0, 1.0);

    float shadowCatch = march(point + normal * 0.11, lightDirection);
    if(shadowCatch < length(lightPos - point)) brightness *= 0.1;

    return brightness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0);

    vec3 rayOrigin = vec3(sin(iTime), 1.5, -0.5);
    vec3 rayDirection = normalize(vec3(uv,1));

    vec3 lightPos = vec3(1, 5, sin(iTime));
    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.0;

    float hitDist = march(rayOrigin, rayDirection);
    vec3 hitPoint = rayOrigin + rayDirection * hitDist;

    vec3 scrollCol = vec3(cos(iTime + uv.y), sin(iTime + uv.y), 1.0);
    col = scrollCol * dither8x8(fragCoord, lightCalculate(lightPos,hitPoint));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float aafract(float x) {
    float v = fract(x),
          w = fwidth(x);
    return v < 1.-w ? v/(1.-w) : (1.-v)/w;
}

float grid(float a)
{
    return aafract(a);
}

float line(float a)
{
    return clamp(abs(a)/fwidth(a),0.,1.);
}

float function(float x){
    // this is where you change the function
    return pow(x,x); // 0 powered by 0 is 1 :)
}

float draw(vec2 uv){
    return line((function(uv.x)-uv.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - (iMouse.w < .5 ? iResolution.xy*.5 : iMouse.xy))/iResolution.y;
	uv *= sin(iTime)*10. + 13.; // zoom
    float b = line(min(grid(uv.x),grid(uv.y)));
    float c = line(min(abs(uv.x),abs(uv.y)));
    fragColor = draw(uv) * vec4(c,b*c,1.,1.);
}

float n(vec2 u){
    vec4 d=vec4(.106,5.574,7.728,3.994),q=u.xyxy,p=floor(q);
    ++p.zw;
    q-=p;
    p=fract(p*d.xyxy);
    d=p+d.wzwz;
	d=p.xxzz*d.ywyw+p.ywyw*d.xxzz;
    p=fract((p.xxzz+d)*(p.ywyw+d));
    p=cos(p*=iTime+d)*q.xxzz+sin(p)*q.ywyw;
    q*=q*(3.-2.*q);
    p=mix(p,p.zwzw,q.x);
    return mix(p.x,p.y,q.y);
}

void mainImage(out vec4 o, in vec2 u){
    u/=iResolution.y;
	float r=length(vec2(n(u*5.),n(u*7.)));
	o=vec4(r+.01/r); 
}

