    uv.x/=aspect;
    mouse.x/=aspect;
    vec3 cam = vec3(0., 0., -3.);
    vec3 ray = normalize(vec3(uv.xy*1.3, 1.));

    vec3 rot = vec3(mouse.y*5., -mouse.x*3., 0.);

    cam.xz = rotate2D(cam.xz, rot.y);
    ray.xz = rotate2D(ray.xz, rot.y);

    cam.zy = rotate2D(cam.zy, rot.x);
    ray.zy = rotate2D(ray.zy, rot.x);


    vec4 t = trace(cam, ray);
    vec3 e = t.xyz;
    vec3 light = vec3(0., 10., -10.);
    vec3 toLight = normalize(light-e);
    vec3 norm = estimateNormal(e);
    float diffuse = max(0.,dot(toLight, norm))*0.8;
    vec3 refl = reflect(ray, norm);
    float specular = pow(max(0.0, dot(refl, toLight)), 4.0)*0.5;
    float d = length(e-cam);
    float fog = 1.0 / (1.0 + d*d*0.01);

    vec3 col = vec3(1.);//texture(iChannel0, refl).rgb*1.+0.8;
    col -= pow(t.w/float(MAX_STEPS), 2.)*0.8;//ao
    col *= (diffuse+specular)*0.+0.8;//other light
    col = 0.01+(col-0.01)*3.6;
    col *= fog;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float PI = 3.14159265;
float time(){
	return iTime;
}

float map(vec3 p){
    p.y = -abs(p.y);
    p.xz *= mat2(cos(time()),-sin(time())
         ,sin(time()),cos(time()));
	float fl = p.y + 0.5;
    float bigHole = length(p.xz) - 1.0;
    float holes = 1000000.0;
    for(float i = 0.0; i < 6.0; i += 1.0){
    	holes = min(holes,length(p.xz - vec2(cos(i * PI / 3.0),sin(i * PI / 3.0)) * 1.5) - 0.3);
    }
    float h = min(bigHole,holes);
    return max(fl,sin(time() * 2.0)*h);



}

const float EPS = 0.001;
vec3 getNormal(vec3 p) {
    return normalize(vec3(
        map(p + vec3(EPS, 0.0, 0.0)) - map(p + vec3(-EPS,  0.0,  0.0)),
        map(p + vec3(0.0, EPS, 0.0)) - map(p + vec3( 0.0, -EPS,  0.0)),
        map(p + vec3(0.0, 0.0, EPS)) - map(p + vec3( 0.0,  0.0, -EPS))
    ));
}

vec4 trace (vec3 o, vec3 r){
	float t = 0.0;
    vec3 p = vec3(0.0,0.0,0.0);

    for(int i = 0; i < 64; ++i){
        p = o + r * t;
        float d = map(p);
        t += d * 0.5;
    }
    return vec4(getNormal(p),t);
}


